        <div id="visualization-area">
            <!-- The p5.js canvas will be attached here -->
        </div>

        <!-- Reset Button -->
        <button id="btn-reset">+</button>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        // Global variables
        let share1, share2;
        let share1Pos = { x: 0, y: 0 };
        let share2Pos = { x: 0, y: 0 };
        let secretImage = [];
        const imgSize = 64;
        const shareSize = imgSize * 2;

        // Create the secret image (smiley face)
        function createSecretImage() {
            // Initialize empty array
            for (let i = 0; i < imgSize; i++) {
                secretImage[i] = [];
                for (let j = 0; j < imgSize; j++) {
                    secretImage[i][j] = 0; // Default to white (0)
                }
            }

            // Draw a circle (face)
            const centerX = imgSize / 2;
            const centerY = imgSize / 2;
            const radius = imgSize / 3;

            for (let i = 0; i < imgSize; i++) {
                for (let j = 0; j < imgSize; j++) {
                    const dist = Math.sqrt((i - centerX) ** 2 + (j - centerY) ** 2);
                    if (dist <= radius) {
                        secretImage[i][j] = 1; // Face is black (1)
                    }
                }
            }

            // Draw eyes (white circles on black face)
            const eyeRadius = radius / 5;
            const leftEyeX = centerX - radius / 2;
            const rightEyeX = centerX + radius / 2;
            const eyesY = centerY - radius / 3;

            for (let i = 0; i < imgSize; i++) {
                for (let j = 0; j < imgSize; j++) {
                    // Left eye
                    const distLeft = Math.sqrt((i - leftEyeX) ** 2 + (j - eyesY) ** 2);
                    // Right eye
                    const distRight = Math.sqrt((i - rightEyeX) ** 2 + (j - eyesY) ** 2);

                    if (distLeft <= eyeRadius || distRight <= eyeRadius) {
                        secretImage[i][j] = 0; // Eyes are white (0)
                    }
                }
            }

            // Draw mouth (smile)
            const mouthRadius = radius / 2;
            const mouthStartAngle = 0.2 * Math.PI;
            const mouthEndAngle = 0.8 * Math.PI;

            for (let i = 0; i < imgSize; i++) {
                for (let j = 0; j < imgSize; j++) {
                    const angle = Math.atan2(j - (centerY + radius / 4), i - centerX);
                    const dist = Math.sqrt((i - centerX) ** 2 + (j - (centerY + radius / 4)) ** 2);

                    if (angle >= mouthStartAngle && angle <= mouthEndAngle && 
                        dist >= mouthRadius - 2 && dist <= mouthRadius + 2) {
                        secretImage[i][j] = 0; // Mouth is white (0)
                    }
                }
            }
        }

        // Generate visual cryptography shares
        function generateShares() {
            share1 = createGraphics(shareSize, shareSize);
            share2 = createGraphics(shareSize, shareSize);

            share1.clear();
            share2.clear();

            for (let i = 0; i < imgSize; i++) {
                for (let j = 0; j < imgSize; j++) {
                    const pixel = secretImage[i][j];
                    const x = i * 2;
                    const y = j * 2;

                    // Randomly choose one of two patterns
                    const pattern = Math.random() > 0.5 ? 0 : 1;

                    if (pixel === 0) {
                        // White pixel - same pattern on both shares
                        drawPattern(share1, x, y, pattern);
                        drawPattern(share2, x, y, pattern);
                    } else {
                        // Black pixel - complementary patterns
                        drawPattern(share1, x, y, pattern);
                        drawPattern(share2, x, y, 1 - pattern);
                    }
                }
            }
        }

        // Draw a 2x2 pattern at given position
        function drawPattern(g, x, y, pattern) {
            g.noStroke();

            if (pattern === 0) {
                // Pattern: top-left and bottom-right black
                g.fill(0);
                g.rect(x, y, 1, 1);
                g.rect(x + 1, y + 1, 1, 1);
                g.noFill();
                g.rect(x + 1, y, 1, 1);
                g.rect(x, y + 1, 1, 1);
            } else {
                // Pattern: top-right and bottom-left black
                g.fill(0);
                g.rect(x + 1, y, 1, 1);
                g.rect(x, y + 1, 1, 1);
                g.noFill();
                g.rect(x, y, 1, 1);
                g.rect(x + 1, y + 1, 1, 1);
            }
        }

        // p5.js setup
        function setup() {
            const canvas = createCanvas(600, 500);
            canvas.parent('visualization-area');

            createSecretImage();
            generateShares();

            // Initialize share positions based on joystick handles
            updateSharePositions();

            // Setup joystick interactions
            setupJoysticks();

            // Setup reset button
            document.getElementById('btn-reset').addEventListener('click', resetPositions);
        }

        // p5.js draw loop
        function draw() {
            background(255);

            // Draw the shares at their current positions
            image(share1, share1Pos.x, share1Pos.y);
            image(share2, share2Pos.x, share2Pos.y);
        }

        // Update share positions based on joystick handle positions
        function updateSharePositions() {
            const canvasWidth = width - shareSize;
            const canvasHeight = height - shareSize;

            // Get handle positions
            const handle1 = document.getElementById('joystick-handle1');
            const handle2 = document.getElementById('joystick-handle2');

            // Convert percentage positions to numbers
            const handle1Top = parseFloat(handle1.style.top) / 100;
            const handle1Left = parseFloat(handle1.style.left) / 100;
            const handle2Top = parseFloat(handle2.style.top) / 100;
            const handle2Left = parseFloat(handle2.style.left) / 100;

            // Map joystick positions to canvas positions
            share1Pos.x = map(handle1Left, 0, 1, 0, canvasWidth);
            share1Pos.y = map(handle1Top, 0, 1, 0, canvasHeight);
            share2Pos.x = map(handle2Left, 0, 1, 0, canvasWidth);
            share2Pos.y = map(handle2Top, 0, 1, 0, canvasHeight);
        }

        // Setup joystick dragging functionality
        function setupJoysticks() {
            let activeHandle = null;
            let startX, startY;
            let startLeft, startTop;

            // Mouse down event for handles
            document.querySelectorAll('.joystick-handle').forEach(handle => {
                handle.addEventListener('mousedown', function(e) {
                    e.preventDefault();
                    activeHandle = this;
                    startX = e.clientX;
                    startY = e.clientY;

                    // Get current position
                    const style = window.getComputedStyle(this);
                    startLeft = parseFloat(style.left);
                    startTop = parseFloat(style.top);

                    // Bring to front
                    this.style.zIndex = '10';
                });
            });

            // Mouse move for document
            document.addEventListener('mousemove', function(e) {
                if (activeHandle) {
                    const container = activeHandle.parentElement;
                    const containerRect = container.getBoundingClientRect();
                    const containerWidth = containerRect.width;
                    const containerHeight = containerRect.height;

                    // Calculate new position
                    const deltaX = e.clientX - startX;
                    const deltaY = e.clientY - startY;

                    let newLeft = startLeft + (deltaX / containerWidth * 100);
                    let newTop = startTop + (deltaY / containerHeight * 100);

                    // Clamp values between 0% and 100%
                    newLeft = Math.max(0, Math.min(100, newLeft));
                    newTop = Math.max(0, Math.min(100, newTop));

                    // Update handle position
                    activeHandle.style.left = newLeft + '%';
                    activeHandle.style.top = newTop + '%';

                    // Update share positions
                    updateSharePositions();
                }
            });

            // Mouse up for document
            document.addEventListener('mouseup', function() {
                if (activeHandle) {
                    activeHandle.style.zIndex = '';
                    activeHandle = null;
                }
            });
        }

        // Reset positions to initial state
        function resetPositions() {
            document.getElementById('joystick-handle1').style.top = '25%';
            document.getElementById('joystick-handle1').style.left = '75%';
            document.getElementById('joystick-handle2').style.top = '75%';
            document.getElementById('joystick-handle2').style.left = '25%';

            updateSharePositions();
        }

        // Helper function to map values from one range to another
        function map(value, start1, stop1, start2, stop2) {
            return start2 + (stop2 - start2) * ((value - start1) / (stop1 - start1));
        }
    </script>
</body>
</html>
