    <script>
        let rings = 1;
        let circlesPerRing = 8;
        let inverseMode = false;
        let locators = [new p5.Vector(-50, 0), new p5.Vector(50, 0)];
        let draggedLocator = null;
        let canvasSize = 550;

        function setup() {
            let canvas = createCanvas(canvasSize, canvasSize);
            canvas.parent('canvas-container');
            angleMode(RADIANS);

            // Set up event listeners for controls
            document.querySelectorAll('input[name="rings"]').forEach(radio => {
