            document.querySelectorAll('input[name="cpr"]').forEach(radio => {
                radio.addEventListener('change', function() {
                    circlesPerRing = parseInt(this.value);
                    redraw();
                });
            });

            document.getElementById('checkbox-inverse').addEventListener('change', function() {
                inverseMode = this.checked;
                redraw();
            });
        }

        function draw() {
            translate(width / 2, height / 2);
            background('#C1D4E6');

            if (inverseMode) {
                drawInverseMode();
            } else {
                drawNormalMode();
            }
        }

        function drawNormalMode() {
            let maxRadius = width / 2 * 0.9;
            let k = (1 - sin(PI / circlesPerRing)) / (1 + sin(PI / circlesPerRing));
            let R_c = maxRadius / (1 + sin(PI / circlesPerRing));
            let r = R_c * sin(PI / circlesPerRing);

            // Draw guide lines
            stroke('#A0A0A0');
            noFill();
            for (let j = 0; j < circlesPerRing; j++) {
                let angle = j * TWO_PI / circlesPerRing;
                line(0, 0, R_c * cos(angle), R_c * sin(angle));
            }

            // Draw circles
            fill('#8C929D');
            stroke(0);
            strokeWeight(1);

            for (let i = 0; i < rings; i++) {
                let currentR_c = R_c * pow(k, i);
                let currentR = r * pow(k, i);

                for (let j = 0; j < circlesPerRing; j++) {
                    let angle = j * TWO_PI / circlesPerRing;
                    let x = currentR_c * cos(angle);
                    let y = currentR_c * sin(angle);
                    circle(x, y, currentR * 2);
                }
            }
        }

        function drawInverseMode() {
            // Draw locators and inversion circle
            fill('#FF0000');
            noStroke();
            circle(locators[0].x, locators[0].y, 8);
            circle(locators[1].x, locators[1].y, 8);

            stroke('#FF0000');
            strokeWeight(1);
            line(locators[0].x, locators[0].y, locators[1].x, locators[1].y);

            let center = p5.Vector.add(locators[0], locators[1]).div(2);
            let radius = p5.Vector.dist(locators[0], locators[1]) / 2;

            fill('rgba(255, 0, 0, 0.3)');
            stroke(0);
            circle(center.x, center.y, radius * 2);

            // Calculate and draw inverted circles
            let maxRadius = width / 2 * 0.9;
            let k = (1 - sin(PI / circlesPerRing)) / (1 + sin(PI / circlesPerRing));
            let R_c = maxRadius / (1 + sin(PI / circlesPerRing));
            let r = R_c * sin(PI / circlesPerRing);

            fill('rgba(220, 100, 220, 0.5)');
            stroke(0);
            strokeWeight(1);

            for (let i = 0; i < rings; i++) {
                let currentR_c = R_c * pow(k, i);
                let currentR = r * pow(k, i);

                for (let j = 0; j < circlesPerRing; j++) {
                    let angle = j * TWO_PI / circlesPerRing;
                    let origCenter = createVector(currentR_c * cos(angle), currentR_c * sin(angle));
                    invertCircle(origCenter, currentR, center, radius);
                }
            }
        }

        function invertCircle(origCenter, origRadius, invCenter, invRadius) {
            let d = p5.Vector.dist(origCenter, invCenter);
            let invRadiusSq = invRadius * invRadius;

            if (abs(d - origRadius) < 0.001) {
                // Inversion is a line
                let lineDist = invRadiusSq / (2 * d);
                let dir = p5.Vector.sub(origCenter, invCenter).normalize();
                let linePoint = p5.Vector.add(invCenter, dir.mult(lineDist));

                // Find perpendicular vector
                let perp = createVector(-dir.y, dir.x);

                // Draw line clipped to canvas
                let scale = width;
                let p1 = p5.Vector.add(linePoint, p5.Vector.mult(perp, scale));
                let p2 = p5.Vector.add(linePoint, p5.Vector.mult(perp, -scale));

                line(p1.x, p1.y, p2.x, p2.y);
            } else {
                // Inversion is a circle
                let factor = invRadiusSq / (d * d - origRadius * origRadius);
                let newRadius = abs(factor) * origRadius;
                let newCenter = p5.Vector.add(invCenter, p5.Vector.sub(origCenter, invCenter).mult(factor));

                circle(newCenter.x, newCenter.y, newRadius * 2);
            }
        }

        function mousePressed() {
            if (!inverseMode) return;

            // Check if mouse is over either locator
            let mousePos = createVector(mouseX - width/2, mouseY - height/2);

            for (let i = 0; i < locators.length; i++) {
                if (p5.Vector.dist(mousePos, locators[i]) <= 8) {
                    draggedLocator = i;
                    return;
                }
            }
        }

        function mouseDragged() {
            if (!inverseMode || draggedLocator === null) return;

            locators[draggedLocator].x = mouseX - width/2;
            locators[draggedLocator].y = mouseY - height/2;
            redraw();
        }

        function mouseReleased() {
            draggedLocator = null;
        }
    </script>
