<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-group">
                <label for="slider-logCa">\(C_a\)</label>
                <input type="range" id="slider-logCa" min="-4" max="0" step="0.01" value="-1">
                <span class="slider-value" id="logCa-value">-1.00</span>
            </div>
            <div class="slider-group">
                <label for="slider-pka">\(pK_a\)</label>
                <input type="range" id="slider-pka" min="-4" max="7" step="0.1" value="1.2">
                <span class="slider-value" id="pka-value">1.2</span>
            </div>
        </div>
        <div id="visualization-container">
            <div id="p5-canvas"></div>
            <div id="formula-strong" class="formula">\( \color{maroon}{pH = - \log C_a} \)</div>
            <div id="formula-weak" class="formula">\( \color{green}{pH = \frac{1}{2} (pK_a - \log C_a)} \)</div>
        </div>
    </div>

    <script>
        // Global variables
        let logCa = -1;
        let pKa = 1.2;
        let canvasWidth, canvasHeight;
        let xMin = -4, xMax = 7;
        let yMin = 0, yMax = 7;

        // p5.js sketch
        function setup() {
            const container = document.getElementById('visualization-container');
            canvasWidth = container.offsetWidth;
            canvasHeight = container.offsetHeight;

            let canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('p5-canvas');
            noLoop();

            // Set up slider event listeners
            document.getElementById('slider-logCa').addEventListener('input', function() {
                logCa = parseFloat(this.value);
                document.getElementById('logCa-value').textContent = logCa.toFixed(2);
                redraw();
            });

            document.getElementById('slider-pka').addEventListener('input', function() {
                pKa = parseFloat(this.value);
                document.getElementById('pka-value').textContent = pKa.toFixed(1);
                redraw();
            });
        }

        function draw() {
            background(255);
            drawAxes();
            drawStrongAcidLine();
            drawWeakAcidLine();
            drawExactCurve();
            drawIndicatorPoint();
        }

        function drawAxes() {
            stroke(100);
            strokeWeight(1);

            // Draw x-axis
            line(mapX(xMin), mapY(0), mapX(xMax), mapY(0));

            // Draw y-axis
            line(mapX(0), mapY(yMin), mapX(0), mapY(yMax));

            // Draw x-axis ticks and labels
            for (let x = xMin; x <= xMax; x++) {
                if (x % 1 === 0) { // integer values
                    line(mapX(x), mapY(0), mapX(x), mapY(0) + 5);
                    noStroke();
                    fill(0);
                    textAlign(CENTER, TOP);
                    text(x, mapX(x), mapY(0) + 8);
                }
            }

            // Draw y-axis ticks and labels
            for (let y = yMin; y <= yMax; y++) {
                if (y % 1 === 0) { // integer values
                    line(mapX(0), mapY(y), mapX(0) - 5, mapY(y));
                    noStroke();
                    fill(0);
                    textAlign(RIGHT, CENTER);
                    text(y, mapX(0) - 8, mapY(y));
                }
            }

            // Axis labels
            textAlign(CENTER, CENTER);
            textSize(14);
            text("pKₐ", mapX((xMin + xMax)/2), mapY(0) + 30);

            push();
            translate(30, mapY((yMin + yMax)/2));
            rotate(-HALF_PI);
            text("pH", 0, 0);
            pop();
        }

        function drawStrongAcidLine() {
            stroke('maroon');
            strokeWeight(1.5);

            const pH = -logCa;
            line(mapX(xMin), mapY(pH), mapX(xMax), mapY(pH));
        }

        function drawWeakAcidLine() {
            stroke('green');
            strokeWeight(1.5);

            const pH1 = 0.5 * (xMin - logCa);
            const pH2 = 0.5 * (xMax - logCa);
            line(mapX(xMin), mapY(pH1), mapX(xMax), mapY(pH2));
        }

        function drawExactCurve() {
            stroke('blue');
            strokeWeight(2);
            noFill();

            beginShape();
            const steps = 100;
            const stepSize = (xMax - xMin) / steps;

            for (let i = 0; i <= steps; i++) {
                const currentPKa = xMin + i * stepSize;
                const pH = calculateExactPH(currentPKa, logCa);
                vertex(mapX(currentPKa), mapY(pH));
            }
            endShape();
        }

        function drawIndicatorPoint() {
            const exactPH = calculateExactPH(pKa, logCa);

            fill('orange');
            stroke(0);
            strokeWeight(1);
            ellipse(mapX(pKa), mapY(exactPH), 10, 10);
        }

        function calculateExactPH(pKa, logCa) {
            const Ka = pow(10, -pKa);
            const Ca = pow(10, logCa);

            // Solve quadratic equation: [H+]^2 + Ka[H+] - Ka*Ca = 0
            const a = 1;
            const b = Ka;
            const c = -Ka * Ca;

            const discriminant = b * b - 4 * a * c;
            const H = (-b + sqrt(discriminant)) / (2 * a);

            return -log10(H);
        }

        function mapX(x) {
            return map(x, xMin, xMax, 50, width - 50);
        }

        function mapY(y) {
            return map(y, yMin, yMax, height - 50, 50);
        }
    </script>
</body>
</html>
