    <!-- CDN for p5.js -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>

    <script>
        // Global variables
        let gridSize = 50;
        let cellSize;
        let canvas;
        let algorithmHistory = [];
        let currentStep = 1;
        let isDraggingStart = false;
        let isDraggingEnd = false;

        // Obstacle configurations
        const obstacleConfigs = {
            '1-square': [{x: 25, y: 25}],
            '2-squares': [{x: 20, y: 20}, {x: 30, y: 30}],
            'l-shaped-wall': [
                {x: 20, y: 20}, {x: 21, y: 20}, {x: 22, y: 20}, {x: 23, y: 20}, {x: 24, y: 20},
                {x: 20, y: 21}, {x: 20, y: 22}, {x: 20, y: 23}, {x: 20, y: 24}
            ]
        };

        // Priority Queue implementation for algorithms
        class PriorityQueue {
            constructor() {
                this.elements = [];
            }

            enqueue(element, priority) {
                this.elements.push({element, priority});
                this.elements.sort((a, b) => a.priority - b.priority);
            }

            dequeue() {
                return this.elements.shift().element;
            }

            isEmpty() {
                return this.elements.length === 0;
            }
        }

        // p5.js setup
        function setup() {
            const container = document.getElementById('canvas-container');
            const width = container.clientWidth;
            const height = container.clientHeight;

            canvas = createCanvas(width, height);
            canvas.parent('canvas-container');

            cellSize = min(width, height) / gridSize;

            // Initialize event listeners
            initializeEventListeners();

            // Run initial computation
            computePath();
        }

        function draw() {
            background(255);

            if (algorithmHistory.length === 0) return;

            const currentState = algorithmHistory[currentStep - 1];
            const { grid, openSet, closedSet, current, path } = currentState;

            // Draw grid
            for (let y = 0; y < gridSize; y++) {
                for (let x = 0; x < gridSize; x++) {
                    // Default cell color (white)
                    fill(255);

                    // Check if cell is obstacle
                    const isObstacle = grid[y][x] === 1;
                    if (isObstacle) {
                        fill(0); // Black for obstacles
                    }

                    // Check if cell is in open set
                    const isOpen = openSet.some(node => node.x === x && node.y === y);
                    if (isOpen) {
                        fill(189, 224, 254); // Light blue for open set
                    }

                    // Check if cell is in closed set
                    const isClosed = closedSet.some(node => node.x === x && node.y === y);
                    if (isClosed) {
                        fill(0, 0, 205); // Dark blue for closed set
                    }

                    // Check if cell is current
                    const isCurrent = current && current.x === x && current.y === y;
                    if (isCurrent) {
                        fill(255, 215, 0); // Gold for current node
                    }

                    // Draw cell
                    stroke(200);
                    rect(x * cellSize, y * cellSize, cellSize, cellSize);
                }
            }

            // Draw start and end points
            const startPos = getJoystickPosition('start');
            const endPos = getJoystickPosition('end');

            fill(52, 168, 83); // Green for start
            ellipse(startPos.x * cellSize + cellSize/2, startPos.y * cellSize + cellSize/2, cellSize * 0.8);

            fill(52, 168, 83); // Green for end
            ellipse(endPos.x * cellSize + cellSize/2, endPos.y * cellSize + cellSize/2, cellSize * 0.8);

            // Draw path if we're at the final step
            if (currentStep === algorithmHistory.length && path && path.length > 0) {
                stroke(255, 0, 0);
                strokeWeight(3);
                noFill();

                beginShape();
                for (const node of path) {
                    vertex(node.x * cellSize + cellSize/2, node.y * cellSize + cellSize/2);
                }
                endShape();
            }
        }

        function windowResized() {
            const container = document.getElementById('canvas-container');
            const width = container.clientWidth;
            const height = container.clientHeight;

            resizeCanvas(width, height);
            cellSize = min(width, height) / gridSize;
        }

        // Initialize all event listeners
        function initializeEventListeners() {
            // Algorithm selection
            document.querySelectorAll('input[name="algorithm"]').forEach(radio => {
                radio.addEventListener('change', computePath);
            });

            // Step slider
            const stepSlider = document.getElementById('slider-step');
            const stepLabel = document.getElementById('label-step');

            stepSlider.addEventListener('input', function() {
                currentStep = parseInt(this.value);
                stepLabel.textContent = currentStep;
                redraw();
            });

            // Obstacles dropdown
            document.getElementById('select-obstacles').addEventListener('change', computePath);

            // Joystick controls
            setupJoystick('start');
            setupJoystick('end');
        }

        // Setup joystick drag functionality
        function setupJoystick(type) {
            const joystick = document.getElementById(`joystick-${type}`);
            const handle = document.getElementById(`handle-${type}`);

            handle.addEventListener('mousedown', function(e) {
                e.preventDefault();
                if (type === 'start') isDraggingStart = true;
                else isDraggingEnd = true;

                document.addEventListener('mousemove', moveHandle);
                document.addEventListener('mouseup', stopDrag);
            });

            function moveHandle(e) {
                const rect = joystick.getBoundingClientRect();
                let x = (e.clientX - rect.left) / rect.width * 100;
                let y = (e.clientY - rect.top) / rect.height * 100;

                // Constrain to joystick area
                x = Math.max(0, Math.min(100, x));
                y = Math.max(0, Math.min(100, y));

                handle.style.left = `${x}%`;
                handle.style.top = `${y}%`;
            }

            function stopDrag() {
                if (type === 'start') isDraggingStart = false;
                else isDraggingEnd = false;

                document.removeEventListener('mousemove', moveHandle);
                document.removeEventListener('mouseup', stopDrag);

                computePath();
            }
        }

        // Get normalized joystick position (0-1 range)
        function getJoystickPosition(type) {
            const handle = document.getElementById(`handle-${type}`);
            const style = window.getComputedStyle(handle);

            return {
                x: Math.floor(parseFloat(style.left) / 100 * (gridSize - 1)),
                y: Math.floor(parseFloat(style.top) / 100 * (gridSize - 1))
            };
        }

        // Compute path based on current settings
        function computePath() {
            // Get current settings
            const algorithm = document.querySelector('input[name="algorithm"]:checked').value;
            const startPos = getJoystickPosition('start');
            const endPos = getJoystickPosition('end');
            const obstacleType = document.getElementById('select-obstacles').value;

            // Initialize grid
            const grid = Array(gridSize).fill().map(() => Array(gridSize).fill(0));

            // Add obstacles
            obstacleConfigs[obstacleType].forEach(obs => {
                if (obs.x >= 0 && obs.x < gridSize && obs.y >= 0 && obs.y < gridSize) {
                    grid[obs.y][obs.x] = 1;
                }
            });

            // Run selected algorithm
            if (algorithm === 'dijkstra') {
                algorithmHistory = dijkstra(grid, startPos, endPos);
            } else {
                algorithmHistory = aStar(grid, startPos, endPos);
            }

            // Update slider
            const stepSlider = document.getElementById('slider-step');
            stepSlider.max = algorithmHistory.length;
            stepSlider.value = 1;
            document.getElementById('label-step').textContent = '1';
            currentStep = 1;

            redraw();
        }

        // Dijkstra's algorithm implementation
        function dijkstra(grid, start, end) {
            const history = [];
            const openSet = new PriorityQueue();
            const closedSet = [];
            const cameFrom = {};
            const gScore = {};

            // Initialize grid with distances
            for (let y = 0; y < gridSize; y++) {
                for (let x = 0; x < gridSize; x++) {
                    gScore[`${x},${y}`] = Infinity;
                }
            }

            gScore[`${start.x},${start.y}`] = 0;
            openSet.enqueue(start, 0);

            while (!openSet.isEmpty()) {
                const current = openSet.dequeue();

                // Skip if this is an obstacle
                if (grid[current.y][current.x] === 1) continue;

                // Add to closed set
                closedSet.push(current);

                // Check if we've reached the end
                if (current.x === end.x && current.y === end.y) {
                    // Reconstruct path
                    const path = reconstructPath(cameFrom, current);

                    // Add final state to history
                    history.push({
                        grid: JSON.parse(JSON.stringify(grid)),
                        openSet: openSet.elements.map(e => e.element),
                        closedSet: [...closedSet],
                        current: null,
                        path: path
                    });

                    return history;
                }

                // Get neighbors (8-directional)
                const neighbors = getNeighbors(current);

                for (const neighbor of neighbors) {
                    // Skip if out of bounds or obstacle
                    if (neighbor.x < 0 || neighbor.x >= gridSize || 
                        neighbor.y < 0 || neighbor.y >= gridSize || 
                        grid[neighbor.y][neighbor.x] === 1) {
                        continue;
                    }

                    // Skip if already in closed set
                    if (closedSet.some(node => node.x === neighbor.x && node.y === neighbor.y)) {
                        continue;
                    }

                    // Calculate tentative gScore
                    const dx = Math.abs(neighbor.x - current.x);
                    const dy = Math.abs(neighbor.y - current.y);
                    const moveCost = (dx === 1 && dy === 1) ? Math.sqrt(2) : 1;
                    const tentativeGScore = gScore[`${current.x},${current.y}`] + moveCost;

                    if (tentativeGScore < gScore[`${neighbor.x},${neighbor.y}`]) {
                        cameFrom[`${neighbor.x},${neighbor.y}`] = current;
                        gScore[`${neighbor.x},${neighbor.y}`] = tentativeGScore;

                        // Check if neighbor is already in open set
                        const inOpenSet = openSet.elements.some(e => 
                            e.element.x === neighbor.x && e.element.y === neighbor.y);

                        if (!inOpenSet) {
                            openSet.enqueue(neighbor, tentativeGScore);
                        }
                    }
                }

                // Add current state to history
                history.push({
                    grid: JSON.parse(JSON.stringify(grid)),
                    openSet: openSet.elements.map(e => e.element),
                    closedSet: [...closedSet],
                    current: {...current},
                    path: null
                });
            }

            // No path found
            history.push({
                grid: JSON.parse(JSON.stringify(grid)),
                openSet: [],
                closedSet: [...closedSet],
                current: null,
                path: []
            });

            return history;
        }

        // A* algorithm implementation
        function aStar(grid, start, end) {
            const history = [];
            const openSet = new PriorityQueue();
            const closedSet = [];
            const cameFrom = {};
            const gScore = {};
            const fScore = {};

            // Initialize grid with distances
            for (let y = 0; y < gridSize; y++) {
                for (let x = 0; x < gridSize; x++) {
                    gScore[`${x},${y}`] = Infinity;
                    fScore[`${x},${y}`] = Infinity;
                }
            }

            gScore[`${start.x},${start.y}`] = 0;
            fScore[`${start.x},${start.y}`] = heuristic(start, end);
            openSet.enqueue(start, fScore[`${start.x},${start.y}`]);

            while (!openSet.isEmpty()) {
                const current = openSet.dequeue();

                // Skip if this is an obstacle
                if (grid[current.y][current.x] === 1) continue;

                // Add to closed set
                closedSet.push(current);

                // Check if we've reached the end
                if (current.x === end.x && current.y === end.y) {
                    // Reconstruct path
                    const path = reconstructPath(cameFrom, current);

                    // Add final state to history
                    history.push({
                        grid: JSON.parse(JSON.stringify(grid)),
                        openSet: openSet.elements.map(e => e.element),
                        closedSet: [...closedSet],
                        current: null,
                        path: path
                    });

                    return history;
                }

                // Get neighbors (8-directional)
                const neighbors = getNeighbors(current);

                for (const neighbor of neighbors) {
                    // Skip if out of bounds or obstacle
                    if (neighbor.x < 0 || neighbor.x >= gridSize || 
                        neighbor.y < 0 || neighbor.y >= gridSize || 
                        grid[neighbor.y][neighbor.x] === 1) {
                        continue;
                    }

                    // Skip if already in closed set
                    if (closedSet.some(node => node.x === neighbor.x && node.y === neighbor.y)) {
                        continue;
                    }

                    // Calculate tentative gScore
                    const dx = Math.abs(neighbor.x - current.x);
                    const dy = Math.abs(neighbor.y - current.y);
                    const moveCost = (dx === 1 && dy === 1) ? Math.sqrt(2) : 1;
                    const tentativeGScore = gScore[`${current.x},${current.y}`] + moveCost;

                    if (tentativeGScore < gScore[`${neighbor.x},${neighbor.y}`]) {
                        cameFrom[`${neighbor.x},${neighbor.y}`] = current;
                        gScore[`${neighbor.x},${neighbor.y}`] = tentativeGScore;
                        fScore[`${neighbor.x},${neighbor.y}`] = tentativeGScore + heuristic(neighbor, end);

                        // Check if neighbor is already in open set
                        const inOpenSet = openSet.elements.some(e => 
                            e.element.x === neighbor.x && e.element.y === neighbor.y);

                        if (!inOpenSet) {
                            openSet.enqueue(neighbor, fScore[`${neighbor.x},${neighbor.y}`]);
                        }
                    }
                }

                // Add current state to history
                history.push({
                    grid: JSON.parse(JSON.stringify(grid)),
                    openSet: openSet.elements.map(e => e.element),
                    closedSet: [...closedSet],
                    current: {...current},
                    path: null
                });
            }

            // No path found
            history.push({
                grid: JSON.parse(JSON.stringify(grid)),
                openSet: [],
                closedSet: [...closedSet],
                current: null,
                path: []
            });

            return history;
        }

        // Heuristic function for A* (Manhattan distance)
        function heuristic(a, b) {
            return Math.abs(a.x - b.x) + Math.abs(a.y - b.y);
        }

        // Get 8-directional neighbors
        function getNeighbors(node) {
            return [
                {x: node.x - 1, y: node.y - 1}, // top-left
                {x: node.x, y: node.y - 1},     // top
                {x: node.x + 1, y: node.y - 1}, // top-right
                {x: node.x - 1, y: node.y},     // left
                {x: node.x + 1, y: node.y},     // right
                {x: node.x - 1, y: node.y + 1}, // bottom-left
                {x: node.x, y: node.y + 1},     // bottom
                {x: node.x + 1, y: node.y + 1}  // bottom-right
            ];
        }

        // Reconstruct path from cameFrom map
        function reconstructPath(cameFrom, current) {
            const path = [current];
            while (`${current.x},${current.y}` in cameFrom) {
                current = cameFrom[`${current.x},${current.y}`];
                path.unshift(current);
            }
            return path;
        }

        // Initialize p5.js
        new p5();
    </script>
</body>
</html>
