            eX: document.getElementById('display-Ex'),
            eY: document.getElementById('display-Ey'),
            xPos: document.getElementById('display-x-pos'),
            yPos: document.getElementById('display-y-pos'),
            force: document.getElementById('display-force')
        };

        // Initialize p5.js sketch
        function sketch(p) {
            let canvas;

            p.setup = function() {
                canvas = p.createCanvas(600, 500);
                canvas.parent('canvas-container');
                p.rectMode(p.CENTER);
                p.textAlign(p.CENTER, p.CENTER);

                // Set up event listeners for sliders
                sourceChargeSlider.addEventListener('input', function() {
                    Q = parseFloat(this.value);
                    sourceChargeLabel.textContent = Q.toFixed(1);
                    updateSimulation();
                });

                testChargeSlider.addEventListener('input', function() {
                    q = parseFloat(this.value);
                    testChargeLabel.textContent = q.toFixed(4);
                    updateSimulation();
                });
            };

            p.draw = function() {
                p.background(255);

                // Set coordinate system: origin at center, y-axis inverted
                p.translate(p.width/2, p.height/2);
                p.scale(1, -1);

                drawGrid(p);
                drawSourceCharge(p);
                drawTestCharge(p);

                // Calculate and draw vectors
                const r = p.dist(0, 0, testPos.x, testPos.y);
                const k = 1;  // Coulomb constant (simplified)

                if (r > 0) {
                    // Electric field vector
                    const eMag = k * Q / (r * r);
                    const eX = eMag * testPos.x / r;
                    const eY = eMag * testPos.y / r;

                    // Electric force vector
                    const fX = q * eX;
                    const fY = q * eY;

                    // Draw vectors
                    drawArrow(p, testPos.x, testPos.y, eX, eY, p.color(255, 0, 0));
                    if (q !== 0) {
                        drawArrow(p, testPos.x, testPos.y, fX, fY, p.color(0, 0, 255));
                    }

                    // Update info panel
                    updateInfoPanel(testPos.x, testPos.y, eMag, eX, eY, p.sqrt(fX*fX + fY*fY));
                }
            };

            p.mousePressed = function() {
                // Convert mouse coordinates to logical coordinates
                const mouseX = (p.mouseX - p.width/2) / scaleFactor;
                const mouseY = -(p.mouseY - p.height/2) / scaleFactor;

                // Check if mouse is over test charge
                const dist = p.dist(mouseX, mouseY, testPos.x, testPos.y);
                if (dist < 0.5) {  // Hit radius of 0.5 meters
                    isDragging = true;
                }
            };

            p.mouseDragged = function() {
                if (isDragging) {
                    // Update test charge position
                    testPos.x = (p.mouseX - p.width/2) / scaleFactor;
                    testPos.y = -(p.mouseY - p.height/2) / scaleFactor;
                }
            };

            p.mouseReleased = function() {
                isDragging = false;
            };

            function drawGrid(p) {
                p.stroke(220);
                p.strokeWeight(1);

                // Draw grid lines
                const gridSize = 2;  // Meters between major grid lines
                const minX = -Math.floor(p.width / 2 / scaleFactor) - gridSize;
                const maxX = Math.floor(p.width / 2 / scaleFactor) + gridSize;
                const minY = -Math.floor(p.height / 2 / scaleFactor) - gridSize;
                const maxY = Math.floor(p.height / 2 / scaleFactor) + gridSize;

                // Vertical lines
                for (let x = minX; x <= maxX; x += gridSize) {
                    p.line(x * scaleFactor, minY * scaleFactor, x * scaleFactor, maxY * scaleFactor);
                    p.push();
                    p.scale(1, -1);  // Flip y-axis for text
                    p.fill(0);
                    p.noStroke();
                    p.text(x, x * scaleFactor, 20);
                    p.pop();
                }

                // Horizontal lines
                for (let y = minY; y <= maxY; y += gridSize) {
                    p.line(minX * scaleFactor, y * scaleFactor, maxX * scaleFactor, y * scaleFactor);
                    if (y !== 0) {  // Don't draw 0 on y-axis (overlaps with x-axis)
                        p.push();
                        p.scale(1, -1);  // Flip y-axis for text
                        p.fill(0);
                        p.noStroke();
                        p.text(y, -20, -y * scaleFactor);
                        p.pop();
                    }
                }

                // Draw axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(minX * scaleFactor, 0, maxX * scaleFactor, 0);  // x-axis
                p.line(0, minY * scaleFactor, 0, maxY * scaleFactor);  // y-axis
            }

            function drawSourceCharge(p) {
                p.fill(0, 0, 255);
                p.noStroke();
                p.circle(0, 0, 20);
            }

            function drawTestCharge(p) {
                p.fill(0);
                p.noStroke();
                p.circle(testPos.x * scaleFactor, testPos.y * scaleFactor, 15);
            }

            function drawArrow(p, x, y, dx, dy, color) {
                const scaledX = x * scaleFactor;
                const scaledY = y * scaleFactor;
                const scaledDx = dx * vectorScale;
                const scaledDy = dy * vectorScale;

                p.push();
                p.translate(scaledX, scaledY);
                p.stroke(color);
                p.strokeWeight(2);
                p.fill(color);

                // Draw line
                p.line(0, 0, scaledDx, scaledDy);

                // Draw arrowhead
                const angle = p.atan2(scaledDy, scaledDx);
                p.rotate(angle);
                p.triangle(15, 0, 0, 5, 0, -5);
                p.pop();
            }

            function updateInfoPanel(x, y, eMag, eX, eY, forceMag) {
                infoPanel.eMag.textContent = `|E| = ${eMag.toFixed(3)} N/C`;
                infoPanel.eX.textContent = `E<sub>x</sub> component = ${eX.toFixed(3)} N/C`;
                infoPanel.eY.textContent = `E<sub>y</sub> component = ${eY.toFixed(3)} N/C`;
                infoPanel.xPos.textContent = `x position = ${x.toFixed(2)} m`;
                infoPanel.yPos.textContent = `y position = ${y.toFixed(2)} m`;
                infoPanel.force.textContent = `Electric force = ${forceMag.toFixed(3)} N`;
            }

            function updateSimulation() {
                // Trigger redraw
                p.redraw();
            }
        }

        // Create p5 instance
        new p5(sketch);
    </script>
</body>
</html>
