                updatePlots();
            });

            // Create initial plots
            createPlots();
            updatePlots();
        }

        function setActiveButton(activeId) {
            document.getElementById('btn-linear-linear').classList.remove('active');
            document.getElementById('btn-log-linear').classList.remove('active');
            document.getElementById('btn-log-log').classList.remove('active');
            document.getElementById(activeId).classList.add('active');
        }

        function createPlots() {
            // Left plot (real space functions)
            leftPlot = Plotly.newPlot('plot-left', [], {
                title: 'Potential Functions in Real Space',
                xaxis: { title: 'r' },
                yaxis: { title: 'f(r)' },
                margin: { t: 40, l: 60, r: 40, b: 60 },
                showlegend: false,
                hovermode: 'closest'
            });

            // Right plot (Fourier transforms)
            rightPlot = Plotly.newPlot('plot-right', [], {
                title: '3D Fourier Transform (real part)',
                xaxis: { title: 'k' },
                yaxis: { title: 'F(k)', type: 'linear' },
                margin: { t: 40, l: 60, r: 40, b: 60 },
                showlegend: false,
                hovermode: 'closest'
            });
        }

        function updatePlots() {
            // Get current control states
            const showHertz = document.getElementById('checkbox-hertz').checked;
            const showOverlap = document.getElementById('checkbox-overlap').checked;
            const showGaussian = document.getElementById('checkbox-gaussian').checked;

            const rMax = parseFloat(document.getElementById('slider-r').value);
            const kMax = parseFloat(document.getElementById('slider-k').value);

            const isLinearLinear = document.getElementById('btn-linear-linear').classList.contains('active');
            const isLogLinear = document.getElementById('btn-log-linear').classList.contains('active');
            const isLogLog = document.getElementById('btn-log-log').classList.contains('active');

            // Update left plot (real space functions)
            const rValues = Array.from({length: 200}, (_, i) => i * rMax / 199);

            const leftTraces = [];
            if (showHertz) {
                leftTraces.push({
                    x: rValues,
                    y: rValues.map(r => {
                        if (r <= 1) return Math.pow(1 - r*r, 2.5);
                        return 0;
                    }),
                    line: { color: '#ff0000' },
                    hovertemplate: 'Hertz<br>f(r) = (1 - r<sup>2</sup>)<sup>5/2</sup>, r ≤ 1<extra></extra>'
                });
            }
            if (showOverlap) {
                leftTraces.push({
                    x: rValues,
                    y: rValues.map(r => {
                        if (r <= 1) return Math.pow(1 - r, 2);
                        return 0;
                    }),
                    line: { color: '#000000' },
                    hovertemplate: 'Overlap<br>f(r) = (1 - r)<sup>2</sup>, r ≤ 1<extra></extra>'
                });
            }
            if (showGaussian) {
                leftTraces.push({
                    x: rValues,
                    y: rValues.map(r => Math.exp(-Math.PI * r * r)),
                    line: { color: '#0000ff' },
                    hovertemplate: 'Gaussian<br>f(r) = e<sup>-πr<sup>2</sup></sup><extra></extra>'
                });
            }

            Plotly.react('plot-left', leftTraces, {
                xaxis: { range: [0, rMax] }
            });

            // Update right plot (Fourier transforms)
            const kValues = Array.from({length: 200}, (_, i) => i * kMax / 199);

            const rightTraces = [];
            if (showHertz) {
                rightTraces.push({
                    x: kValues,
                    y: kValues.map(k => {
                        if (k === 0) return 15 * Math.PI / 10; // limit as k->0
                        return 15 * Math.PI * ((3 - k*k) * Math.sin(k) - 3 * k * Math.cos(k)) / Math.pow(k, 5);
                    }),
                    line: { color: '#ff0000' },
                    hovertemplate: 'Hertz<br>F(k) = 15π((3-k²)sin(k) - 3kcos(k))/k⁵<extra></extra>'
                });
            }
            if (showOverlap) {
                rightTraces.push({
                    x: kValues,
                    y: kValues.map(k => {
                        if (k === 0) return 8 * Math.PI / 3; // limit as k->0
                        return 8 * Math.PI * (2 - k*k - 2 * Math.cos(k) - 2 * k * Math.sin(k)) / Math.pow(k, 4);
                    }),
                    line: { color: '#000000' },
                    hovertemplate: 'Overlap<br>F(k) = 8π(2 - k² - 2cos(k) - 2ksin(k))/k⁴<extra></extra>'
                });
            }
            if (showGaussian) {
                rightTraces.push({
                    x: kValues,
                    y: kValues.map(k => (1/Math.sqrt(Math.PI)) * Math.exp(-k*k / (4*Math.PI))),
                    line: { color: '#0000ff' },
                    hovertemplate: 'Gaussian<br>F(k) = (1/√π)e<sup>-k²/(4π)</sup><extra></extra>'
                });
            }

            Plotly.react('plot-right', rightTraces, {
                xaxis: { range: [0, kMax] },
                yaxis: { 
                    type: isLinearLinear ? 'linear' : 'log',
                    autorange: true
                }
            });

            // Update MathJax rendering for tooltips
            if (typeof MathJax !== 'undefined') {
                MathJax.typeset();
            }
        }
    </script>
