                </div>
                <div class="budget-circle" id="budget-2050">
                    <svg width="60" height="60" viewBox="0 0 60 60"></svg>
                    <div class="budget-circle-value">2050</div>
                </div>
            </div>
        </div>
    </div>

    <script>
        // Global variables
        let neutralYear = 2040;
        let scale = 'individual';
        let plotType = 'emissions';
        let population = 250;
        let annualEmissions = 14;
        let reductionTime = 20;
        let annualBudget = 1.5;
        let startYear = 2020;
        let compensationCost = 15;
        let fractionalCompensation = 50;
        let constantCompensation = 25;

        // DOM elements
        const mainPlotContainer = document.getElementById('main-plot-container');
        const budgetPlotContainer = document.getElementById('budget-plot-container');

        // Initialize p5.js sketch
        let mainPlot;
        let budgetCircles = [];

        function setup() {
            // Create main plot canvas
            const canvas = createCanvas(mainPlotContainer.offsetWidth, mainPlotContainer.offsetHeight);
            canvas.parent('main-plot-container');
            noLoop();

            // Initialize budget circles
            initializeBudgetCircles();

            // Set up event listeners
            setupEventListeners();

            // Initial draw
            redraw();
        }

        function initializeBudgetCircles() {
            const years = [2020, 2030, 2040, 2050];
            years.forEach(year => {
                const circleContainer = document.getElementById(`budget-${year}`);
                const svg = circleContainer.querySelector('svg');
                budgetCircles.push({
                    year: year,
                    svg: svg,
                    value: 0
                });
            });
        }

        function setupEventListeners() {
            // Scale switch
            document.getElementById('scale-switch').addEventListener('change', (e) => {
                scale = e.target.value;
                const populationSlider = document.getElementById('slider-population');
                populationSlider.disabled = scale !== 'population';
                redraw();
            });

            // Type switch
            document.getElementById('type-switch').addEventListener('change', (e) => {
                plotType = e.target.value;
                redraw();
            });

            // Climate neutral buttons
            document.getElementById('climate-neutral-buttons').addEventListener('click', (e) => {
                if (e.target.tagName === 'BUTTON') {
                    // Remove active class from all buttons
                    document.querySelectorAll('#climate-neutral-buttons button').forEach(btn => {
                        btn.classList.remove('active');
                    });

                    // Add active class to clicked button
                    e.target.classList.add('active');

                    // Set neutral year
                    switch(e.target.id) {
                        case 'btn-neutral-now': neutralYear = 2020; break;
                        case 'btn-neutral-2030': neutralYear = 2030; break;
                        case 'btn-neutral-2040': neutralYear = 2040; break;
                        case 'btn-neutral-2050': neutralYear = 2050; break;
                        case 'btn-neutral-never': neutralYear = null; break;
                    }

                    redraw();
                }
            });

            // Sliders
            const sliders = [
                { id: 'slider-population', valueId: 'value-population', prop: 'population' },
                { id: 'slider-annual-emissions', valueId: 'value-annual-emissions', prop: 'annualEmissions' },
                { id: 'slider-reduction-time', valueId: 'value-reduction-time', prop: 'reductionTime' },
                { id: 'slider-annual-budget', valueId: 'value-annual-budget', prop: 'annualBudget' },
                { id: 'slider-start-year', valueId: 'value-start-year', prop: 'startYear' },
                { id: 'slider-compensation-cost', valueId: 'value-compensation-cost', prop: 'compensationCost' },
                { id: 'slider-fractional-compensation', valueId: 'value-fractional-compensation', prop: 'fractionalCompensation' },
                { id: 'slider-constant-compensation', valueId: 'value-constant-compensation', prop: 'constantCompensation' }
            ];

            sliders.forEach(slider => {
                const element = document.getElementById(slider.id);
                const valueElement = document.getElementById(slider.valueId);

                element.addEventListener('input', () => {
                    let value = parseFloat(element.value);
                    if (slider.id === 'slider-annual-emissions') {
                        valueElement.textContent = value.toFixed(1);
                    } else {
                        valueElement.textContent = value;
                    }

                    // Update global variable
                    window[slider.prop] = value;

                    redraw();
                });
            });

            // Window resize
            window.addEventListener('resize', () => {
                resizeCanvas(mainPlotContainer.offsetWidth, mainPlotContainer.offsetHeight);
                redraw();
            });
        }

        function redraw() {
            // Clear previous drawings
            clear();
            background(255);

            // Calculate data
            const data = calculateData();

            // Draw based on plot type
            if (plotType === 'emissions') {
                drawEmissionsPlot(data);
            } else {
                drawCostsPlot(data);
            }

            // Update budget circles
            updateBudgetCircles(data);
        }

        function calculateData() {
            const data = [];
            const totalBudget = 30 * annualBudget;

            for (let year = 2020; year <= 2050; year++) {
                // Calculate real emissions per person
                let realEmissions;
                if (year < startYear) {
                    realEmissions = annualEmissions;
                } else {
                    const yearsSinceStart = year - startYear;
                    realEmissions = annualEmissions * Math.pow(0.5, yearsSinceStart / reductionTime);
                }

                // Calculate compensated emissions
                const fractional = realEmissions * (fractionalCompensation / 100);
                const constant = annualEmissions * (constantCompensation / 100);
                const compensatedEmissions = fractional + constant;

                // Calculate effective emissions
                const effectiveEmissions = realEmissions - compensatedEmissions;

                // Calculate cumulative emissions
                let cumulativeEffective = 0;
                for (let y = 2020; y <= year; y++) {
                    const yReal = y < startYear ? annualEmissions : 
                                 annualEmissions * Math.pow(0.5, (y - startYear) / reductionTime);
                    const yFractional = yReal * (fractionalCompensation / 100);
                    const yConstant = annualEmissions * (constantCompensation / 100);
                    cumulativeEffective += (yReal - (yFractional + yConstant));
                }

                // Calculate costs
                const annualCosts = compensatedEmissions * compensationCost;

                data.push({
                    year: year,
                    realEmissions: realEmissions,
                    compensatedEmissions: compensatedEmissions,
                    effectiveEmissions: effectiveEmissions,
                    cumulativeEffective: cumulativeEffective,
                    annualCosts: annualCosts,
                    budgetRemaining: totalBudget - cumulativeEffective
                });
            }

            return data;
        }

        function drawEmissionsPlot(data) {
            // Set up plot dimensions
            const margin = { top: 40, right: 40, bottom: 40, left: 60 };
            const plotWidth = width - margin.left - margin.right;
            const plotHeight = height - margin.top - margin.bottom;

            // Calculate scales
            const xScale = (x) => margin.left + (x - 2020) / 30 * plotWidth;
            const maxEmissions = Math.max(...data.map(d => d.realEmissions));
            const yScale = (y) => height - margin.bottom - (y / maxEmissions) * plotHeight;

            // Draw axes
            stroke(200);
            strokeWeight(1);
            line(margin.left, margin.top, margin.left, height - margin.bottom);
            line(margin.left, height - margin.bottom, width - margin.right, height - margin.bottom);

            // Draw x-axis ticks and labels
            for (let year = 2020; year <= 2050; year += 5) {
                const x = xScale(year);
                line(x, height - margin.bottom, x, height - margin.bottom + 5);
                noStroke();
                textAlign(CENTER, TOP);
                text(year, x, height - margin.bottom + 10);
            }

            // Draw y-axis ticks and labels
            const yTicks = 5;
            const yTickStep = maxEmissions / yTicks;
            for (let i = 0; i <= yTicks; i++) {
                const yValue = i * yTickStep;
                const y = yScale(yValue);
                line(margin.left - 5, y, margin.left, y);
                noStroke();
                textAlign(RIGHT, CENTER);
                text(yValue.toFixed(1), margin.left - 10, y);
            }

            // Draw y-axis label
            textAlign(CENTER, CENTER);
            textSize(12);
            push();
            translate(margin.left - 30, height / 2);
            rotate(-HALF_PI);
            text(scale === 'individual' ? 'annual CO₂ emissions per person (tons)' : 'annual city emission of CO₂ (mil tons)', 0, 0);
            pop();

            // Draw x-axis label
            textAlign(CENTER, TOP);
            text('year', width / 2, height - margin.bottom + 25);

            // Draw emissions curves
            beginShape();
            noFill();
            stroke(255, 0, 0);
            strokeWeight(2);
            for (let i = 0; i < data.length; i++) {
                const d = data[i];
                vertex(xScale(d.year), yScale(d.realEmissions));
            }
            endShape();

            // Draw effective emissions curve
            beginShape();
            noFill();
            stroke(0);
            strokeWeight(2);
            for (let i = 0; i < data.length; i++) {
                const d = data[i];
                vertex(xScale(d.year), yScale(d.effectiveEmissions));
            }
            endShape();

            // Fill compensated area (between red and black lines)
            beginShape();
            fill(0, 0, 255, 100);
            noStroke();
            for (let i = 0; i < data.length; i++) {
                const d = data[i];
                vertex(xScale(d.year), yScale(d.realEmissions));
            }
            for (let i = data.length - 1; i >= 0; i--) {
                const d = data[i];
                vertex(xScale(d.year), yScale(d.effectiveEmissions));
            }
            endShape(CLOSE);

            // Fill positive effective emissions area (red)
            beginShape();
            fill(255, 0, 0, 50);
            noStroke();
            for (let i = 0; i < data.length; i++) {
                const d = data[i];
                if (d.effectiveEmissions > 0) {
                    vertex(xScale(d.year), yScale(d.effectiveEmissions));
                } else {
                    vertex(xScale(d.year), yScale(0));
                    break;
                }
            }
            vertex(xScale(data[0].year), yScale(0));
            endShape(CLOSE);

            // Fill negative effective emissions area (green)
            for (let i = 0; i < data.length; i++) {
                const d = data[i];
                if (d.effectiveEmissions < 0) {
                    beginShape();
                    fill(0, 255, 0, 50);
                    noStroke();
                    vertex(xScale(d.year), yScale(0));
                    for (let j = i; j < data.length; j++) {
                        const dj = data[j];
                        if (dj.effectiveEmissions < 0) {
                            vertex(xScale(dj.year), yScale(dj.effectiveEmissions));
                        } else {
                            vertex(xScale(dj.year), yScale(0));
                            break;
                        }
                    }
                    vertex(xScale(data[data.length - 1].year), yScale(0));
                    vertex(xScale(d.year), yScale(0));
                    endShape(CLOSE);
                    break;
                }
            }

            // Draw climate neutral line if selected
            if (neutralYear !== null && neutralYear >= 2020 && neutralYear <= 2050) {
                const neutralX = xScale(neutralYear);
                stroke(0, 150);
                strokeWeight(1);
                line(neutralX, margin.top, neutralX, height - margin.bottom);

                // Label
                textAlign(CENTER, BOTTOM);
                noStroke();
                fill(0);
                text(`climate neutral ${neutralYear}`, neutralX, margin.top - 5);

                // Draw horizontal line for cumulative emissions until neutral year
                const neutralData = data.find(d => d.year === neutralYear);
                if (neutralData) {
                    const yCumulative = yScale(neutralData.cumulativeEffective);
                    stroke(0);
                    strokeWeight(1);
                    drawingContext.setLineDash([5, 5]);
                    line(margin.left, yCumulative, width - margin.right, yCumulative);
                    drawingContext.setLineDash([]);

                    // Label
                    textAlign(RIGHT, CENTER);
                    noStroke();
                    fill(0);
                    text(`CO₂ emissions until ${neutralYear} = ${neutralData.cumulativeEffective.toFixed(1)} tons CO₂`, width - margin.right - 10, yCumulative);
                }
            }

            // Draw horizontal line for cumulative emissions until 2050
            const finalData = data[data.length - 1];
            const yFinalCumulative = yScale(finalData.cumulativeEffective);
            stroke(0);
            strokeWeight(1);
            drawingContext.setLineDash([5, 5]);
            line(margin.left, yFinalCumulative, width - margin.right, yFinalCumulative);
            drawingContext.setLineDash([]);

            // Label
            textAlign(RIGHT, CENTER);
            noStroke();
            fill(0);
            text(`CO₂ emissions until 2050 = ${finalData.cumulativeEffective.toFixed(1)} tons CO₂`, width - margin.right - 10, yFinalCumulative);

            // Draw legend
            const legendX = width - margin.right - 150;
            const legendY = margin.top + 20;

            fill(255);
            stroke(200);
            strokeWeight(1);
            rect(legendX - 10, legendY - 10, 160, 100);

            noStroke();
            textAlign(LEFT, CENTER);
            textSize(12);

            fill(255, 0, 0);
            rect(legendX, legendY, 10, 10);
            fill(0);
            text('real emissions', legendX + 15, legendY + 5);

            fill(0);
            rect(legendX, legendY + 20, 10, 10);
            text('effective emissions', legendX + 15, legendY + 25);

            fill(0, 0, 255, 100);
            rect(legendX, legendY + 40, 10, 10);
            fill(0);
            text('compensated CO₂', legendX + 15, legendY + 45);

            fill(255, 0, 0, 50);
            rect(legendX, legendY + 60, 10, 10);
            fill(0);
            text('positive effective', legendX + 15, legendY + 65);

            fill(0, 255, 0, 50);
            rect(legendX, legendY + 80, 10, 10);
            fill(0);
            text('negative effective', legendX + 15, legendY + 85);
        }

        function drawCostsPlot(data) {
            // Set up plot dimensions
            const margin = { top: 40, right: 40, bottom: 40, left: 60 };
            const plotWidth = width - margin.left - margin.right;
            const plotHeight = height - margin.top - margin.bottom;

            // Calculate scales
            const xScale = (x) => margin.left + (x - 2020) / 30 * plotWidth;
            const maxCosts = Math.max(...data.map(d => d.annualCosts));
            const yScale = (y) => height - margin.bottom - (y / maxCosts) * plotHeight;

            // Draw axes
            stroke(200);
            strokeWeight(1);
            line(margin.left, margin.top, margin.left, height - margin.bottom);
            line(margin.left, height - margin.bottom, width - margin.right, height - margin.bottom);

            // Draw x-axis ticks and labels
            for (let year = 2020; year <= 2050; year += 5) {
                const x = xScale(year);
                line(x, height - margin.bottom, x, height - margin.bottom + 5);
                noStroke();
                textAlign(CENTER, TOP);
                text(year, x, height - margin.bottom + 10);
            }

            // Draw y-axis ticks and labels
            const yTicks = 5;
            const yTickStep = maxCosts / yTicks;
            for (let i = 0; i <= yTicks; i++) {
                const yValue = i * yTickStep;
                const y = yScale(yValue);
                line(margin.left - 5, y, margin.left, y);
                noStroke();
                textAlign(RIGHT, CENTER);
                text('$' + yValue.toFixed(0), margin.left - 10, y);
            }

            // Draw y-axis label
            textAlign(CENTER, CENTER);
            textSize(12);
            push();
            translate(margin.left - 30, height / 2);
            rotate(-HALF_PI);
            text('annual personal compensation costs ($)', 0, 0);
            pop();

            // Draw x-axis label
            textAlign(CENTER, TOP);
            text('year', width / 2, height - margin.bottom + 25);

            // Draw costs curve
            beginShape();
            noFill();
            stroke(0, 0, 255);
            strokeWeight(2);
            for (let i = 0; i < data.length; i++) {
                const d = data[i];
                vertex(xScale(d.year), yScale(d.annualCosts));
            }
            endShape();

            // Fill area under costs curve
            beginShape();
            fill(0, 0, 255, 50);
            noStroke();
            vertex(xScale(2020), yScale(0));
            for (let i = 0; i < data.length; i++) {
                const d = data[i];
                vertex(xScale(d.year), yScale(d.annualCosts));
            }
            vertex(xScale(2050), yScale(0));
            endShape(CLOSE);

            // Calculate total costs
            const totalCosts = data.reduce((sum, d) => sum + d.annualCosts, 0);
            const averageAnnualCost = totalCosts / 31;

            // Draw average annual cost line
            const yAverage = yScale(averageAnnualCost);
            stroke(0);
            strokeWeight(1);
            drawingContext.setLineDash([5, 5]);
            line(margin.left, yAverage, width - margin.right, yAverage);
            drawingContext.setLineDash([]);

            // Label
            textAlign(RIGHT, CENTER);
            noStroke();
            fill(0);
            text(`annual personal compensation costs (average) = $${averageAnnualCost.toFixed(0)} per year`, width - margin.right - 10, yAverage);

            // Draw total costs label
            textAlign(RIGHT, TOP);
            noStroke();
            fill(0);
            text(`total personal compensation costs (until 2050) = $${totalCosts.toFixed(0)}`, width - margin.right - 10, margin.top);

            // Draw legend
            const legendX = width - margin.right - 150;
            const legendY = margin.top + 20;

            fill(255);
            stroke(200);
            strokeWeight(1);
            rect(legendX - 10, legendY - 10, 160, 50);

            noStroke();
            textAlign(LEFT, CENTER);
            textSize(12);

            fill(0, 0, 255);
            rect(legendX, legendY, 10, 10);
            fill(0);
            text('annual compensation costs', legendX + 15, legendY + 5);

            fill(0, 0, 255, 50);
            rect(legendX, legendY + 20, 10, 10);
            fill(0);
            text('total compensation costs', legendX + 15, legendY + 25);
        }

        function updateBudgetCircles(data) {
            const budgetYears = [2020, 2030, 2040, 2050];

            budgetYears.forEach((year, i) => {
                const circleData = data.find(d => d.year === year);
                if (!circleData) return;

                const circleContainer = document.getElementById(`budget-${year}`);
                const svg = circleContainer.querySelector('svg');
                const valueElement = circleContainer.querySelector('.budget-circle-value');

                // Clear previous SVG
                svg.innerHTML = '';

                // Calculate circle properties
                const budgetRemaining = circleData.budgetRemaining;
                const radius = Math.sqrt(Math.abs(budgetRemaining)) * 5;
                const cx = 30;
                const cy = 30;

                // Create SVG circle
                const circle = document.createElementNS('http://www.w3.org/2000/svg', 'circle');
                circle.setAttribute('cx', cx);
                circle.setAttribute('cy', cy);
                circle.setAttribute('r', radius);

                if (budgetRemaining >= 0) {
                    circle.setAttribute('fill', 'green');
                    circle.setAttribute('fill-opacity', '0.5');
                    circle.setAttribute('stroke', 'green');
                } else {
                    circle.setAttribute('fill', 'none');
                    circle.setAttribute('stroke', 'red');
                    circle.setAttribute('stroke-dasharray', '5,5');
                }

                circle.setAttribute('stroke-width', '2');
                svg.appendChild(circle);

                // Add text label
                const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
                text.setAttribute('x', cx);
                text.setAttribute('y', cy);
                text.setAttribute('text-anchor', 'middle');
                text.setAttribute('dominant-baseline', 'middle');
                text.setAttribute('font-size', '10');
                text.setAttribute('fill', 'black');
                text.textContent = budgetRemaining.toFixed(1);
                svg.appendChild(text);

                // Update value display
                valueElement.textContent = `${year}: ${budgetRemaining.toFixed(1)} tons`;
            });
        }

        // Initialize when the page loads
        window.onload = function() {
            // Set initial slider values
            document.getElementById('value-population').textContent = document.getElementById('slider-population').value;
            document.getElementById('value-annual-emissions').textContent = document.getElementById('slider-annual-emissions').value;
            document.getElementById('value-reduction-time').textContent = document.getElementById('slider-reduction-time').value;
            document.getElementById('value-annual-budget').textContent = document.getElementById('slider-annual-budget').value;
            document.getElementById('value-start-year').textContent = document.getElementById('slider-start-year').value;
            document.getElementById('value-compensation-cost').textContent = document.getElementById('slider-compensation-cost').value;
            document.getElementById('value-fractional-compensation').textContent = document.getElementById('slider-fractional-compensation').value;
            document.getElementById('value-constant-compensation').textContent = document.getElementById('slider-constant-compensation').value;

            // Initialize p5.js
            new p5();
        };
    </script>
</body>
</html>
