    <script>
        // Initialize Three.js scene
        const container = document.getElementById('canvas-container');
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf0f0f0);

        // Camera setup
        const camera = new THREE.PerspectiveCamera(75, container.clientWidth / container.clientHeight, 0.1, 1000);
        camera.position.set(0, 0, 5);

        // Renderer setup
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(container.clientWidth, container.clientHeight);
        container.appendChild(renderer.domElement);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight.position.set(1, 1, 1);
        scene.add(directionalLight);

        // Controls
        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Polyhedron definitions
        const polyhedrons = {
            solid1: {
                name: "Hexahedron",
                unfoldedColor: new THREE.Color(0xF5E0E0),
                foldedColors: [
                    new THREE.Color(0xE47A55), // orange
                    new THREE.Color(0xA4C6E3), // light blue
                    new THREE.Color(0xF4E0E1), // pink
                    new THREE.Color(0xF3C76E), // yellow
                    new THREE.Color(0x8DD3C7), // teal
                    new THREE.Color(0xBEBADA)  // lavender
                ],
                // Define the net geometry (vertices in 2D space)
                vertices: [
                    new THREE.Vector3(0, 1, 0),    // 0
                    new THREE.Vector3(0, -1, 0),   // 1
                    new THREE.Vector3(-1, 0, 0),   // 2
