                    { vertices: [2, 3, 9, 8], colorIndex: 3, hinge: [2, 3], parent: 0, angle: Math.PI/2 }, // right
                    { vertices: [3, 4, 10, 9], colorIndex: 4, hinge: [3, 4], parent: 0, angle: Math.PI/2 }, // back 1
                    { vertices: [4, 5, 11, 10], colorIndex: 5, hinge: [4, 5], parent: 0, angle: Math.PI/2 }, // back 2
                    { vertices: [5, 0, 6, 11], colorIndex: 1, hinge: [5, 0], parent: 0, angle: Math.PI/2 }, // left
                    { vertices: [6, 7, 8, 9, 10, 11], colorIndex: 0, hinge: [6, 7], parent: 1, angle: Math.PI/2 }  // top
                ]
            }
        };

        // Current polyhedron and fold state
        let currentSolid = 'solid1';
        let foldAmount = 0;
        let polyhedronGroup = null;

        // Create a polyhedron based on the current solid and fold amount
        function createPolyhedron(solidName, t) {
            const solid = polyhedrons[solidName];
            const group = new THREE.Group();

            // Create faces and their pivot points
            const faceObjects = [];

            solid.faces.forEach((faceDef, i) => {
                // Create geometry for the face
                const vertices = faceDef.vertices.map(idx => solid.vertices[idx]);
                const shape = new THREE.Shape();
                shape.moveTo(vertices[0].x, vertices[0].y);
                for (let j = 1; j < vertices.length; j++) {
                    shape.lineTo(vertices[j].x, vertices[j].y);
                }
                shape.lineTo(vertices[0].x, vertices[0].y);

                const geometry = new THREE.ShapeGeometry(shape);

                // Create material with interpolated color
                const color = solid.unfoldedColor.clone();
                if (solid.foldedColors[faceDef.colorIndex]) {
                    color.lerp(solid.foldedColors[faceDef.colorIndex], t);
                }

                const material = new THREE.MeshStandardMaterial({
                    color: color,
                    side: THREE.DoubleSide,
                    flatShading: true
                });

                // Create mesh
                const mesh = new THREE.Mesh(geometry, material);

                if (faceDef.parent === null) {
                    // Base face - just add to group
                    group.add(mesh);
                    faceObjects[i] = { mesh, pivot: null };
                } else {
                    // Create pivot for this face
                    const parentObj = faceObjects[faceDef.parent];
                    const hingeEdge = faceDef.hinge;
                    const v1 = solid.vertices[hingeEdge[0]];
                    const v2 = solid.vertices[hingeEdge[1]];

                    // Pivot position is at the midpoint of the hinge edge
                    const pivotPos = new THREE.Vector3(
                        (v1.x + v2.x) / 2,
                        (v1.y + v2.y) / 2,
                        0
                    );

                    // Create pivot and position it
                    const pivot = new THREE.Group();
                    pivot.position.copy(pivotPos);

                    // Position mesh relative to pivot
                    mesh.position.sub(pivotPos);

                    // Add mesh to pivot, then pivot to parent
                    pivot.add(mesh);
                    parentObj.mesh.add(pivot);

                    // Set pivot rotation based on fold amount
                    const axis = new THREE.Vector3().subVectors(v2, v1).normalize();
                    const angle = t * faceDef.angle;

                    // For simplicity, we'll just rotate around Z axis
                    // In a more complete solution, we'd implement proper 3D rotation around the hinge edge
                    pivot.rotation.z = angle;

                    faceObjects[i] = { mesh, pivot };
                }
            });

            return group;
        }

        // Update the current polyhedron based on fold amount
        function updatePolyhedron() {
            if (polyhedronGroup) {
                scene.remove(polyhedronGroup);
            }

            polyhedronGroup = createPolyhedron(currentSolid, foldAmount);
            scene.add(polyhedronGroup);

            // Reset camera position based on the polyhedron size
            const bbox = new THREE.Box3().setFromObject(polyhedronGroup);
            const center = new THREE.Vector3();
            bbox.getCenter(center);
            const size = bbox.getSize(new THREE.Vector3()).length();

            camera.position.copy(center);
            camera.position.z += size * 1.5;
            controls.target.copy(center);
            controls.update();
        }

        // Handle window resize
        function onWindowResize() {
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        }

        window.addEventListener('resize', onWindowResize);

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        animate();

        // Initialize with first solid
        updatePolyhedron();

        // Event listeners for UI controls

        // Solid selector buttons
        document.querySelectorAll('.solid-btn').forEach(btn => {
            btn.addEventListener('click', function() {
                document.querySelectorAll('.solid-btn').forEach(b => b.classList.remove('active'));
                this.classList.add('active');

                currentSolid = 'solid' + this.textContent;
                updatePolyhedron();
            });
        });

        // Fold slider
        const sliderFold = document.getElementById('slider-fold');
        sliderFold.addEventListener('input', function() {
            foldAmount = parseFloat(this.value);
            updatePolyhedron();
        });

        // Reset view button
        document.getElementById('btn-reset-view').addEventListener('click', function() {
            if (polyhedronGroup) {
                const bbox = new THREE.Box3().setFromObject(polyhedronGroup);
                const center = new THREE.Vector3();
                bbox.getCenter(center);
                const size = bbox.getSize(new THREE.Vector3()).length();

                camera.position.copy(center);
                camera.position.z += size * 1.5;
                controls.target.copy(center);
                controls.update();
            }
        });
    </script>
</body>
</html>
