    <script>
        let p5Instance;
        let alpha13 = 0.6;
        let alpha23 = 3.93;

        // Color gradient stops
        const colorStops = [
            { pos: 0.0, color: [44, 123, 182] },    // #2c7bb6
            { pos: 0.25, color: [171, 217, 233] },  // #abd9e9
            { pos: 0.5, color: [255, 255, 191] },   // #ffffbf
            { pos: 0.75, color: [253, 174, 97] },   // #fdae61
            { pos: 1.0, color: [215, 25, 28] }      // #d7191c
        ];

        // Initialize sliders and button
        document.addEventListener('DOMContentLoaded', function() {
            const sliderAC = document.getElementById('slider-ac');
            const sliderBC = document.getElementById('slider-bc');
            const valueAC = document.getElementById('value-ac');
            const valueBC = document.getElementById('value-bc');
            const btnReset = document.getElementById('btn-reset');

            // Update display values when sliders change
            sliderAC.addEventListener('input', function() {
                alpha13 = parseFloat(this.value);
                valueAC.textContent = alpha13.toFixed(2);
                if (p5Instance) p5Instance.redraw();
            });

            sliderBC.addEventListener('input', function() {
                alpha23 = parseFloat(this.value);
                valueBC.textContent = alpha23.toFixed(2);
                if (p5Instance) p5Instance.redraw();
            });

            // Reset button handler
            btnReset.addEventListener('click', function() {
                sliderAC.value = 0.6;
                sliderBC.value = 3.93;
                alpha13 = 0.6;
                alpha23 = 3.93;
                valueAC.textContent = '0.60';
                valueBC.textContent = '3.93';
                if (p5Instance) p5Instance.redraw();
            });

            // Initialize p5.js sketch
            const sketch = function(p) {
                let canvasWidth = 600;
                let canvasHeight = 550;
                let padding = 50;
                let triangleSize;
                let triangleHeight;
                let ax, ay, bx, by, cx, cy;

                p.setup = function() {
                    const canvas = p.createCanvas(canvasWidth, canvasHeight);
                    canvas.parent('ternary-plot-canvas');
                    p.noLoop(); // We'll manually trigger redraws

                    // Calculate triangle dimensions
                    triangleSize = p.min(canvasWidth - 2*padding, (canvasHeight - 2*padding) / p.sin(p.PI/3));
                    triangleHeight = triangleSize * p.sin(p.PI/3);

                    // Calculate vertex positions
                    ax = padding;
                    ay = canvasHeight - padding;
                    bx = ax + triangleSize;
                    by = ay;
                    cx = ax + triangleSize/2;
                    cy = ay - triangleHeight;
                };

                p.draw = function() {
                    p.background(255);
                    p.stroke(0);
                    p.strokeWeight(1);

                    // Draw main triangle
                    p.triangle(ax, ay, bx, by, cx, cy);

                    // Draw grid and calculate colors
                    const divisions = 10;
                    const smallTriangleSize = triangleSize / divisions;
                    const smallTriangleHeight = smallTriangleSize * p.sin(p.PI/3);

                    for (let i = 0; i < divisions; i++) {
                        for (let j = 0; j < divisions - i; j++) {
                            // Draw lower triangle (pointing down)
                            drawSmallTriangle(i, j, false, divisions);

                            // Draw upper triangle (pointing up)
                            if (j < divisions - i - 1) {
                                drawSmallTriangle(i, j, true, divisions);
                            }
                        }
                    }

                    // Draw labels
                    p.textSize(16);
                    p.textAlign(p.CENTER, p.CENTER);
                    p.fill(0);
                    p.text('A', ax - 20, ay);
                    p.text('B', bx + 20, by);
                    p.text('C', cx, cy - 20);

                    // Draw tick marks and labels
                    p.textSize(12);
                    for (let t = 0; t <= 10; t++) {
                        const val = t / 10;
                        const x = ax + t * smallTriangleSize / 2;
                        const y = ay - t * smallTriangleHeight;

                        // A-C axis (left)
                        p.line(x, y, x - 5, y);
                        p.text(val.toFixed(1), x - 10, y);

                        // B-C axis (right)
                        const x2 = bx - t * smallTriangleSize / 2;
                        p.line(x2, y, x2 + 5, y);
                        p.text(val.toFixed(1), x2 + 10, y);

                        // A-B axis (bottom)
                        const x3 = ax + t * smallTriangleSize;
                        p.line(x3, ay, x3, ay + 5);
                        p.text(val.toFixed(1), x3, ay + 15);
                    }
                };

                function drawSmallTriangle(i, j, isUpward, divisions) {
                    const smallTriangleSize = triangleSize / divisions;
                    const smallTriangleHeight = smallTriangleSize * p.sin(p.PI/3);

                    // Calculate vertices
                    let x1, y1, x2, y2, x3, y3;

                    if (!isUpward) {
                        // Downward pointing triangle
                        x1 = ax + i * smallTriangleSize + j * smallTriangleSize / 2;
                        y1 = ay - j * smallTriangleHeight;
                        x2 = x1 + smallTriangleSize / 2;
                        y2 = y1 - smallTriangleHeight;
                        x3 = x1 + smallTriangleSize;
                        y3 = y1;
                    } else {
                        // Upward pointing triangle
                        x1 = ax + i * smallTriangleSize + (j + 1) * smallTriangleSize / 2;
                        y1 = ay - (j + 1) * smallTriangleHeight;
                        x2 = x1 + smallTriangleSize / 2;
                        y2 = y1 + smallTriangleHeight;
                        x3 = x1 - smallTriangleSize / 2;
                        y3 = y2;
                    }

                    // Calculate centroid (in Cartesian coordinates)
                    const centroidX = (x1 + x2 + x3) / 3;
                    const centroidY = (y1 + y2 + y3) / 3;

                    // Convert centroid to barycentric coordinates (xA, xB, xC)
                    const xA = barycentricX(centroidX, centroidY, 'A');
                    const xB = barycentricX(centroidX, centroidY, 'B');
                    const xC = 1 - xA - xB;

                    // Calculate vapor phase mole fractions (yA, yB, yC)
                    const alpha1 = alpha13;
                    const alpha2 = alpha23;
                    const alpha3 = 1;

                    const D = alpha1 * xA + alpha2 * xB + alpha3 * xC;
                    const yA = (alpha1 * xA) / D;
                    const yB = (alpha2 * xB) / D;
                    const yC = (alpha3 * xC) / D;

                    // Calculate function value F (sum of squared differences)
                    const F = Math.pow(xA - yA, 2) + Math.pow(xB - yB, 2) + Math.pow(xC - yC, 2);

                    // Normalize F to [0, 1] range (max F is 2.0)
                    const v = Math.min(F / 2.0, 1.0);

                    // Get color from gradient
                    const col = getColorFromGradient(v);

                    // Draw the triangle
                    p.fill(col[0], col[1], col[2]);
                    p.stroke(0);
                    p.strokeWeight(0.5);
                    p.triangle(x1, y1, x2, y2, x3, y3);
                }

                function barycentricX(x, y, component) {
                    // Convert Cartesian coordinates to barycentric coordinates
                    const v0 = [cx - ax, cy - ay];  // Vector from A to C
                    const v1 = [bx - ax, by - ay];  // Vector from A to B
                    const v2 = [x - ax, y - ay];    // Vector from A to point

                    const denom = v0[0] * v1[1] - v1[0] * v0[1];

                    const v = (v2[0] * v1[1] - v1[0] * v2[1]) / denom;
                    const w = (v0[0] * v2[1] - v2[0] * v0[1]) / denom;
                    const u = 1 - v - w;

                    if (component === 'A') return u;
                    if (component === 'B') return v;
                    return w;
                }

                function getColorFromGradient(value) {
                    // Find the two stops the value is between
                    let lowerStop = colorStops[0];
                    let upperStop = colorStops[colorStops.length - 1];

                    for (let i = 0; i < colorStops.length - 1; i++) {
                        if (value >= colorStops[i].pos && value <= colorStops[i+1].pos) {
                            lowerStop = colorStops[i];
                            upperStop = colorStops[i+1];
                            break;
                        }
                    }

                    // Interpolate between the two colors
                    const t = (value - lowerStop.pos) / (upperStop.pos - lowerStop.pos);
                    const r = Math.round(lowerStop.color[0] + t * (upperStop.color[0] - lowerStop.color[0]));
                    const g = Math.round(lowerStop.color[1] + t * (upperStop.color[1] - lowerStop.color[1]));
                    const b = Math.round(lowerStop.color[2] + t * (upperStop.color[2] - lowerStop.color[2]));

                    return [r, g, b];
                }
            };

            // Create p5 instance
            p5Instance = new p5(sketch);
        });
    </script>
