    <script>
        // Initialize the plot when the page loads
        document.addEventListener('DOMContentLoaded', function() {
            // Set up event listeners for all sliders
            document.getElementById('slider-wso').addEventListener('input', updateValues);
            document.getElementById('slider-n').addEventListener('input', updateValues);
            document.getElementById('slider-bx').addEventListener('input', updateValues);
            document.getElementById('slider-by').addEventListener('input', updateValues);
            document.getElementById('slider-bz').addEventListener('input', updateValues);

            // Initialize the plot
            updatePlot();
        });

        // Update the displayed values and the plot
        function updateValues() {
            document.getElementById('value-wso').textContent = parseFloat(document.getElementById('slider-wso').value).toFixed(1);
            document.getElementById('value-n').textContent = document.getElementById('slider-n').value;
            document.getElementById('value-bx').textContent = parseFloat(document.getElementById('slider-bx').value).toFixed(1);
            document.getElementById('value-by').textContent = parseFloat(document.getElementById('slider-by').value).toFixed(1);
            document.getElementById('value-bz').textContent = parseFloat(document.getElementById('slider-bz').value).toFixed(1);

            updatePlot();
        }

        // Calculate and update the plot
        function updatePlot() {
            // Get current parameter values
            const n = parseInt(document.getElementById('slider-n').value);
            const wso = parseFloat(document.getElementById('slider-wso').value);
            const BX = parseFloat(document.getElementById('slider-bx').value);
            const BY = parseFloat(document.getElementById('slider-by').value);
            const BZ = parseFloat(document.getElementById('slider-bz').value);

            // Generate k values from -5 to 5 in steps of 0.1
            const k = [];
            for (let i = -50; i <= 50; i++) {
                k.push(i * 0.1);
            }

            // Calculate energy bands
            const E_plus = [];
            const E_minus = [];

            for (let i = 0; i < k.length; i++) {
                const ki = k[i];
                const sqrt_term = Math.sqrt(Math.pow(BZ + wso * ki, 2) + Math.pow(BX, 2) + Math.pow(BY, 2));
                const E_plus_val = (2 * n + 1) + Math.pow(ki, 2) + sqrt_term;
                const E_minus_val = (2 * n + 1) + Math.pow(ki, 2) - sqrt_term;

                E_plus.push(E_plus_val);
                E_minus.push(E_minus_val);
            }

            // Prepare plot data
            let plotData = [];

            if (wso === 0 && BX === 0 && BY === 0 && BZ === 0) {
                // Only one trace when all parameters are zero
                plotData.push({
                    x: k,
                    y: E_plus,
                    mode: 'lines',
                    line: {
                        color: 'red',
                        width: 2
                    },
                    name: 'E'
                });
            } else {
                // Two traces otherwise
                plotData.push({
                    x: k,
                    y: E_plus,
                    mode: 'lines',
                    line: {
                        color: 'red',
                        width: 2
                    },
                    name: 'E+'
                });

                plotData.push({
                    x: k,
                    y: E_minus,
                    mode: 'lines',
                    line: {
                        color: 'red',
                        dash: 'dash',
                        width: 2
                    },
                    name: 'E-'
                });
            }

            // Create dynamic title
            let titleParts = [`n = ${n}`];
            if (wso !== 0) titleParts.push(`wso = ${wso.toFixed(1)}`);
            if (BX !== 0) titleParts.push(`BX = ${BX.toFixed(1)}`);
            if (BY !== 0) titleParts.push(`BY = ${BY.toFixed(1)}`);
            if (BZ !== 0) titleParts.push(`BZ = ${BZ.toFixed(1)}`);

            const plotTitle = titleParts.join(', ');

            // Define plot layout
            const layout = {
                title: plotTitle,
                xaxis: {
                    title: 'normalized k length (lwky)',
                    range: [-5, 5],
                    zeroline: true
                },
                yaxis: {
                    title: '2 E / &#x210F;&omega;',
                    range: [0, 40],
                    zeroline: true
                },
                margin: {
                    l: 60,
                    r: 40,
                    b: 50,
                    t: 60,
                    pad: 4
                }
            };

            // Update or create the plot
            Plotly.react('plot-container', plotData, layout);
        }
    </script>
</body>
</html>
