                sketches.push(sketch);
            }

            // Trigger initial update
            update();
        });

        function setupSliders() {
            // Precipitation sliders
            for (let i = 1; i <= 3; i++) {
                const slider = document.getElementById(`slider-precip-${i}`);
                const valueDisplay = document.getElementById(`value-precip-${i}`);

