                parseInt(document.getElementById('slider-uh-2').value),
                parseInt(document.getElementById('slider-uh-3').value),
                parseInt(document.getElementById('slider-uh-4').value)
            ];

            // Calculate component hydrographs
            const C1 = U.map(val => P[0] * val);
            const C2 = U.map(val => P[1] * val);
            const C3 = U.map(val => P[2] * val);

            // Calculate composite hydrograph
            const Q = [
                P[0] * U[0],
                P[0] * U[1] + P[1] * U[0],
                P[0] * U[2] + P[1] * U[1] + P[2] * U[0],
                P[0] * U[3] + P[1] * U[2] + P[2] * U[1],
                P[1] * U[3] + P[2] * U[2],
                P[2] * U[3]
            ];

            // Update all sketches with new data
            sketches[0].setData(P, 'P (in)', 10, 't (hr)', 5);
            sketches[1].setData(U, 'Q (cfs/in)', 10, 't (hr)', 5);
            sketches[2].setData(C1, 'Q (cfs)', 100, 't (hr)', 8, 0);
            sketches[3].setData(C2, 'Q (cfs)', 100, 't (hr)', 8, 1);
            sketches[4].setData(C3, 'Q (cfs)', 100, 't (hr)', 8, 2);
            sketches[5].setData(Q, 'Q (cfs)', 100, 't (hr)', 8, 0);
        }

        function createSketch(plotNumber) {
            return function(p) {
                let data = [];
                let yLabel = '';
                let yMax = 10;
                let xLabel = '';
                let xMax = 5;
                let timeOffset = 0;

                p.setData = function(newData, newYLabel, newYMax, newXLabel, newXMax, newTimeOffset = 0) {
                    data = newData;
                    yLabel = newYLabel;
                    yMax = newYMax;
                    xLabel = newXLabel;
                    xMax = newXMax;
                    timeOffset = newTimeOffset;
                    p.redraw();
                };

                p.setup = function() {
                    const container = p.canvas.parentElement;
                    const width = container.clientWidth;
                    const height = container.clientHeight;

                    p.createCanvas(width, height).parent(container);
                    p.noLoop();
                };

                p.draw = function() {
                    p.background(255);

                    if (data.length === 0) return;

                    const margin = 40;
                    const plotWidth = p.width - margin * 2;
                    const plotHeight = p.height - margin * 2;

                    // Draw axes
                    p.stroke(0);
                    p.strokeWeight(1);
                    p.line(margin, p.height - margin, p.width - margin, p.height - margin); // x-axis
                    p.line(margin, p.height - margin, margin, margin); // y-axis

                    // Draw tick marks and labels on x-axis
                    const xTicks = Math.min(xMax, 10);
                    for (let i = 0; i <= xTicks; i++) {
                        const x = margin + (i / xTicks) * plotWidth;
                        p.line(x, p.height - margin, x, p.height - margin + 5);
                        p.textAlign(p.CENTER, p.TOP);
                        p.noStroke();
                        p.text(i, x, p.height - margin + 10);
                    }

                    // Draw x-axis label
                    p.textAlign(p.CENTER, p.TOP);
                    p.text(xLabel, p.width / 2, p.height - margin / 2);

                    // Draw tick marks and labels on y-axis
                    const yTicks = Math.min(yMax, 10);
                    for (let i = 0; i <= yTicks; i++) {
                        const y = p.height - margin - (i / yTicks) * plotHeight;
                        p.line(margin - 5, y, margin, y);
                        p.textAlign(p.RIGHT, p.CENTER);
                        p.noStroke();
                        p.text(i * (yMax / yTicks), margin - 10, y);
                    }

                    // Draw y-axis label
                    p.push();
                    p.translate(margin / 2, p.height / 2);
                    p.rotate(-p.HALF_PI);
                    p.textAlign(p.CENTER, p.CENTER);
                    p.text(yLabel, 0, 0);
                    p.pop();

                    // Draw the step chart
                    p.stroke('#2e86c1');
                    p.strokeWeight(2);
                    p.fill('#d4e6f1');

                    p.beginShape();

                    // Start at bottom left
                    p.vertex(margin, p.height - margin);

                    for (let i = 0; i < data.length; i++) {
                        const x1 = margin + ((i + timeOffset) / xMax) * plotWidth;
                        const x2 = margin + ((i + 1 + timeOffset) / xMax) * plotWidth;
                        const y = p.height - margin - (data[i] / yMax) * plotHeight;

                        // Horizontal line
                        p.vertex(x1, y);
                        p.vertex(x2, y);
                    }

                    // End at bottom right
                    const lastX = margin + ((data.length + timeOffset) / xMax) * plotWidth;
                    p.vertex(lastX, p.height - margin);

                    p.endShape(p.CLOSE);

                    // Draw vertical lines
                    for (let i = 0; i < data.length; i++) {
                        const x = margin + ((i + timeOffset) / xMax) * plotWidth;
                        const y = p.height - margin - (data[i] / yMax) * plotHeight;

                        p.line(x, p.height - margin, x, y);
                    }
                };

                p.windowResized = function() {
                    const container = p.canvas.parentElement;
                    const width = container.clientWidth;
                    const height = container.clientHeight;

                    p.resizeCanvas(width, height);
                    p.redraw();
                };
            };
        }
    </script>
</body>
