            }

            function drawBestResponses() {
                // Calculate indifference points
                const den1 = gameState.a11 - gameState.a12 - gameState.a21 + gameState.a22;
                const y1_indiff = (gameState.a22 - gameState.a12) / den1;

                const den2 = gameState.b11 - gameState.b12 - gameState.b21 + gameState.b22;
                const x1_indiff = (gameState.b22 - gameState.b21) / den2;

                // Player 1's best response (blue)
                p.stroke(0, 0, 255);
                p.strokeWeight(2);

                if (Math.abs(den1) < 1e-6) {
                    // Dominant strategy case for Player 1
                    const pref = gameState.a12 - gameState.a22;
                    if (pref > 0) {
                        // Always choose strategy 1 (x1=1)
                        drawVerticalLine(1, 0, 1, 'blue');
                    } else {
                        // Always choose strategy 2 (x1=0)
                        drawVerticalLine(0, 0, 1, 'blue');
                    }
                } else if (y1_indiff >= 0 && y1_indiff <= 1) {
                    // C-shape case
                    drawHorizontalLine(0, 1, y1_indiff, 'blue');

                    if (den1 > 0) {
                        drawVerticalLine(0, 0, y1_indiff, 'blue');
                        drawVerticalLine(1, y1_indiff, 1, 'blue');
                    } else {
                        drawVerticalLine(0, y1_indiff, 1, 'blue');
                        drawVerticalLine(1, 0, y1_indiff, 'blue');
                    }
                } else {
                    // Dominant strategy in mixed extension
                    const midY = 0.5;
                    const payoff1 = midY * gameState.a11 + (1 - midY) * gameState.a12;
                    const payoff2 = midY * gameState.a21 + (1 - midY) * gameState.a22;

                    if (payoff1 > payoff2) {
                        drawVerticalLine(1, 0, 1, 'blue');
                    } else {
                        drawVerticalLine(0, 0, 1, 'blue');
                    }
                }

                // Player 2's best response (green)
                p.stroke(0, 128, 0);
                p.strokeWeight(2);

                if (Math.abs(den2) < 1e-6) {
                    // Dominant strategy case for Player 2
                    const pref = gameState.b21 - gameState.b22;
                    if (pref > 0) {
                        // Always choose strategy 1 (y1=1)
                        drawHorizontalLine(0, 1, 1, 'green');
                    } else {
                        // Always choose strategy 2 (y1=0)
                        drawHorizontalLine(0, 1, 0, 'green');
                    }
                } else if (x1_indiff >= 0 && x1_indiff <= 1) {
                    // C-shape case
                    drawVerticalLine(x1_indiff, 0, 1, 'green');

                    if (den2 > 0) {
                        drawHorizontalLine(0, x1_indiff, 0, 'green');
                        drawHorizontalLine(x1_indiff, 1, 1, 'green');
                    } else {
                        drawHorizontalLine(0, x1_indiff, 1, 'green');
                        drawHorizontalLine(x1_indiff, 1, 0, 'green');
                    }
                } else {
                    // Dominant strategy in mixed extension
                    const midX = 0.5;
                    const payoff1 = midX * gameState.b11 + (1 - midX) * gameState.b21;
                    const payoff2 = midX * gameState.b12 + (1 - midX) * gameState.b22;

                    if (payoff1 > payoff2) {
                        drawHorizontalLine(0, 1, 1, 'green');
                    } else {
                        drawHorizontalLine(0, 1, 0, 'green');
                    }
                }

                // Find and draw Nash equilibria (intersections)
                findNashEquilibria(x1_indiff, y1_indiff, den1, den2);
                drawEquilibria();
            }

            function drawVerticalLine(x, yStart, yEnd, color) {
                const xPos = margin + x * plotSize;
                const y1 = canvasSize - margin - yStart * plotSize;
                const y2 = canvasSize - margin - yEnd * plotSize;

                if (color === 'blue') p.stroke(0, 0, 255);
                else if (color === 'green') p.stroke(0, 128, 0);
                else p.stroke(0);

                p.line(xPos, y1, xPos, y2);
            }

            function drawHorizontalLine(xStart, xEnd, y, color) {
                const x1 = margin + xStart * plotSize;
                const x2 = margin + xEnd * plotSize;
                const yPos = canvasSize - margin - y * plotSize;

                if (color === 'blue') p.stroke(0, 0, 255);
                else if (color === 'green') p.stroke(0, 128, 0);
                else p.stroke(0);

                p.line(x1, yPos, x2, yPos);
            }

            function findNashEquilibria(x1_indiff, y1_indiff, den1, den2) {
                gameState.equilibria = [];

                // Calculate Player 1's best response conditions
                let p1_br = [];
                if (Math.abs(den1) < 1e-6) {
                    // Dominant strategy
                    const pref = gameState.a12 - gameState.a22;
                    if (pref > 0) {
                        p1_br.push({x: 1, yMin: 0, yMax: 1});
                    } else {
                        p1_br.push({x: 0, yMin: 0, yMax: 1});
                    }
                } else if (y1_indiff >= 0 && y1_indiff <= 1) {
                    // C-shape
                    p1_br.push({x: null, y: y1_indiff, xMin: 0, xMax: 1}); // Horizontal segment

                    if (den1 > 0) {
                        p1_br.push({x: 0, yMin: 0, yMax: y1_indiff});
                        p1_br.push({x: 1, yMin: y1_indiff, yMax: 1});
                    } else {
                        p1_br.push({x: 0, yMin: y1_indiff, yMax: 1});
                        p1_br.push({x: 1, yMin: 0, yMax: y1_indiff});
                    }
                } else {
                    // Dominant in mixed extension
                    const midY = 0.5;
                    const payoff1 = midY * gameState.a11 + (1 - midY) * gameState.a12;
                    const payoff2 = midY * gameState.a21 + (1 - midY) * gameState.a22;

                    if (payoff1 > payoff2) {
                        p1_br.push({x: 1, yMin: 0, yMax: 1});
                    } else {
                        p1_br.push({x: 0, yMin: 0, yMax: 1});
                    }
                }

                // Calculate Player 2's best response conditions
                let p2_br = [];
                if (Math.abs(den2) < 1e-6) {
                    // Dominant strategy
                    const pref = gameState.b21 - gameState.b22;
                    if (pref > 0) {
                        p2_br.push({y: 1, xMin: 0, xMax: 1});
                    } else {
                        p2_br.push({y: 0, xMin: 0, xMax: 1});
                    }
                } else if (x1_indiff >= 0 && x1_indiff <= 1) {
                    // C-shape
                    p2_br.push({y: null, x: x1_indiff, yMin: 0, yMax: 1}); // Vertical segment

                    if (den2 > 0) {
                        p2_br.push({y: 0, xMin: 0, xMax: x1_indiff});
                        p2_br.push({y: 1, xMin: x1_indiff, xMax: 1});
                    } else {
                        p2_br.push({y: 1, xMin: 0, xMax: x1_indiff});
                        p2_br.push({y: 0, xMin: x1_indiff, xMax: 1});
                    }
                } else {
                    // Dominant in mixed extension
                    const midX = 0.5;
                    const payoff1 = midX * gameState.b11 + (1 - midX) * gameState.b21;
                    const payoff2 = midX * gameState.b12 + (1 - midX) * gameState.b22;

                    if (payoff1 > payoff2) {
                        p2_br.push({y: 1, xMin: 0, xMax: 1});
                    } else {
                        p2_br.push({y: 0, xMin: 0, xMax: 1});
                    }
                }

                // Find intersections between p1_br and p2_br
                for (const p1 of p1_br) {
                    for (const p2 of p2_br) {
                        if (p1.x !== null && p2.y !== null) {
                            // Vertical line (p1) meets horizontal line (p2)
                            if (p1.x >= p2.xMin && p1.x <= p2.xMax && 
                                p2.y >= p1.yMin && p2.y <= p1.yMax) {
                                gameState.equilibria.push({x: p1.x, y: p2.y});
                            }
                        } else if (p1.x !== null && p2.x !== null) {
                            // Two vertical lines
                            if (Math.abs(p1.x - p2.x) < 1e-6) {
                                const yMin = Math.max(p1.yMin, p2.yMin);
                                const yMax = Math.min(p1.yMax, p2.yMax);
                                if (yMin <= yMax) {
                                    gameState.equilibria.push({x: p1.x, y: yMin, isLine: true, yEnd: yMax});
                                }
                            }
                        } else if (p1.y !== null && p2.y !== null) {
                            // Two horizontal lines
                            if (Math.abs(p1.y - p2.y) < 1e-6) {
                                const xMin = Math.max(p1.xMin, p2.xMin);
                                const xMax = Math.min(p1.xMax, p2.xMax);
                                if (xMin <= xMax) {
                                    gameState.equilibria.push({x: xMin, y: p1.y, isLine: true, xEnd: xMax});
                                }
                            }
                        } else if (p1.x !== null && p2.x === null) {
                            // Vertical line (p1) meets vertical line (p2)
                            if (Math.abs(p1.x - p2.x) < 1e-6) {
                                const yMin = Math.max(p1.yMin, p2.yMin);
                                const yMax = Math.min(p1.yMax, p2.yMax);
                                if (yMin <= yMax) {
                                    gameState.equilibria.push({x: p1.x, y: yMin, isLine: true, yEnd: yMax});
                                }
                            }
                        } else if (p1.y !== null && p2.y === null) {
                            // Horizontal line (p1) meets vertical line (p2)
                            if (p2.x >= p1.xMin && p2.x <= p1.xMax && 
                                p1.y >= p2.yMin && p1.y <= p2.yMax) {
                                gameState.equilibria.push({x: p2.x, y: p1.y});
                            }
                        }
                    }
                }

                // Update equilibria display
                updateEquilibriaDisplay();
            }

            function drawEquilibria() {
                if (gameState.equilibria.length === 0) return;

                p.stroke(255, 0, 0);
                p.fill(255, 0, 0);
                p.strokeWeight(4);

                for (const eq of gameState.equilibria) {
                    if (eq.isLine) {
                        const x1 = margin + eq.x * plotSize;
                        const y1 = canvasSize - margin - eq.y * plotSize;
                        const x2 = margin + (eq.xEnd || eq.x) * plotSize;
                        const y2 = canvasSize - margin - (eq.yEnd || eq.y) * plotSize;
                        p.line(x1, y1, x2, y2);

                        // Draw endpoints
                        p.ellipse(x1, y1, 8, 8);
                        p.ellipse(x2, y2, 8, 8);
                    } else {
                        const x = margin + eq.x * plotSize;
                        const y = canvasSize - margin - eq.y * plotSize;
                        p.ellipse(x, y, 8, 8);
                    }
                }
            }
        };

        // Helper function to convert decimal to fraction
        function toFraction(decimal, tolerance = 1e-6) {
            if (Math.abs(decimal - Math.round(decimal)) < tolerance) {
                return Math.round(decimal).toString();
            }

            let bestNumerator = 1;
            let bestDenominator = 1;
            let bestError = Math.abs(decimal - bestNumerator / bestDenominator);

            for (let denominator = 1; denominator <= 10; denominator++) {
                const numerator = Math.round(decimal * denominator);
                const error = Math.abs(decimal - numerator / denominator);

                if (error < bestError) {
                    bestError = error;
                    bestNumerator = numerator;
                    bestDenominator = denominator;
                }
            }

            if (bestError < tolerance) {
                if (bestDenominator === 1) {
                    return bestNumerator.toString();
                } else {
                    return `${bestNumerator}/${bestDenominator}`;
                }
            } else {
                return decimal.toFixed(2);
            }
        }

        function updateEquilibriaDisplay() {
            const equilibriaDiv = document.getElementById('equilibria-display');

            if (gameState.equilibria.length === 0) {
                equilibriaDiv.innerHTML = '\\( \\emptyset \\)';
                MathJax.typeset();
                return;
            }

            let points = [];

            for (const eq of gameState.equilibria) {
                if (eq.isLine) {
                    const x1 = toFraction(eq.x);
                    const y1 = toFraction(eq.y);
                    const x2 = toFraction(eq.xEnd || eq.x);
                    const y2 = toFraction(eq.yEnd || eq.y);

                    if (x1 === x2) {
                        // Vertical line
                        points.push(`(\\text{all points on }x=${x1} \\text{ from }y=${y1} \\text{ to }y=${y2})`);
                    } else {
                        // Horizontal line
                        points.push(`(\\text{all points on }y=${y1} \\text{ from }x=${x1} \\text{ to }x=${x2})`);
                    }
                } else {
                    const x = toFraction(eq.x);
                    const y = toFraction(eq.y);
                    points.push(`(${x}, ${y})`);
                }
            }

            equilibriaDiv.innerHTML = `\\( \\{ ${points.join(', ')} \\} \\)`;
            MathJax.typeset();
        }

        // Initialize p5.js sketch
        new p5(sketch);

        // Update game state and visualization when sliders change
        function updateGameState() {
            // Update matrix A values
            gameState.a11 = parseInt(document.getElementById('slider-a11').value);
            gameState.a12 = parseInt(document.getElementById('slider-a12').value);
            gameState.a21 = parseInt(document.getElementById('slider-a21').value);
            gameState.a22 = parseInt(document.getElementById('slider-a22').value);

            // Update matrix B values
            gameState.b11 = parseInt(document.getElementById('slider-b11').value);
            gameState.b12 = parseInt(document.getElementById('slider-b12').value);
            gameState.b21 = parseInt(document.getElementById('slider-b21').value);
            gameState.b22 = parseInt(document.getElementById('slider-b22').value);

            // Update payoff table
            document.getElementById('cell-11').textContent = `${gameState.a11}, ${gameState.b11}`;
            document.getElementById('cell-12').textContent = `${gameState.a12}, ${gameState.b12}`;
            document.getElementById('cell-21').textContent = `${gameState.a21}, ${gameState.b21}`;
            document.getElementById('cell-22').textContent = `${gameState.a22}, ${gameState.b22}`;

            // Redraw visualization
            sketch.redraw();
        }

        // Set up event listeners for all sliders
        const sliders = [
            'slider-a11', 'slider-a12', 'slider-a21', 'slider-a22',
            'slider-b11', 'slider-b12', 'slider-b21', 'slider-b22'
        ];

        sliders.forEach(sliderId => {
            const slider = document.getElementById(sliderId);
            const valueSpan = document.getElementById(`value-${sliderId}`);

            slider.addEventListener('input', function() {
                valueSpan.textContent = this.value;
                updateGameState();
            });

            // Set initial values
            valueSpan.textContent = slider.value;
        });

        // Set up increment/decrement buttons
        const buttons = [
            'btn-minus-a11', 'btn-plus-a11',
            'btn-minus-a12', 'btn-plus-a12',
            'btn-minus-a21', 'btn-plus-a21',
            'btn-minus-a22', 'btn-plus-a22',
            'btn-minus-b11', 'btn-plus-b11',
            'btn-minus-b12', 'btn-plus-b12',
            'btn-minus-b21', 'btn-plus-b21',
            'btn-minus-b22', 'btn-plus-b22'
        ];

        buttons.forEach(btnId => {
            document.getElementById(btnId).addEventListener('click', function() {
                const parts = btnId.split('-');
                const direction = parts[1];
                const matrix = parts[2][0]; // 'a' or 'b'
                const index = parts[2].substring(1); // '11', '12', etc.
                const sliderId = `slider-${matrix}${index}`;
                const slider = document.getElementById(sliderId);

                if (direction === 'minus') {
                    slider.value = parseInt(slider.value) - 1;
                } else {
                    slider.value = parseInt(slider.value) + 1;
                }

                document.getElementById(`value-${sliderId}`).textContent = slider.value;
                updateGameState();
            });
        });

        // Initial update
        updateGameState();
    </script>
</body>
