    <script>
        let sketch = function(p) {
            // Canvas dimensions
            const canvasWidth = 600;
            const canvasHeight = 500;

            // Coordinate system parameters
            const xMin = -6;
            const xMax = 6;
            const yMin = -5;
            const yMax = 5;
            const scale = 50; // pixels per unit

            // Initial locator positions
            const initialLocators = [
                {x: -4, y: 1},
                {x: -2, y: 1.8},
                {x: 1, y: -1},
                {x: 4, y: -1}
            ];

            // Current state
            let locators = [];
            let cubicCoeffs = {a: 0, b: 0, c: 0, d: 0};
            let quadraticCoeffs = {p: 0, q: 0, r: 0};
            let tValue = 0.5;
            let showFamily = false;
            let draggedLocator = null;

            // Color scheme
            const colors = {
