                document.getElementById('btn-reset').addEventListener('click', function() {
                    resetLocators();
                    tValue = 0.5;
                    document.getElementById('slider-t').value = tValue;
                    document.getElementById('input-t').value = tValue;
                    document.getElementById('checkbox-family').checked = false;
                    showFamily = false;
                    updateCubicCoefficients();
                    updateQuadraticCoefficients();
