                for (let i = 0; i < locators.length; i++) {
                    const screenX = p.map(locators[i].x, xMin, xMax, 0, canvasWidth);
                    const screenY = p.map(locators[i].y, yMax, yMin, 0, canvasHeight);

                    if (p.dist(p.mouseX, p.mouseY, screenX, screenY) < 15) {
                        draggedLocator = i;
                        return;
                    }
                }
            };

            p.mouseDragged = function() {
                if (draggedLocator !== null) {
                    const newX = p.map(p.mouseX, 0, canvasWidth, xMin, xMax);
                    const newY = p.map(p.mouseY, 0, canvasHeight, yMax, yMin);

                    locators[draggedLocator].x = p.constrain(newX, xMin, xMax);
                    locators[draggedLocator].y = p.constrain(newY, yMin, yMax);

                    updateCubicCoefficients();
                    updateQuadraticCoefficients();
                    p.redraw();
                }
            };

            p.mouseReleased = function() {
                draggedLocator = null;
            };

            function resetLocators() {
                locators = [];
                for (let loc of initialLocators) {
                    locators.push({x: loc.x, y: loc.y});
                }
            }

            function updateCubicCoefficients() {
                // Solve the system of equations to find cubic coefficients a, b, c, d
                // where y = ax³ + bx² + cx + d for each locator point

                const x1 = locators[0].x, y1 = locators[0].y;
                const x2 = locators[1].x, y2 = locators[1].y;
                const x3 = locators[2].x, y3 = locators[2].y;
                const x4 = locators[3].x, y4 = locators[3].y;

                // Create the matrix and vector for the system
                const matrix = [
                    [x1*x1*x1, x1*x1, x1, 1],
                    [x2*x2*x2, x2*x2, x2, 1],
                    [x3*x3*x3, x3*x3, x3, 1],
                    [x4*x4*x4, x4*x4, x4, 1]
                ];
                const vector = [y1, y2, y3, y4];

                // Solve the system (using a simple Gaussian elimination implementation)
                const solution = solveSystem(matrix, vector);

                if (solution) {
                    cubicCoeffs.a = solution[0];
                    cubicCoeffs.b = solution[1];
                    cubicCoeffs.c = solution[2];
                    cubicCoeffs.d = solution[3];
                }
            }

            function updateQuadraticCoefficients() {
                // Calculate the quadratic coefficients p, q, r for the osculating quadratic
                // at x = t, where the quadratic matches the cubic's value, first derivative, and second derivative

                const a = cubicCoeffs.a;
                const b = cubicCoeffs.b;
                const c = cubicCoeffs.c;
                const d = cubicCoeffs.d;
                const t = tValue;

                quadraticCoeffs.p = (3 * a * t) + b;
                quadraticCoeffs.q = (-3 * a * t * t) + c;
                quadraticCoeffs.r = (a * t * t * t) + d;
            }

            function solveSystem(matrix, vector) {
                // Simple Gaussian elimination implementation for 4x4 system
                const n = 4;
                let m = matrix.map(row => [...row]);
                let v = [...vector];

                // Forward elimination
                for (let i = 0; i < n; i++) {
                    // Find the row with maximum element in current column
                    let maxRow = i;
                    for (let j = i + 1; j < n; j++) {
                        if (Math.abs(m[j][i]) > Math.abs(m[maxRow][i])) {
                            maxRow = j;
                        }
                    }

                    // Swap rows
                    [m[i], m[maxRow]] = [m[maxRow], m[i]];
                    [v[i], v[maxRow]] = [v[maxRow], v[i]];

                    // Make all rows below this one 0 in current column
                    for (let j = i + 1; j < n; j++) {
                        const factor = m[j][i] / m[i][i];
                        for (let k = i; k < n; k++) {
                            m[j][k] -= m[i][k] * factor;
                        }
                        v[j] -= v[i] * factor;
                    }
                }

                // Back substitution
                const solution = new Array(n);
                for (let i = n - 1; i >= 0; i--) {
                    solution[i] = v[i] / m[i][i];
                    for (let j = 0; j < i; j++) {
                        v[j] -= m[j][i] * solution[i];
                    }
                }

                return solution;
            }

            function drawCoordinateSystem() {
                p.push();
                p.stroke(200);
                p.strokeWeight(1);

                // Draw grid lines
                for (let x = xMin; x <= xMax; x++) {
                    if (x !== 0) {
                        const screenX = p.map(x, xMin, xMax, 0, canvasWidth);
                        p.line(screenX, 0, screenX, canvasHeight);
                    }
                }

                for (let y = yMin; y <= yMax; y++) {
                    if (y !== 0) {
                        const screenY = p.map(y, yMax, yMin, 0, canvasHeight);
                        p.line(0, screenY, canvasWidth, screenY);
                    }
                }

                // Draw axes
                p.stroke(0);
                p.strokeWeight(2);

                const xAxisY = p.map(0, yMax, yMin, 0, canvasHeight);
                p.line(0, xAxisY, canvasWidth, xAxisY);

                const yAxisX = p.map(0, xMin, xMax, 0, canvasWidth);
                p.line(yAxisX, 0, yAxisX, canvasHeight);

                // Draw ticks and labels
                p.textSize(12);
                p.fill(0);
                p.noStroke();
                p.textAlign(p.CENTER, p.TOP);

                for (let x = xMin; x <= xMax; x++) {
                    if (x !== 0) {
                        const screenX = p.map(x, xMin, xMax, 0, canvasWidth);
                        p.text(x, screenX, xAxisY + 5);
                    }
                }

                p.textAlign(p.RIGHT, p.CENTER);
                for (let y = yMin; y <= yMax; y++) {
                    if (y !== 0) {
                        const screenY = p.map(y, yMax, yMin, 0, canvasHeight);
                        p.text(y, yAxisX - 5, screenY);
                    }
                }

                // Draw origin label
                p.textAlign(p.RIGHT, p.TOP);
                p.text("0", yAxisX - 5, xAxisY + 5);

                p.pop();
            }

            function drawCubicCurve() {
                p.push();
                p.stroke(colors.cubic);
                p.strokeWeight(3);
                p.noFill();

                p.beginShape();
                for (let x = xMin; x <= xMax; x += 0.1) {
                    const y = cubicCoeffs.a * x*x*x + cubicCoeffs.b * x*x + cubicCoeffs.c * x + cubicCoeffs.d;
                    const screenX = p.map(x, xMin, xMax, 0, canvasWidth);
                    const screenY = p.map(y, yMax, yMin, 0, canvasHeight);
                    p.vertex(screenX, screenY);
                }
                p.endShape();

                p.pop();
            }

            function drawQuadraticCurve() {
                p.push();
                p.stroke(colors.quadratic);
                p.strokeWeight(3);
                p.noFill();

                p.beginShape();
                for (let x = xMin; x <= xMax; x += 0.1) {
                    const y = quadraticCoeffs.p * x*x + quadraticCoeffs.q * x + quadraticCoeffs.r;
                    const screenX = p.map(x, xMin, xMax, 0, canvasWidth);
                    const screenY = p.map(y, yMax, yMin, 0, canvasHeight);
                    p.vertex(screenX, screenY);
                }
                p.endShape();

                // Draw the point of tangency
                const tangentY = cubicCoeffs.a * tValue*tValue*tValue + cubicCoeffs.b * tValue*tValue + cubicCoeffs.c * tValue + cubicCoeffs.d;
                const screenX = p.map(tValue, xMin, xMax, 0, canvasWidth);
                const screenY = p.map(tangentY, yMax, yMin, 0, canvasHeight);

                p.fill(colors.quadratic);
                p.noStroke();
                p.ellipse(screenX, screenY, 8, 8);

                p.pop();
            }

            function drawQuadraticFamily() {
                const steps = 20;
                const stepSize = (xMax - xMin) / steps;

                for (let i = 0; i <= steps; i++) {
                    const t = xMin + i * stepSize;

                    // Calculate quadratic coefficients for this t value
                    const p = (3 * cubicCoeffs.a * t) + cubicCoeffs.b;
                    const q = (-3 * cubicCoeffs.a * t * t) + cubicCoeffs.c;
                    const r = (cubicCoeffs.a * t * t * t) + cubicCoeffs.d;

                    // Choose color from palette
                    const colorIndex = i % colors.family.length;
                    p.fill(colors.family[colorIndex]);
                    p.noStroke();

                    // Draw filled quadratic
                    p.beginShape();

                    // Start at left edge
                    let x = xMin;
                    let y = p * x*x + q * x + r;
                    let screenX = p.map(x, xMin, xMax, 0, canvasWidth);
                    let screenY = p.map(y, yMax, yMin, 0, canvasHeight);
                    p.vertex(screenX, screenY);

                    // Add points along the curve
                    for (x = xMin; x <= xMax; x += 0.1) {
                        y = p * x*x + q * x + r;
                        screenX = p.map(x, xMin, xMax, 0, canvasWidth);
                        screenY = p.map(y, yMax, yMin, 0, canvasHeight);
                        p.vertex(screenX, screenY);
                    }

                    // End at right edge
                    x = xMax;
                    y = p * x*x + q * x + r;
                    screenX = p.map(x, xMin, xMax, 0, canvasWidth);
                    screenY = p.map(y, yMax, yMin, 0, canvasHeight);
                    p.vertex(screenX, screenY);

                    // Close the shape at the bottom or top depending on parabola direction
                    if (p > 0) { // Opens upward
                        p.vertex(screenX, canvasHeight);
                        p.vertex(p.map(xMin, xMin, xMax, 0, canvasWidth), canvasHeight);
                    } else { // Opens downward
                        p.vertex(screenX, 0);
                        p.vertex(p.map(xMin, xMin, xMax, 0, canvasWidth), 0);
                    }

                    p.endShape(p.CLOSE);
                }
            }

            function drawLocators() {
                p.push();
                p.stroke(colors.locator.stroke);
                p.strokeWeight(2);
                p.fill(colors.locator.fill);

                for (let loc of locators) {
                    const screenX = p.map(loc.x, xMin, xMax, 0, canvasWidth);
                    const screenY = p.map(loc.y, yMax, yMin, 0, canvasHeight);

                    p.ellipse(screenX, screenY, 20, 20);

                    // Draw cross inside the locator
                    p.line(screenX - 6, screenY, screenX + 6, screenY);
                    p.line(screenX, screenY - 6, screenX, screenY + 6);
                }

                p.pop();
            }
        };

        new p5(sketch);
    </script>
</body>
</html>
