    <script>
        // Constants
        const T0 = 273.15; // Reference temperature in Kelvin (0°C)
        const MAX_VOLUME = 2000; // Maximum volume for y-axis

        // State variables
        let v0 = 211; // Initial volume
        let k = v0 / T0; // Proportionality constant
        let currentTemp = 270; // Current temperature (Celsius by default)
        let currentTempUnit = 'C'; // Current temperature unit
        let currentVolume = 0; // Will be calculated

        // Plotly graph variables
        let plot;
        let plotData = [];
        let markerTrace;
        let lineTrace;

        // Initialize the visualization
        document.addEventListener('DOMContentLoaded', function() {
            // Initialize sliders and event listeners
            initSliders();

            // Initialize Plotly graph
            initPlot();

            // Initialize p5.js sketch
            initPistonAnimation();

            // Calculate initial state
            updateState();
        });

        function initSliders() {
            const v0Slider = document.getElementById('slider-v0');
            const v0Value = document.getElementById('v0-value');
            const TSlider = document.getElementById('slider-T');
            const TValue = document.getElementById('T-value');
            const radioCelsius = document.getElementById('radio-celsius');
            const radioKelvin = document.getElementById('radio-kelvin');

            // Update v0 slider display
            v0Slider.addEventListener('input', function() {
                v0 = parseInt(this.value);
                v0Value.textContent = `${v0} mL`;
                k = v0 / T0;
                updateState();
            });

            // Update T slider display and state
            TSlider.addEventListener('input', function() {
                currentTemp = parseInt(this.value);
                updateTempDisplay();
                updateState();
            });

            // Handle temperature unit changes
            radioCelsius.addEventListener('change', function() {
                if (this.checked) {
                    currentTempUnit = 'C';
                    TSlider.min = -273;
                    TSlider.max = 727;
                    // Convert from Kelvin to Celsius
                    currentTemp = Math.round(currentTemp - 273.15);
                    TSlider.value = currentTemp;
                    updateTempDisplay();
                    updateState();
                }
            });

            radioKelvin.addEventListener('change', function() {
                if (this.checked) {
                    currentTempUnit = 'K';
                    TSlider.min = 0;
                    TSlider.max = 1000;
                    // Convert from Celsius to Kelvin
                    currentTemp = Math.round(currentTemp + 273.15);
                    TSlider.value = currentTemp;
                    updateTempDisplay();
                    updateState();
                }
            });

            function updateTempDisplay() {
                if (currentTempUnit === 'C') {
                    TValue.textContent = `${currentTemp} °C`;
                } else {
                    TValue.textContent = `${currentTemp} K`;
                }
            }
        }

        function initPlot() {
            const plotContainer = document.getElementById('plot-container');

            // Create initial plot data
            lineTrace = {
                x: [],
                y: [],
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: [],
                    width: 3,
                    colorscale: 'Viridis'
                },
                name: 'Charles\'s Law'
            };

            markerTrace = {
                x: [currentTemp],
                y: [0],
                type: 'scatter',
                mode: 'markers',
                marker: {
                    size: 12,
                    color: getColorForTemp(currentTempUnit === 'C' ? currentTemp + 273.15 : currentTemp)
                },
                name: 'Current State'
            };

            plotData = [lineTrace, markerTrace];

            // Create plot layout
            const layout = {
                title: 'Volume vs Temperature',
                xaxis: {
                    title: currentTempUnit === 'C' ? 'Temperature (°C)' : 'Temperature (K)',
                    range: currentTempUnit === 'C' ? [-273, 727] : [0, 1000]
                },
                yaxis: {
                    title: 'Volume (mL)',
                    range: [0, MAX_VOLUME]
                },
                margin: {
                    l: 60,
                    r: 40,
                    b: 50,
                    t: 50,
                    pad: 10
                }
            };

            // Create the plot
            plot = Plotly.newPlot(plotContainer, plotData, layout);
        }

        function updatePlot() {
            const tempInKelvin = currentTempUnit === 'C' ? currentTemp + 273.15 : currentTemp;

            // Update line trace
            const numPoints = 100;
            const xValues = [];
            const yValues = [];
            const colors = [];

            const minTemp = currentTempUnit === 'C' ? -273 : 0;
            const maxTemp = currentTempUnit === 'C' ? 727 : 1000;

            for (let i = 0; i <= numPoints; i++) {
                const t = minTemp + (maxTemp - minTemp) * (i / numPoints);
                const tK = currentTempUnit === 'C' ? t + 273.15 : t;
                const v = k * tK;

                xValues.push(t);
                yValues.push(v);
                colors.push(getColorForTemp(tK));
            }

            Plotly.update(plotContainer, {
                x: [xValues, [currentTemp]],
                y: [yValues, [currentVolume]],
                'line.color': [colors, null],
                'marker.color': [null, getColorForTemp(tempInKelvin)]
            }, {
                'xaxis.title': currentTempUnit === 'C' ? 'Temperature (°C)' : 'Temperature (K)',
                'xaxis.range': [minTemp, maxTemp]
            });
        }

        function getColorForTemp(tempInKelvin) {
            // Normalize temperature to 0-1 range (0K-1000K)
            const normalizedTemp = Math.min(Math.max(tempInKelvin / 1000, 0), 1);

            // Create color gradient from purple (cold) to yellow (medium) to red (hot)
            if (normalizedTemp < 0.5) {
                // Interpolate between purple (128,0,128) and yellow (255,255,0)
                const t = normalizedTemp * 2;
                return `rgb(${128 + (255-128)*t}, ${0 + 255*t}, ${128 - 128*t})`;
            } else {
                // Interpolate between yellow (255,255,0) and red (255,0,0)
                const t = (normalizedTemp - 0.5) * 2;
                return `rgb(255, ${255 - 255*t}, 0)`;
            }
        }

        function initPistonAnimation() {
            new p5(function(p) {
                let canvas;
                let pistonHeight;
                let gasHeight;
                let gasColor;

                p.setup = function() {
                    const container = document.getElementById('piston-container');
                    const width = container.clientWidth;
                    const height = container.clientHeight;

                    canvas = p.createCanvas(width * 0.8, height * 0.9);
                    canvas.parent('piston-container');

                    updatePiston();
                };

                p.draw = function() {
                    p.background(240);

                    // Draw cylinder
                    p.fill(200, 200, 255);
                    p.noStroke();
                    p.rect(p.width * 0.3, p.height * 0.1, p.width * 0.4, p.height * 0.8);

                    // Draw gas
                    p.fill(gasColor);
                    p.rect(p.width * 0.3, p.height - gasHeight, p.width * 0.4, gasHeight);

                    // Draw piston
                    p.fill(150);
                    p.rect(p.width * 0.25, p.height - pistonHeight - 10, p.width * 0.5, 10);
                    p.rect(p.width * 0.4, p.height - pistonHeight - 30, p.width * 0.2, 20);

                    // Draw pressure arrows
                    p.fill(255, 165, 0);
                    for (let x = p.width * 0.3; x <= p.width * 0.7; x += p.width * 0.1) {
                        p.triangle(x, p.height - pistonHeight - 15, x - 5, p.height - pistonHeight - 25, x + 5, p.height - pistonHeight - 25);
                    }
                };

                p.windowResized = function() {
                    const container = document.getElementById('piston-container');
                    const width = container.clientWidth;
                    const height = container.clientHeight;
                    p.resizeCanvas(width * 0.8, height * 0.9);
                };

                function updatePiston() {
                    // Calculate heights (inverted because 0 volume is at top)
                    const maxVolume = k * (currentTempUnit === 'C' ? 1000 : 727 + 273.15);
                    pistonHeight = p.map(currentVolume, 0, maxVolume, p.height * 0.8, p.height * 0.1);
                    gasHeight = p.height * 0.9 - pistonHeight;

                    // Set gas color based on temperature
                    const tempInKelvin = currentTempUnit === 'C' ? currentTemp + 273.15 : currentTemp;
                    gasColor = p.color(getColorForTemp(tempInKelvin));
                }

                // Expose update function to global scope
                window.updatePistonAnimation = updatePiston;
            });
        }

        function updateState() {
            // Calculate current volume based on current temperature
            const tempInKelvin = currentTempUnit === 'C' ? currentTemp + 273.15 : currentTemp;
            currentVolume = k * tempInKelvin;

            // Update plot
            updatePlot();

            // Update piston animation
            if (window.updatePistonAnimation) {
                window.updatePistonAnimation();
            }
        }
    </script>
</body>
</html>
