        const sliderA = document.getElementById('slider-a');
        const sliderB = document.getElementById('slider-b');
        const sliderC = document.getElementById('slider-c');

        // Info panel elements
        const arcLabel1 = document.getElementById('arc-label-1');
        const arcValue1 = document.getElementById('arc-value-1');
        const vertexLabel1 = document.getElementById('vertex-label-1');
        const vertexValue1 = document.getElementById('vertex-value-1');
        const arcLabel2 = document.getElementById('arc-label-2');
        const arcValue2 = document.getElementById('arc-value-2');
        const vertexLabel2 = document.getElementById('vertex-label-2');
        const vertexValue2 = document.getElementById('vertex-value-2');
        const arcLabel3 = document.getElementById('arc-label-3');
        const arcValue3 = document.getElementById('arc-value-3');
        const vertexLabel3 = document.getElementById('vertex-label-3');
        const vertexValue3 = document.getElementById('vertex-value-3');
        const arcTotal = document.getElementById('arc-total');
        const vertexTotal = document.getElementById('vertex-total');

        // Event listeners
        radioA.addEventListener('change', () => { selectedVertex = 'A'; updateVisualization(); });
        radioB.addEventListener('change', () => { selectedVertex = 'B'; updateVisualization(); });
        radioC.addEventListener('change', () => { selectedVertex = 'C'; updateVisualization(); });
        radioNone.addEventListener('change', () => { selectedVertex = 'None'; updateVisualization(); });

        sliderA.addEventListener('input', () => { angleA = parseFloat(sliderA.value); updateVisualization(); });
        sliderB.addEventListener('input', () => { angleB = parseFloat(sliderB.value); updateVisualization(); });
        sliderC.addEventListener('input', () => { angleC = parseFloat(sliderC.value); updateVisualization(); });

        function setup() {
            const canvas = createCanvas(canvasSize, canvasSize);
            canvas.parent('canvas-container');
            centerX = width / 2;
            centerY = height / 2;
            radius = width * 0.4;

            // Set up mouse interaction
            canvas.mousePressed(canvasMousePressed);
            canvas.mouseReleased(canvasMouseReleased);
            canvas.mouseDragged(canvasMouseDragged);

            updateVisualization();
        }

        function draw() {
            background(255);
            drawCircleAndTriangle();
        }

        function drawCircleAndTriangle() {
            // Draw colored sectors
            drawSectors();

            // Draw the circle
            fill(170, 204, 222);
            stroke(102, 102, 102);
            strokeWeight(1);
            ellipse(centerX, centerY, radius * 2, radius * 2);

            // Draw center point
            fill(102, 102, 102);
            noStroke();
            ellipse(centerX, centerY, 5, 5);

            // Calculate vertex positions
            const vertexAX = centerX + radius * cos(radians(angleA));
            const vertexAY = centerY + radius * sin(radians(angleA));
            const vertexBX = centerX + radius * cos(radians(angleB));
            const vertexBY = centerY + radius * sin(radians(angleB));
            const vertexCX = centerX + radius * cos(radians(angleC));
            const vertexCY = centerY + radius * sin(radians(angleC));

            // Draw the triangle
            fill(173, 216, 230, 128);
            stroke(102, 102, 102);
            strokeWeight(1);
            beginShape();
            vertex(vertexAX, vertexAY);
            vertex(vertexBX, vertexBY);
            vertex(vertexCX, vertexCY);
            endShape(CLOSE);

            // Draw vertex labels
            textSize(20);
            textStyle(BOLD);
            fill(0, 0, 139);
            noStroke();
            text('A', vertexAX + 20 * cos(radians(angleA)), vertexAY + 20 * sin(radians(angleA)));
            text('B', vertexBX + 20 * cos(radians(angleB)), vertexBY + 20 * sin(radians(angleB)));
            text('C', vertexCX + 20 * cos(radians(angleC)), vertexCY + 20 * sin(radians(angleC)));

            // Highlight selected angle if not 'None'
            if (selectedVertex !== 'None') {
                highlightSelectedAngle();
            }
        }

        function drawSectors() {
            noStroke();

            // Calculate arc measures
            const arcAB = (angleB - angleA + 360) % 360;
            const arcBC = (angleC - angleB + 360) % 360;
            const arcCA = (angleA - angleC + 360) % 360;

            // Draw sector for arc AB (green)
            fill(224, 240, 224);
            arc(centerX, centerY, radius * 2, radius * 2, 
                radians(angleA), radians(angleA + arcAB), PIE);

            // Draw sector for arc BC (yellow)
            fill(245, 245, 213);
            arc(centerX, centerY, radius * 2, radius * 2, 
                radians(angleB), radians(angleB + arcBC), PIE);

            // Draw sector for arc CA (blue)
            fill(224, 232, 248);
            arc(centerX, centerY, radius * 2, radius * 2, 
                radians(angleC), radians(angleC + arcCA), PIE);
        }

        function highlightSelectedAngle() {
            let arcStart, arcEnd, arcMeasure, vertexAngle;
            let vertexX, vertexY;

            // Determine which arc and vertex to highlight based on selection
            if (selectedVertex === 'A') {
                arcStart = angleB;
                arcEnd = angleC;
                vertexX = centerX + radius * cos(radians(angleA));
                vertexY = centerY + radius * sin(radians(angleA));
            } else if (selectedVertex === 'B') {
                arcStart = angleC;
                arcEnd = angleA;
                vertexX = centerX + radius * cos(radians(angleB));
                vertexY = centerY + radius * sin(radians(angleB));
            } else { // 'C'
                arcStart = angleA;
                arcEnd = angleB;
                vertexX = centerX + radius * cos(radians(angleC));
                vertexY = centerY + radius * sin(radians(angleC));
            }

            // Calculate arc measure
            arcMeasure = (arcEnd - arcStart + 360) % 360;
            vertexAngle = arcMeasure / 2;

            // Draw the central angle (θ)
            stroke(0, 128, 0);
            strokeWeight(2);
            noFill();
            arc(centerX, centerY, radius * 2, radius * 2, 
                radians(arcStart), radians(arcEnd));

            // Draw arrow at end of central angle
            drawAngleArrow(centerX, centerY, radians(arcEnd), radius, 15, 0, 128, 0);

            // Draw small arc near center
            const smallRadius = 20;
            arc(centerX, centerY, smallRadius * 2, smallRadius * 2, 
                radians(arcStart), radians(arcEnd));

            // Label the central angle
            const midAngle = (arcStart + arcMeasure / 2) % 360;
            const labelX = centerX + (radius + 30) * cos(radians(midAngle));
            const labelY = centerY + (radius + 30) * sin(radians(midAngle));
            textSize(16);
            fill(0, 128, 0);
            noStroke();
            text(`θ = ${arcMeasure.toFixed(1)}°`, labelX, labelY);

            // Draw the inscribed angle (θ/2)
            stroke(255, 0, 0);
            strokeWeight(2);
            const inscribedRadius = radius * 0.3;
            const startAngle = atan2(centerY - vertexY, centerX - vertexX) - PI/2;
            const endAngle = startAngle + radians(vertexAngle);
            arc(vertexX, vertexY, inscribedRadius * 2, inscribedRadius * 2, 
                startAngle, endAngle);

            // Label the inscribed angle
            const inscribedLabelX = vertexX + (inscribedRadius + 20) * cos(startAngle + radians(vertexAngle/2));
            const inscribedLabelY = vertexY + (inscribedRadius + 20) * sin(startAngle + radians(vertexAngle/2));
            fill(255, 0, 0);
            text(`θ/2 = ${vertexAngle.toFixed(1)}°`, inscribedLabelX, inscribedLabelY);
        }

        function drawAngleArrow(x, y, angle, radius, size, r, g, b) {
            push();
            translate(x, y);
            rotate(angle);
            fill(r, g, b);
            noStroke();
            triangle(-size, radius - size, size, radius - size, 0, radius);
            pop();
        }

        function updateVisualization() {
            // Update slider positions
            sliderA.value = angleA;
            sliderB.value = angleB;
            sliderC.value = angleC;

            // Calculate arc measures
            const arcAB = (angleB - angleA + 360) % 360;
            const arcBC = (angleC - angleB + 360) % 360;
            const arcCA = (angleA - angleC + 360) % 360;

            // Calculate vertex angles
            const vertexA = arcBC / 2;
            const vertexB = arcCA / 2;
            const vertexC = arcAB / 2;

            // Update info panel based on selected vertex
            if (selectedVertex === 'None') {
                // Show all three arc/vertex pairs
                arcLabel1.textContent = 'Arc AB';
                arcValue1.textContent = `${arcAB.toFixed(1)}°`;
                vertexLabel1.textContent = 'Vertex C';
                vertexValue1.textContent = `${vertexC.toFixed(1)}°`;

                arcLabel2.textContent = 'Arc BC';
                arcValue2.textContent = `${arcBC.toFixed(1)}°`;
                vertexLabel2.textContent = 'Vertex A';
                vertexValue2.textContent = `${vertexA.toFixed(1)}°`;

                arcLabel3.textContent = 'Arc CA';
                arcValue3.textContent = `${arcCA.toFixed(1)}°`;
                vertexLabel3.textContent = 'Vertex B';
                vertexValue3.textContent = `${vertexB.toFixed(1)}°`;

                // Update totals
                arcTotal.textContent = '360°';
                vertexTotal.textContent = '180°';
            } else {
                // Show only the selected arc/vertex pair
                if (selectedVertex === 'A') {
                    arcLabel1.textContent = 'Arc BC';
                    arcValue1.textContent = `${arcBC.toFixed(1)}°`;
                    vertexLabel1.textContent = 'Vertex A';
                    vertexValue1.textContent = `${vertexA.toFixed(1)}°`;
                } else if (selectedVertex === 'B') {
                    arcLabel1.textContent = 'Arc CA';
                    arcValue1.textContent = `${arcCA.toFixed(1)}°`;
                    vertexLabel1.textContent = 'Vertex B';
                    vertexValue1.textContent = `${vertexB.toFixed(1)}°`;
                } else { // 'C'
                    arcLabel1.textContent = 'Arc AB';
                    arcValue1.textContent = `${arcAB.toFixed(1)}°`;
                    vertexLabel1.textContent = 'Vertex C';
                    vertexValue1.textContent = `${vertexC.toFixed(1)}°`;
                }

                // Clear other rows
                arcLabel2.textContent = '';
                arcValue2.textContent = '';
                vertexLabel2.textContent = '';
                vertexValue2.textContent = '';

                arcLabel3.textContent = '';
                arcValue3.textContent = '';
                vertexLabel3.textContent = '';
                vertexValue3.textContent = '';

                // Clear totals
                arcTotal.textContent = '?';
                vertexTotal.textContent = '?';
            }

            redraw();
        }

        function canvasMousePressed() {
            // Check if mouse is near any vertex
            const mouseDistA = dist(mouseX, mouseY, 
                centerX + radius * cos(radians(angleA)), 
                centerY + radius * sin(radians(angleA)));
            const mouseDistB = dist(mouseX, mouseY, 
                centerX + radius * cos(radians(angleB)), 
                centerY + radius * sin(radians(angleB)));
            const mouseDistC = dist(mouseX, mouseY, 
                centerX + radius * cos(radians(angleC)), 
                centerY + radius * sin(radians(angleC)));

            const threshold = 20;
            if (mouseDistA < threshold) {
                draggingVertex = 'A';
            } else if (mouseDistB < threshold) {
                draggingVertex = 'B';
            } else if (mouseDistC < threshold) {
                draggingVertex = 'C';
            }
        }

        function canvasMouseDragged() {
            if (draggingVertex) {
                // Calculate angle from center to mouse position
                let newAngle = degrees(atan2(mouseY - centerY, mouseX - centerX));
                if (newAngle < 0) newAngle += 360;

                // Update the corresponding angle
                if (draggingVertex === 'A') {
                    angleA = newAngle;
                    sliderA.value = angleA;
                } else if (draggingVertex === 'B') {
                    angleB = newAngle;
                    sliderB.value = angleB;
                } else if (draggingVertex === 'C') {
                    angleC = newAngle;
                    sliderC.value = angleC;
                }

                updateVisualization();
            }
        }

        function canvasMouseReleased() {
            draggingVertex = null;
        }
    </script>
</body>
</html>
