    <script>
        // Constants
        const N = 100; // Number of spatial grid points
        const xi = Array.from({length: N}, (_, i) => i / (N - 1)); // Spatial grid

        // State variables
        let c = new Array(N).fill(0);
        let theta = new Array(N).fill(1);
        let currentTau = 1.0;
        let currentPhi = 2.0;
        let currentBeta = 0.2;
        let currentGamma = 20;
        let currentXi = 0.4;

        // Initialize plots
        let concentrationPlot = document.getElementById('plot-concentration');
        let temperaturePlot = document.getElementById('plot-temperature');

        // Initialize sliders and buttons
        const sliderTau = document.getElementById('slider-tau');
        const sliderPhi = document.getElementById('slider-phi');
        const sliderBeta = document.getElementById('slider-beta');
        const sliderGamma = document.getElementById('slider-gamma');
        const sliderXi = document.getElementById('slider-xi');

        const btnDecTau = document.getElementById('btn-dec-tau');
        const btnIncTau = document.getElementById('btn-inc-tau');
        const btnDecPhi = document.getElementById('btn-dec-phi');
        const btnIncPhi = document.getElementById('btn-inc-phi');
        const btnDecBeta = document.getElementById('btn-dec-beta');
        const btnIncBeta = document.getElementById('btn-inc-beta');
        const btnDecGamma = document.getElementById('btn-dec-gamma');
        const btnIncGamma = document.getElementById('btn-inc-gamma');
        const btnDecXi = document.getElementById('btn-dec-xi');
        const btnIncXi = document.getElementById('btn-inc-xi');

        const valueTau = document.getElementById('value-tau');
        const valuePhi = document.getElementById('value-phi');
        const valueBeta = document.getElementById('value-beta');
        const valueGamma = document.getElementById('value-gamma');
        const valueXi = document.getElementById('value-xi');

        // Helper functions
        function updateSliderValue(slider, displayElement, precision) {
            const value = parseFloat(slider.value);
            displayElement.textContent = value.toFixed(precision);
            return value;
        }

        function adjustSlider(slider, step, direction) {
            const currentValue = parseFloat(slider.value);
            const newValue = currentValue + (direction * step);
            slider.value = Math.max(parseFloat(slider.min), Math.min(parseFloat(slider.max), newValue));
            slider.dispatchEvent(new Event('input'));
        }

        // Numerical solver functions
        function reactionRate(c, theta, phi, gamma) {
            return phi * phi * c * Math.exp(gamma * (1 - 1/theta));
        }

        function laplacian(u, h) {
            const n = u.length;
            const result = new Array(n).fill(0);

            // Central difference for interior points
            for (let i = 1; i < n - 1; i++) {
                result[i] = (u[i-1] - 2*u[i] + u[i+1]) / (h*h);
            }

            // Boundary conditions
            // At ξ=0 (i=0): symmetry (du/dξ = 0)
            result[0] = 2 * (u[1] - u[0]) / (h*h);

            // At ξ=1 (i=n-1): fixed value (u=1)
            result[n-1] = 0; // Will be set by boundary condition

            return result;
        }

        function derivatives(c, theta, phi, beta, gamma, h) {
            const dcdt = laplacian(c, h);
            const dthetadt = laplacian(theta, h);

            for (let i = 0; i < c.length; i++) {
                const rate = reactionRate(c[i], theta[i], phi, gamma);
                dcdt[i] -= rate;
                dthetadt[i] += beta * rate;
            }

            // Boundary conditions at ξ=1
            dcdt[c.length-1] = 0;
            dthetadt[theta.length-1] = 0;

            return { dcdt, dthetadt };
        }

        function rk4Step(c, theta, phi, beta, gamma, h, dt) {
            const k1 = derivatives(c, theta, phi, beta, gamma, h);

            const c2 = c.map((val, i) => val + k1.dcdt[i] * dt/2);
            const theta2 = theta.map((val, i) => val + k1.dthetadt[i] * dt/2);
            const k2 = derivatives(c2, theta2, phi, beta, gamma, h);

            const c3 = c.map((val, i) => val + k2.dcdt[i] * dt/2);
            const theta3 = theta.map((val, i) => val + k2.dthetadt[i] * dt/2);
            const k3 = derivatives(c3, theta3, phi, beta, gamma, h);

            const c4 = c.map((val, i) => val + k3.dcdt[i] * dt);
            const theta4 = theta.map((val, i) => val + k3.dthetadt[i] * dt);
            const k4 = derivatives(c4, theta4, phi, beta, gamma, h);

            const newC = c.map((val, i) => {
                return val + (k1.dcdt[i] + 2*k2.dcdt[i] + 2*k3.dcdt[i] + k4.dcdt[i]) * dt/6;
            });

            const newTheta = theta.map((val, i) => {
                return val + (k1.dthetadt[i] + 2*k2.dthetadt[i] + 2*k3.dthetadt[i] + k4.dthetadt[i]) * dt/6;
            });

            // Apply boundary conditions
            newC[newC.length-1] = 1;
            newTheta[newTheta.length-1] = 1;

            return { c: newC, theta: newTheta };
        }

        function solvePDE(tau, phi, beta, gamma) {
            let currentC = new Array(N).fill(0);
            let currentTheta = new Array(N).fill(1);

            const h = 1 / (N - 1);
            const dt = 0.01;
            const steps = Math.ceil(tau / dt);
            const actualDt = tau / steps;

            for (let step = 0; step < steps; step++) {
                const result = rk4Step(currentC, currentTheta, phi, beta, gamma, h, actualDt);
                currentC = result.c;
                currentTheta = result.theta;
            }

            return { c: currentC, theta: currentTheta };
        }

        // Plotting functions
        function createConcentrationPlot(c, xiValue) {
            const cValue = interpolateValue(xi, c, xiValue);

            // Create marker line
            const markerLineX = [xiValue, xiValue, 0];
            const markerLineY = [0, cValue, cValue];

            const layout = {
                margin: { t: 10, r: 100, b: 40, l: 60 },
                xaxis: { title: 'ξ', range: [0, 1] },
                yaxis: { title: 'c', range: [0, 1.1] },
                showlegend: false,
                annotations: [{
                    x: 0.85,
                    y: 0.5,
                    xref: 'paper',
                    yref: 'paper',
                    text: `τ = ${currentTau.toFixed(2)}\nξ = ${xiValue.toFixed(3)}\nc = ${cValue.toFixed(5)}`,
                    showarrow: false,
                    font: { size: 12 }
                }]
            };

            const data = [
                { x: xi, y: c, type: 'scatter', mode: 'lines', line: { color: '#1f77b4' } },
                { x: markerLineX, y: markerLineY, type: 'scatter', mode: 'lines', line: { color: '#333333', width: 2 } }
            ];

            Plotly.newPlot(concentrationPlot, data, layout);
        }

        function createTemperaturePlot(theta, xiValue) {
            const thetaValue = interpolateValue(xi, theta, xiValue);
            const yRange = [Math.min(...theta) - 0.1, Math.max(...theta) + 0.1];

            // Create marker line
            const markerLineX = [xiValue, xiValue, 0];
            const markerLineY = [yRange[0], thetaValue, thetaValue];

            const layout = {
                margin: { t: 10, r: 100, b: 40, l: 60 },
                xaxis: { title: 'ξ', range: [0, 1] },
                yaxis: { title: 'θ', autorange: true },
                showlegend: false,
                annotations: [{
                    x: 0.85,
                    y: 0.5,
                    xref: 'paper',
                    yref: 'paper',
                    text: `τ = ${currentTau.toFixed(2)}\nξ = ${xiValue.toFixed(3)}\nθ = ${thetaValue.toFixed(5)}`,
                    showarrow: false,
                    font: { size: 12 }
                }]
            };

            const data = [
                { x: xi, y: theta, type: 'scatter', mode: 'lines', line: { color: '#ff7f0e' } },
                { x: markerLineX, y: markerLineY, type: 'scatter', mode: 'lines', line: { color: '#333333', width: 2 } }
            ];

            Plotly.newPlot(temperaturePlot, data, layout);
        }

        function updatePlots() {
            const solution = solvePDE(currentTau, currentPhi, currentBeta, currentGamma);
            c = solution.c;
            theta = solution.theta;

            createConcentrationPlot(c, currentXi);
            createTemperaturePlot(theta, currentXi);
        }

        function updateMarkerPosition() {
            const cValue = interpolateValue(xi, c, currentXi);
            const thetaValue = interpolateValue(xi, theta, currentXi);

            // Update concentration plot
            const markerLineXC = [currentXi, currentXi, 0];
            const markerLineYC = [0, cValue, cValue];

            Plotly.restyle(concentrationPlot, {
                x: [null, markerLineXC],
                y: [null, markerLineYC]
            }, [1]);

            Plotly.relayout(concentrationPlot, {
                'annotations[0].text': `τ = ${currentTau.toFixed(2)}\nξ = ${currentXi.toFixed(3)}\nc = ${cValue.toFixed(5)}`
            });

            // Update temperature plot
            const yRange = [Math.min(...theta) - 0.1, Math.max(...theta) + 0.1];
            const markerLineXT = [currentXi, currentXi, 0];
            const markerLineYT = [yRange[0], thetaValue, thetaValue];

            Plotly.restyle(temperaturePlot, {
                x: [null, markerLineXT],
                y: [null, markerLineYT]
            }, [1]);

            Plotly.relayout(temperaturePlot, {
                'annotations[0].text': `τ = ${currentTau.toFixed(2)}\nξ = ${currentXi.toFixed(3)}\nθ = ${thetaValue.toFixed(5)}`
            });
        }

        function interpolateValue(x, y, xiValue) {
            if (xiValue <= x[0]) return y[0];
            if (xiValue >= x[x.length-1]) return y[y.length-1];

            for (let i = 1; i < x.length; i++) {
                if (xiValue <= x[i]) {
                    const t = (xiValue - x[i-1]) / (x[i] - x[i-1]);
                    return y[i-1] + t * (y[i] - y[i-1]);
                }
            }
            return y[y.length-1];
        }

        // Event listeners
        sliderTau.addEventListener('input', () => {
            currentTau = updateSliderValue(sliderTau, valueTau, 2);
            updatePlots();
        });

        sliderPhi.addEventListener('input', () => {
            currentPhi = updateSliderValue(sliderPhi, valuePhi, 1);
            updatePlots();
        });

        sliderBeta.addEventListener('input', () => {
            currentBeta = updateSliderValue(sliderBeta, valueBeta, 2);
            updatePlots();
        });

        sliderGamma.addEventListener('input', () => {
            currentGamma = updateSliderValue(sliderGamma, valueGamma, 0);
            updatePlots();
        });

        sliderXi.addEventListener('input', () => {
            currentXi = updateSliderValue(sliderXi, valueXi, 3);
            updateMarkerPosition();
        });

        // Button event listeners
        btnDecTau.addEventListener('click', () => adjustSlider(sliderTau, 0.01, -1));
        btnIncTau.addEventListener('click', () => adjustSlider(sliderTau, 0.01, 1));
        btnDecPhi.addEventListener('click', () => adjustSlider(sliderPhi, 0.1, -1));
        btnIncPhi.addEventListener('click', () => adjustSlider(sliderPhi, 0.1, 1));
        btnDecBeta.addEventListener('click', () => adjustSlider(sliderBeta, 0.01, -1));
