        function update() {
            // Get current slider values
            const b_max = parseFloat(document.getElementById('slider-bmax').value);
            const b_0 = parseFloat(document.getElementById('slider-b0').value);
            const r = parseFloat(document.getElementById('slider-r').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);

            // Update displayed values
