            document.getElementById('value-b0').textContent = b_0.toFixed(1);
            document.getElementById('value-r').textContent = r.toFixed(3);
            document.getElementById('value-beta').textContent = beta.toFixed(3);

            // Calculate Richards curve
            const M = Math.pow(b_max / b_0, beta) - 1;
            const t_values = Array.from({length: 66}, (_, i) => 15 + i);
            const V_values = t_values.map(t => 
                b_max / Math.pow(1 + M * Math.exp(-r * t), 1/beta)
            );

            // Calculate inflection point properties
            const t_i = Math.log(M * beta) / r;
            const V_i = b_max * Math.pow((beta + 1) / (2 * beta), 1/beta);
            const V_prime_i = (r / beta) * V_i * (1 - Math.pow(V_i / b_max, beta));

            // Calculate 4/5 law parameters
            let u, p;
            let valid45Law = true;

            if (t_i - (4/5) * V_i / V_prime_i > 0) {
                u = t_i - (4/5) * V_i / V_prime_i;
                p = V_i / Math.pow(t_i - u, 4/5);
            } else {
                u = NaN;
                p = NaN;
                valid45Law = false;
            }

            // Update results display
            document.getElementById('value-u').textContent = isNaN(u) ? 'undefined' : u.toFixed(4);
            document.getElementById('value-p').textContent = isNaN(p) ? 'undefined' : p.toFixed(4);

            // Calculate 4/5 law curve
            const V_45_values = valid45Law ? 
                t_values.map(t => t > u ? p * Math.pow(t - u, 4/5) : null) : 
                t_values.map(() => null);

            // Update plot
            Plotly.react(plotDiv, [
                traces[0],
                {
                    ...traces[1],
                    x: t_values,
                    y: V_values
                },
                {
                    ...traces[2],
                    x: t_values,
                    y: V_45_values
                }
            ], layout);
        }

