    <script>
        // Initialize Three.js scene
        let scene, camera, renderer, mainGroup;
        let segments = [];

        function initThreeJS() {
            // Get canvas element
            const canvas = document.getElementById('main-canvas');

            // Create scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf5f5f5);

            // Create camera
            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.z = 50;

            // Create renderer
            renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
            renderer.setSize(window.innerWidth - 300, window.innerHeight);

            // Create lights
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(1, 1, 1);
            scene.add(directionalLight);

            // Create main group for all segments
            mainGroup = new THREE.Group();
            scene.add(mainGroup);

            // Handle window resize
            window.addEventListener('resize', () => {
                camera.aspect = (window.innerWidth - 300) / window.innerHeight;
                camera.updateProjectionMatrix();
                renderer.setSize(window.innerWidth - 300, window.innerHeight);
            });

            // Initial render
            redraw();
            animate();
        }

        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }

        function redraw() {
            // Clear previous segments
            while (mainGroup.children.length > 0) {
                mainGroup.remove(mainGroup.children[0]);
            }

            // Get current control values
            const polygonSides = parseInt(document.querySelector('input[name="base-polygon"]:checked').value);
            const units = parseInt(document.getElementById('slider-units').value);
            const angleAdvance = parseFloat(document.getElementById('slider-angle').value) * (Math.PI / 180);
            const conicalFactor = parseFloat(document.getElementById('slider-conical').value);
            const rotateX = parseFloat(document.getElementById('slider-rotate-x').value) * (Math.PI / 180);
            const rotateZ = parseFloat(document.getElementById('slider-rotate-z').value) * (Math.PI / 180);

            // Create base polygon shape
            const shape = new THREE.Shape();
            const radius = 10;
            for (let i = 0; i < polygonSides; i++) {
                const angle = (i / polygonSides) * Math.PI * 2;
                const x = radius * Math.cos(angle);
                const y = radius * Math.sin(angle);

                if (i === 0) {
                    shape.moveTo(x, y);
                } else {
                    shape.lineTo(x, y);
                }
            }

            // Generate all segments
            for (let i = 0; i < units; i++) {
                // Calculate position and rotation
                const angle = i * angleAdvance;
                const spiralRadius = 4 * Math.sqrt(i);
                const scale = 0.5 * Math.log(i + 2);

                const x = spiralRadius * Math.cos(angle);
                const y = spiralRadius * Math.sin(angle);
                const z = conicalFactor * spiralRadius;

                // Create geometry and material
                const geometry = new THREE.ShapeGeometry(shape);
                const material = new THREE.MeshStandardMaterial({
                    color: new THREE.Color().setHSL((angle / (2 * Math.PI)) % 1.0, 0.8, 0.6),
                    side: THREE.DoubleSide,
                    flatShading: true
                });

                // Create mesh
                const mesh = new THREE.Mesh(geometry, material);
                mesh.position.set(x, y, z);
                mesh.scale.set(scale, scale, scale);
                mesh.rotation.z = angle;

                mainGroup.add(mesh);
            }

            // Apply master rotations
            mainGroup.rotation.x = rotateX;
            mainGroup.rotation.z = rotateZ;
        }

        // Initialize controls
        function initControls() {
            // Update value displays
            document.getElementById('slider-units').addEventListener('input', function() {
                document.getElementById('units-value').textContent = this.value;
                redraw();
            });

            document.getElementById('slider-angle').addEventListener('input', function() {
                document.getElementById('angle-value').textContent = this.value;
                redraw();
            });

            document.getElementById('slider-conical').addEventListener('input', function() {
