            document.getElementById('btn-units-plus').addEventListener('click', function() {
                const slider = document.getElementById('slider-units');
                slider.value = parseInt(slider.value) + parseInt(slider.step);
                document.getElementById('units-value').textContent = slider.value;
                redraw();
            });

            document.getElementById('btn-angle-plus').addEventListener('click', function() {
                const slider = document.getElementById('slider-angle');
                slider.value = (parseFloat(slider.value) + parseFloat(slider.step)).toFixed(1);
                document.getElementById('angle-value').textContent = slider.value;
                redraw();
            });

            document.getElementById('btn-conical-plus').addEventListener('click', function() {
                const slider = document.getElementById('slider-conical');
                slider.value = (parseFloat(slider.value) + parseFloat(slider.step)).toFixed(2);
