                slider.value = parseInt(slider.value) + parseInt(slider.step);
                document.getElementById('rotate-z-value').textContent = slider.value;
                redraw();
            });

            // Radio buttons
            document.querySelectorAll('input[name="base-polygon"]').forEach(radio => {
                radio.addEventListener('change', redraw);
            });
        }

        // Initialize everything when the page loads
        window.addEventListener('DOMContentLoaded', () => {
            initThreeJS();
            initControls();
        });
    </script>
