    <script src="https://cdn.jsdelivr.net/npm/three@0.150.1/examples/js/controls/OrbitControls.js"></script>

    <script>
        // Scene setup
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf0f0f0);

        // Camera setup
        const camera = new THREE.PerspectiveCamera(75, 800 / 600, 0.1, 1000);
        camera.position.set(1.5, 1.5, 1.5);

        // Renderer setup
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(800, 600);
        document.getElementById('canvas-container').appendChild(renderer.domElement);

        // Controls setup
        const orbitControls = new THREE.OrbitControls(camera, renderer.domElement);
        orbitControls.enableDamping = true;
        orbitControls.dampingFactor = 0.05;

        // Lighting
        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight1.position.set(1, 1, 1);
        scene.add(directionalLight1);

        const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight2.position.set(-1, -1, -1);
        scene.add(directionalLight2);

        // Groups for organization
        const atomsGroup = new THREE.Group();
        scene.add(atomsGroup);

        const planesGroup = new THREE.Group();
        scene.add(planesGroup);

        const axesGroup = new THREE.Group();
        scene.add(axesGroup);

        // Unit cell wireframe
        const unitCellGeometry = new THREE.BoxGeometry(1, 1, 1);
        const unitCellEdges = new THREE.EdgesGeometry(unitCellGeometry);
        const unitCell = new THREE.LineSegments(unitCellEdges, new THREE.LineBasicMaterial({ color: 0x333333 }));
        scene.add(unitCell);

        // Coordinate axes
        const axesHelper = new THREE.AxesHelper(1.5);
        axesHelper.position.set(-0.5, -0.5, -0.5); // Offset to match unit cell
        axesGroup.add(axesHelper);

        // Atom material
        const atomMaterial = new THREE.MeshPhongMaterial({
            color: 0x3a5fcd,
            specular: 0x66ccff,
            shininess: 30
        });

        // Plane material
        const planeMaterial = new THREE.MeshBasicMaterial({
            color: 0x1e90ff,
            side: THREE.DoubleSide,
            transparent: true,
            opacity: 0.8
        });

        // Function to calculate greatest common divisor
        function gcd(a, b) {
            a = Math.abs(a);
            b = Math.abs(b);
            if (b > a) [a, b] = [b, a];
            while (b > 0) [a, b] = [b, a % b];
            return a;
        }

        function gcd3(a, b, c) {
            return gcd(gcd(a, b), c);
        }

        // Function to generate plane geometry
        function createPlaneGeometry(h, k, l, n) {
            // Normalize the plane equation
            const divisor = gcd3(h, k, l);
            if (divisor === 0) return null;

            h /= divisor;
            k /= divisor;
            l /= divisor;
            n /= divisor;

            // Find intersections with unit cell edges
            const vertices = [];
            const cubeVertices = [
                new THREE.Vector3(-0.5, -0.5, -0.5),
                new THREE.Vector3(0.5, -0.5, -0.5),
                new THREE.Vector3(0.5, 0.5, -0.5),
                new THREE.Vector3(-0.5, 0.5, -0.5),
                new THREE.Vector3(-0.5, -0.5, 0.5),
                new THREE.Vector3(0.5, -0.5, 0.5),
                new THREE.Vector3(0.5, 0.5, 0.5),
                new THREE.Vector3(-0.5, 0.5, 0.5)
            ];

            const edges = [
                [0, 1], [1, 2], [2, 3], [3, 0], // bottom face
                [4, 5], [5, 6], [6, 7], [7, 4], // top face
                [0, 4], [1, 5], [2, 6], [3, 7]  // vertical edges
            ];

            for (const [i, j] of edges) {
                const v1 = cubeVertices[i];
                const v2 = cubeVertices[j];

                const denom = h * (v2.x - v1.x) + k * (v2.y - v1.y) + l * (v2.z - v1.z);
                if (Math.abs(denom) < 1e-10) continue;

                const t = (n - (h * v1.x + k * v1.y + l * v1.z)) / denom;
                if (t >= 0 && t <= 1) {
                    const point = new THREE.Vector3(
                        v1.x + t * (v2.x - v1.x),
                        v1.y + t * (v2.y - v1.y),
                        v1.z + t * (v2.z - v1.z)
                    );
                    vertices.push(point);
                }
            }

            if (vertices.length < 3) return null;

            // Sort vertices around centroid
            const centroid = new THREE.Vector3();
            vertices.forEach(v => centroid.add(v));
            centroid.divideScalar(vertices.length);

            const normal = new THREE.Vector3(h, k, l).normalize();
            const refVector = new THREE.Vector3().subVectors(vertices[0], centroid).normalize();
            const upVector = new THREE.Vector3().crossVectors(normal, refVector).normalize();

            vertices.sort((a, b) => {
                const va = new THREE.Vector3().subVectors(a, centroid);
                const vb = new THREE.Vector3().subVectors(b, centroid);

                const angleA = Math.atan2(va.dot(upVector), va.dot(refVector));
                const angleB = Math.atan2(vb.dot(upVector), vb.dot(refVector));

                return angleA - angleB;
            });

            // Create shape geometry
            const shape = new THREE.Shape();
            shape.moveTo(vertices[0].x, vertices[0].y);
            for (let i = 1; i < vertices.length; i++) {
                shape.lineTo(vertices[i].x, vertices[i].y);
            }
            shape.lineTo(vertices[0].x, vertices[0].y);

            const geometry = new THREE.ShapeGeometry(shape);
            geometry.rotateFromVector3(normal);

            // Position the geometry correctly
            const plane = new THREE.Plane(normal, n / Math.sqrt(h * h + k * k + l * l));
            geometry.translate(plane.normal.x * plane.constant, plane.normal.y * plane.constant, plane.normal.z * plane.constant);

            return geometry;
        }

        // Function to update the scene based on current controls
        function updateScene() {
            // Clear previous atoms and planes
            atomsGroup.clear();
            planesGroup.clear();

            // Get current lattice type
            const latticeType = document.querySelector('#lattice-controls button.active').id.split('-')[1];

            // Get Miller indices
            const h = parseInt(document.getElementById('input-h').value) || 0;
            const k = parseInt(document.getElementById('input-k').value) || 0;
            const l = parseInt(document.getElementById('input-l').value) || 0;

            // Create atoms based on lattice type
            const atomPositions = [];

            // Always add corner atoms
            for (let x = -0.5; x <= 0.5; x += 1) {
                for (let y = -0.5; y <= 0.5; y += 1) {
                    for (let z = -0.5; z <= 0.5; z += 1) {
                        atomPositions.push(new THREE.Vector3(x, y, z));
                    }
                }
            }

            // Add additional atoms based on lattice type
            if (latticeType === 'bcc') {
                atomPositions.push(new THREE.Vector3(0, 0, 0));
            } else if (latticeType === 'fcc') {
                atomPositions.push(new THREE.Vector3(0.5, 0, 0));
                atomPositions.push(new THREE.Vector3(-0.5, 0, 0));
                atomPositions.push(new THREE.Vector3(0, 0.5, 0));
                atomPositions.push(new THREE.Vector3(0, -0.5, 0));
                atomPositions.push(new THREE.Vector3(0, 0, 0.5));
                atomPositions.push(new THREE.Vector3(0, 0, -0.5));
            }

            // Create atom spheres
            const atomGeometry = new THREE.SphereGeometry(0.1, 32, 32);
            atomPositions.forEach(pos => {
                const atom = new THREE.Mesh(atomGeometry, atomMaterial);
                atom.position.copy(pos);
                atomsGroup.add(atom);
            });

            // Create planes if Miller indices are not all zero
            if (h !== 0 || k !== 0 || l !== 0) {
                const divisor = gcd3(h, k, l);
                const hNorm = h / divisor;
                const kNorm = k / divisor;
                const lNorm = l / divisor;

                // Calculate plane equation values at cube vertices
                const cubeVertices = [
                    new THREE.Vector3(-0.5, -0.5, -0.5),
                    new THREE.Vector3(0.5, -0.5, -0.5),
                    new THREE.Vector3(0.5, 0.5, -0.5),
                    new THREE.Vector3(-0.5, 0.5, -0.5),
                    new THREE.Vector3(-0.5, -0.5, 0.5),
                    new THREE.Vector3(0.5, -0.5, 0.5),
                    new THREE.Vector3(0.5, 0.5, 0.5),
                    new THREE.Vector3(-0.5, 0.5, 0.5)
                ];

                const planeValues = cubeVertices.map(v => hNorm * v.x + kNorm * v.y + lNorm * v.z);
                const minN = Math.floor(Math.min(...planeValues));
                const maxN = Math.ceil(Math.max(...planeValues));

                // Create planes for each integer n in the range
                for (let n = minN; n <= maxN; n++) {
                    const geometry = createPlaneGeometry(hNorm, kNorm, lNorm, n);
                    if (geometry) {
                        const plane = new THREE.Mesh(geometry, planeMaterial);
                        planesGroup.add(plane);
                    }
                }
            }
        }

        // Initialize the scene
        updateScene();

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            orbitControls.update();
            renderer.render(scene, camera);
        }
        animate();

        // Event listeners for controls
        document.getElementById('btn-sc').addEventListener('click', function() {
            document.querySelectorAll('#lattice-controls button').forEach(btn => btn.classList.remove('active'));
            this.classList.add('active');
            updateScene();
        });

        document.getElementById('btn-bcc').addEventListener('click', function() {
            document.querySelectorAll('#lattice-controls button').forEach(btn => btn.classList.remove('active'));
            this.classList.add('active');
            updateScene();
        });

        document.getElementById('btn-fcc').addEventListener('click', function() {
            document.querySelectorAll('#lattice-controls button').forEach(btn => btn.classList.remove('active'));
            this.classList.add('active');
            updateScene();
        });

        document.getElementById('input-h').addEventListener('change', updateScene);
        document.getElementById('input-k').addEventListener('change', updateScene);
        document.getElementById('input-l').addEventListener('change', updateScene);

        document.getElementById('slider-opacity').addEventListener('input', function() {
            const opacity = parseFloat(this.value);
            planeMaterial.opacity = opacity;
            planesGroup.children.forEach(plane => {
                plane.material.opacity = opacity;
            });
        });

        // Handle window resize
        window.addEventListener('resize', function() {
            camera.aspect = 800 / 600;
            camera.updateProjectionMatrix();
            renderer.setSize(800, 600);
        });
    </script>
</body>
