        function draw2DPlot(N) {
            const canvas = document.getElementById('canvas-2d');
            const ctx = canvas.getContext('2d');
            const points = getPointsForN(N);

            // Clear canvas
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // Calculate min and max distances
            let minDistance = Infinity;
            const distances = [];

            for (let i = 0; i < points.length; i++) {
                for (let j = i + 1; j < points.length; j++) {
                    const [x1, y1, z1] = points[i];
                    const [x2, y2, z2] = points[j];
                    const dx = x1 - x2;
                    const dy = y1 - y2;
                    const dz = z1 - z2;
                    const distance = Math.sqrt(dx * dx + dy * dy + dz * dz);
                    distances.push(distance);
                    if (distance < minDistance) {
                        minDistance = distance;
                    }
                }
            }

            // Update text display
            document.getElementById('div-2d-text').innerHTML = `
                minimum distance between points = ${minDistance.toFixed(5)}<br>
                maximum distance between points = 2.00000
            `;

            // Draw points
            points.forEach(point => {
