    <script>
        // Global variables for p5 sketches
        let orbitSketch;
        let plotSketch;
        let orbitCanvas;
        let plotCanvas;

        // Initialize the application when the page loads
        window.onload = function() {
            // Set up event listeners for sliders
            document.getElementById('slider-angle').addEventListener('input', updateAll);
            document.getElementById('slider-longitude').addEventListener('input', updateAll);
            document.getElementById('slider-eccentricity').addEventListener('input', updateAll);

            // Initialize p5 sketches
            orbitSketch = new p5(createOrbitSketch, 'orbit-canvas');
            plotSketch = new p5(createPlotSketch, 'plot-canvas');

            // Initial update
            updateAll();
        };

        // Update all displays when any slider changes
        function updateAll() {
            // Update displayed values
            document.getElementById('value-angle').textContent = 
                parseFloat(document.getElementById('slider-angle').value).toFixed(2);
            document.getElementById('value-longitude').textContent = 
                parseFloat(document.getElementById('slider-longitude').value).toFixed(2);
            document.getElementById('value-eccentricity').textContent = 
                parseFloat(document.getElementById('slider-eccentricity').value).toFixed(6);

            // Redraw visualizations
            if (orbitSketch) orbitSketch.redraw();
            if (plotSketch) plotSketch.redraw();
        }

        // Orbit Sketch using p5.js
        function createOrbitSketch(p) {
            p.setup = function() {
                orbitCanvas = p.createCanvas(800, 300);
                p.noLoop(); // We'll manually trigger redraws
            };

            p.draw = function() {
                // Get current parameters
                const e = parseFloat(document.getElementById('slider-eccentricity').value);
                const ω = parseFloat(document.getElementById('slider-longitude').value);

                // Clear canvas
                p.background(240);

                // Set drawing style
                p.stroke(160, 82, 45);
                p.noFill();
                p.strokeWeight(2);

                // Center of canvas
                const centerX = p.width / 2;
                const centerY = p.height / 2;

                // Calculate ellipse dimensions
                const a = p.width * 0.2; // semi-major axis (fixed)
                const b = a * Math.sqrt(1 - e * e); // semi-minor axis

                // Draw ellipse with rotation
                p.push();
                p.translate(centerX, centerY);
                p.rotate(-ω * Math.PI / 180); // negative because y-axis is flipped in p5

                // Draw axes
                p.stroke(200);
                p.line(-a, 0, a, 0);
                p.line(0, -b, 0, b);

                // Draw ellipse
                p.ellipse(0, 0, a * 2, b * 2);

                // Draw periapsis marker
                p.fill(160, 82, 45);
                p.noStroke();
                p.ellipse(a, 0, 8, 8);

                p.pop();

