                p.fill(0);
                p.noStroke();
                p.textSize(12);
                p.textAlign(p.CENTER);
                p.text("Periapsis", centerX + a * Math.cos(-ω * Math.PI / 180), 
                                      centerY + a * Math.sin(-ω * Math.PI / 180) * (b/a) - 10);
                p.text("ω = " + ω.toFixed(2) + "°", p.width - 50, 20);
                p.text("e = " + e.toFixed(6), p.width - 50, 40);
            };
        }

        // Equation of Time Plot using p5.js
        function createPlotSketch(p) {
            let points = [];
            let yMin = -10;
            let yMax = 10;

            p.setup = function() {
                plotCanvas = p.createCanvas(800, 400);
                p.noLoop(); // We'll manually trigger redraws
            };

            p.draw = function() {
                // Get current parameters
                const ε = parseFloat(document.getElementById('slider-angle').value) * Math.PI / 180;
                const e = parseFloat(document.getElementById('slider-eccentricity').value);
                const ω = parseFloat(document.getElementById('slider-longitude').value) * Math.PI / 180;

                // Clear canvas
                p.background(255);

                // Calculate points for the curve
                points = [];
                let prevEOT = 0;
                yMin = Infinity;
                yMax = -Infinity;

                const steps = 200;
                for (let i = 0; i <= steps; i++) {
                    const M = (i / steps) * 2 * Math.PI;

                    // Solve Kepler's equation for E (Eccentric Anomaly)
                    let E = M; // Initial guess
                    for (let iter = 0; iter < 10; iter++) {
                        E = E - (E - e * Math.sin(E) - M) / (1 - e * Math.cos(E));
                    }

                    // Calculate True Anomaly (ν)
                    const ν = 2 * Math.atan2(
                        Math.sqrt(1 + e) * Math.sin(E / 2),
                        Math.sqrt(1 - e) * Math.cos(E / 2)
                    );

                    // Calculate True Longitude (λ)
                    const λ = ν + ω;

                    // Calculate Right Ascension (α)
                    const α = Math.atan2(
                        Math.cos(ε) * Math.sin(λ),
                        Math.cos(λ)
                    );

                    // Calculate Equation of Time in radians
                    let EOT_rad = M - α;

                    // Phase unwrapping
                    if (i > 0) {
                        const diff = EOT_rad - prevEOT;
                        if (diff > Math.PI) EOT_rad -= 2 * Math.PI;
                        if (diff < -Math.PI) EOT_rad += 2 * Math.PI;
                    }
                    prevEOT = EOT_rad;

                    // Convert to minutes
                    const EOT_minutes = EOT_rad * (720 / Math.PI);

                    // Update min/max for scaling
                    if (EOT_minutes < yMin) yMin = EOT_minutes;
                    if (EOT_minutes > yMax) yMax = EOT_minutes;

                    points.push({x: M, y: EOT_minutes});
                }

                // Add some padding to the y-axis
                const yPadding = Math.max(5, (yMax - yMin) * 0.1);
                yMin -= yPadding;
                yMax += yPadding;

                // Draw axes and grid
                p.stroke(200);
                p.strokeWeight(1);

                // Horizontal grid lines
                for (let y = Math.ceil(yMin / 5) * 5; y <= Math.floor(yMax / 5) * 5; y += 5) {
                    const yPos = p.map(y, yMin, yMax, p.height - 50, 50);
                    p.line(50, yPos, p.width - 50, yPos);

                    // Y-axis labels
                    p.fill(0);
                    p.noStroke();
                    p.textSize(12);
                    p.textAlign(p.RIGHT, p.CENTER);
                    p.text(y, 45, yPos);
                }

                // Vertical grid lines
                for (let x = 0; x <= 6; x++) {
                    const xPos = p.map(x, 0, 6, 50, p.width - 50);
                    p.line(xPos, 50, xPos, p.height - 50);

                    // X-axis labels
                    p.fill(0);
                    p.noStroke();
                    p.textSize(12);
                    p.textAlign(p.CENTER, p.TOP);
                    p.text(x, xPos, p.height - 45);
                }

                // Axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(50, 50, 50, p.height - 50); // Y-axis
                p.line(50, p.height - 50, p.width - 50, p.height - 50); // X-axis

                // Axis labels
                p.fill(0);
                p.noStroke();
                p.textSize(14);
                p.textAlign(p.CENTER, p.TOP);
                p.text("Mean Anomaly (M, radians)", p.width / 2, p.height - 30);

                p.textAlign(p.CENTER, p.CENTER);
                p.push();
                p.translate(30, p.height / 2);
                p.rotate(-Math.PI / 2);
                p.text("Equation of Time (minutes)", 0, 0);
                p.pop();

                // Draw the curve
                p.stroke(255, 140, 0);
                p.strokeWeight(2);
                p.noFill();

                p.beginShape();
                for (let i = 0; i < points.length; i++) {
                    const x = p.map(points[i].x, 0, 2 * Math.PI, 50, p.width - 50);
                    const y = p.map(points[i].y, yMin, yMax, p.height - 50, 50);

                    // Only connect points if they're not too far apart (to handle discontinuities)
                    if (i === 0 || Math.abs(points[i].y - points[i-1].y) < 100) {
                        p.vertex(x, y);
                    } else {
                        p.endShape();
                        p.beginShape();
                    }
                }
                p.endShape();

                // Draw parameter info
                p.fill(0);
                p.noStroke();
                p.textSize(12);
                p.textAlign(p.LEFT, p.TOP);
                p.text("ε = " + (ε * 180 / Math.PI).toFixed(2) + "°", 60, 60);
                p.text("ω = " + (ω * 180 / Math.PI).toFixed(2) + "°", 60, 80);
                p.text("e = " + e.toFixed(6), 60, 100);
            };
        }
    </script>
</body>
</html>
