                    <input type="checkbox" id="check-verification" checked> verification
                </label>
            </div>

            <div class="control-group">
                <label for="slider-plot-range">plot range: <span id="plot-range-value" class="value-display">1</span></label>
                <input type="range" id="slider-plot-range" min="0.5" max="2" step="0.01" value="1">
            </div>
        </div>

        <div class="visualization">
            <div id="canvas-container"></div>
        </div>
    </div>

    <script>
        let sketch = function(p) {
            // Parameters from controls
            let c, h_C, delta, plotRange, currentStep, showVerification;

            // Calculated points
            let F, C, D, K, M, B, A, E;
            let radius;

            // Base scale factor
            const baseScale = 50;

            p.setup = function() {
                let container = document.getElementById('canvas-container');
                let canvas = p.createCanvas(800, 600);
                canvas.parent('canvas-container');
                p.noLoop();

                // Initialize controls
                updateParameters();
                setupEventListeners();
            };

            function updateParameters() {
                c = parseFloat(document.getElementById('slider-c').value);
                h_C = parseFloat(document.getElementById('slider-h_C').value);
                delta = parseFloat(document.getElementById('slider-delta').value);
                plotRange = parseFloat(document.getElementById('slider-plot-range').value);
                currentStep = parseInt(document.querySelector('input[name="steps"]:checked').value);
                showVerification = document.getElementById('check-verification').checked;

                // Update value displays
                document.getElementById('c-value').textContent = c.toFixed(1);
                document.getElementById('h_C-value').textContent = h_C.toFixed(1);
                document.getElementById('delta-value').textContent = delta.toFixed(2);
                document.getElementById('plot-range-value').textContent = plotRange.toFixed(2);

                // Calculate geometry
                calculateGeometry();
            }

            function setupEventListeners() {
                // Sliders
                document.getElementById('slider-c').addEventListener('input', function() {
                    updateParameters();
                    redrawScene();
                });
                document.getElementById('slider-h_C').addEventListener('input', function() {
                    updateParameters();
                    redrawScene();
                });
                document.getElementById('slider-delta').addEventListener('input', function() {
                    updateParameters();
                    redrawScene();
                });
                document.getElementById('slider-plot-range').addEventListener('input', function() {
                    updateParameters();
                    redrawScene();
                });

                // Radio buttons
                document.querySelectorAll('input[name="steps"]').forEach(radio => {
                    radio.addEventListener('change', function() {
                        updateParameters();
                        redrawScene();
                    });
                });

                // Checkbox
                document.getElementById('check-verification').addEventListener('change', function() {
                    updateParameters();
                    redrawScene();
                });
            }

            function calculateGeometry() {
                // Step 1: Basic points
                F = { x: 0, y: 0 };
                C = { x: 0, y: h_C };
                D = { x: c / 2, y: 0 };
                K = { x: c, y: 0 };

                // Step 2: Find point M
                // Equation of perpendicular bisector of CD: c*x_M - 2*h_C*y_M + h_C^2 - c^2/4 = 0
                // Solve for x_M in terms of y_M
                // x_M = (2*h_C*y_M - h_C^2 + c^2/4) / c

                // Equation from angle condition: tan(δ) = (c/2 * y_M) / (y_M^2 + x_M^2 - 3c/2*x_M + c^2/2)
                // Substitute x_M from above
                // This becomes a quadratic in y_M
                const tanDelta = Math.tan(delta);
                const a = 1 + (4 * h_C * h_C) / (c * c);
                const b = (-2 * h_C / c) * (h_C * h_C / c - c / 4) - (3 * h_C);
                const c_term = (h_C * h_C / c - c / 4) * (h_C * h_C / c - c / 4) + c * c / 2 - (3 * h_C / 2) * (h_C * h_C / c - c / 4);

                const discriminant = b * b - 4 * a * c_term;
                let y_M = (-b - Math.sqrt(discriminant)) / (2 * a); // Take the negative root
                let x_M = (2 * h_C * y_M - h_C * h_C + c * c / 4) / c;

                M = { x: x_M, y: y_M };

                // Radius of circle κ
                radius = Math.sqrt((M.x - C.x) * (M.x - C.x) + (M.y - C.y) * (M.y - C.y));

                // Step 3: Find point B
                // Intersection of circle κ with x-axis (y=0)
                // (x - M.x)^2 + (0 - M.y)^2 = radius^2
                // x = M.x ± sqrt(radius^2 - M.y^2)
                // We take the solution that's not D (x = c/2)
                const x_B = 2 * M.x - c / 2;
                B = { x: x_B, y: 0 };

                // Step 4: Find point A
                A = { x: x_B - c, y: 0 };

                // Verification: point E
                if (showVerification) {
                    E = { x: c - x_B, y: 0 };
                }
            }

            function redrawScene() {
                p.clear();
                p.background(224); // Light grey background

                // Set up coordinate system
                p.push();
                p.translate(p.width / 2, p.height * 0.6);
                p.scale(1, -1); // Flip Y-axis
                const scale = baseScale * plotRange;
                p.scale(scale, scale);

                // Draw elements based on current step
                if (currentStep >= 1) {
                    drawStep1();
                }
                if (currentStep >= 2) {
                    drawStep2();
                }
                if (currentStep >= 3) {
                    drawStep3();
                }
                if (currentStep >= 4) {
                    drawStep4();
                }
                if (currentStep >= 5) {
                    drawStep5();
                }

                // Draw verification if enabled
                if (showVerification) {
                    drawVerification();
                }

                p.pop();
            }

            function drawStep1() {
                // Line λ (x-axis)
                p.stroke(0);
                p.strokeWeight(1 / plotRange);
                p.line(-p.width / (2 * scale), 0, p.width / (2 * scale), 0);

                // Points F, C, D
                drawPoint(F, 'F');
                drawPoint(C, 'C');
                drawPoint(D, 'D');

                // Line segment FC
                p.stroke(0);
                p.strokeWeight(1 / plotRange);
                p.line(F.x, F.y, C.x, C.y);
            }

            function drawStep2() {
                // Point K
                drawPoint(K, 'K');

                // Dashed lines CD and DK
                p.drawingContext.setLineDash([5, 5]);
                p.stroke(0);
                p.strokeWeight(1 / plotRange);
                p.line(C.x, C.y, D.x, D.y);
                p.line(D.x, D.y, K.x, K.y);
                p.drawingContext.setLineDash([]);

                // Point M
                drawPoint(M, 'M');

                // Dashed lines MC, MD, MK
                p.drawingContext.setLineDash([5, 5]);
                p.stroke(0);
                p.strokeWeight(1 / plotRange);
                p.line(M.x, M.y, C.x, C.y);
                p.line(M.x, M.y, D.x, D.y);
                p.line(M.x, M.y, K.x, K.y);
                p.drawingContext.setLineDash([]);

                // Circle κ
                p.noFill();
                p.stroke(0);
                p.strokeWeight(1 / plotRange);
                p.ellipse(M.x, M.y, radius * 2, radius * 2);
            }

            function drawStep3() {
                // Point B
                drawPoint(B, 'B');
            }

            function drawStep4() {
                // Point A
                drawPoint(A, 'A');
            }

            function drawStep5() {
                // Triangle ABC with thick lines
                p.stroke(0);
                p.strokeWeight(3 / plotRange);
                p.line(A.x, A.y, B.x, B.y);
                p.line(B.x, B.y, C.x, C.y);
                p.line(C.x, C.y, A.x, A.y);
            }

            function drawVerification() {
                if (!E) return;

                // Point E
                drawPoint(E, 'E');

                // Dashed lines CE and KB
                p.drawingContext.setLineDash([5, 5]);
                p.stroke(0);
                p.strokeWeight(1 / plotRange);
                p.line(C.x, C.y, E.x, E.y);
                p.line(K.x, K.y, B.x, B.y);
                p.drawingContext.setLineDash([]);
            }

            function drawPoint(point, label) {
                // Draw point
                p.fill(0);
                p.noStroke();
                p.ellipse(point.x, point.y, 5 / (scale * plotRange));

                // Draw label
                p.push();
                p.scale(1, -1); // Temporarily unflip Y-axis for text
                p.textSize(12 / (scale * plotRange));
                p.textAlign(p.CENTER, p.CENTER);
                p.fill(0);
                p.noStroke();
                p.text(label, point.x, -point.y + 15 / (scale * plotRange));
                p.pop();
            }

            // Helper to get current scale
            function get scale() {
                return baseScale * plotRange;
            }
        };

        new p5(sketch);
    </script>
</body>
</html>
