                red: [],
                blue: []
            }
        };

        // Calculate initial data (z=0)
        function calculateInitialData() {
            plotData.intensity.red = t_values.map(t => {
                return (t < -15 || t > 170) ? 0 : (t > 15 ? 1 : 0.5 * (1 + Math.tanh(t/4)));
            });

            plotData.population.red = t_values.map(t => {
                return (t < -15) ? 0 : 4.5 * (1 - Math.exp(-(t+15)/50));
            });

            plotData.coherence.red = t_values.map(t => {
                return (t < -15) ? 0 : 2.2 * (1 - Math.exp(-(t+15)/50));
            });

            // At z=0, blue lines are same as red lines
            plotData.intensity.blue = [...plotData.intensity.red];
            plotData.population.blue = [...plotData.population.red];
            plotData.coherence.blue = [...plotData.coherence.red];
        }

        // Update blue data based on current z value
        function updateBlueData(z) {
            const t_delay = z / 0.3;

            // Intensity
            plotData.intensity.blue = t_values.map(t => {
                const t_prime = t - t_delay;
                const I_base = (t_prime < -15) ? 0 : (t_prime > 15 ? 1 : 0.5 * (1 + Math.tanh(t_prime/4)));
                const envelope = (t_prime > 0) ? Math.exp(-0.035 * z * t_prime / 32) : 1;
                return (t > 170 + t_delay) ? 0 : I_base * envelope;
            });

            // Population
            const scaling_factor_p = 1 - 0.45 * (z/32);
            plotData.population.blue = t_values.map(t => {
                const t_prime = t - t_delay;
                return (t > 170 + t_delay) ? 0 : scaling_factor_p * ((t_prime < -15) ? 0 : 4.5 * (1 - Math.exp(-(t_prime+15)/50)));
