            });

            // Population plot
            Plotly.newPlot('plot-population', [
                {
                    x: t_values,
                    y: plotData.population.red,
                    name: 'z = 0 meters',
                    mode: 'lines',
                    line: { color: 'red', width: 2 }
                },
                {
                    x: t_values,
                    y: plotData.population.blue,
                    name: 'z = 0. meters',
                    mode: 'lines',
                    line: { color: 'blue', width: 2, dash: 'dash' }
                }
            ], {
                ...commonLayout,
                yaxis: {
                    title: 'excited-state population × 10<sup>4</sup>',
