        <div id="plot-container"></div>
    </div>

    <!-- CDN for Plotly.js -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>

    <script>
        // Constants
        const C_A0 = 10;        // mmol/dm³, initial concentration of A
        const R = 1.987;        // cal/mol K, ideal gas constant
        const E_f = 25000;      // cal/mol, forward activation energy
        const ΔH_rxn = -200000; // cal/mol, enthalpy of reaction
        const E_r = E_f - ΔH_rxn; // cal/mol, reverse activation energy
        const k_f0 = 1e15;      // 1/s, forward pre-exponential factor
        const rhoCp = 20000;    // cal/(dm³ K), product of density and heat capacity

        // Initialize plot
        let plot = document.getElementById('plot-container');
        let layout = {
            title: 'Steady-State Operating Points',
            xaxis: {
                title: 'temperature (K)',
                range: [250, 400],
                gridcolor: '#eee'
            },
            yaxis: {
                title: 'product concentration (mmol/dm³)',
                range: [0, 12],
                gridcolor: '#eee'
            },
            showlegend: false,
            annotations: [
                {
                    x: 360,
                    y: 0,
                    text: 'mass balance',
                    showarrow: false,
                    font: {
                        color: '#008000',
                        size: 12
                    }
                },
                {
                    x: 280,
                    y: 0,
                    text: 'energy balance',
                    showarrow: false,
                    font: {
                        color: '#0000FF',
                        size: 12
                    }
                }
            ],
            margin: {t: 40, b: 60, l: 60, r: 40}
        };

        // Initial data with empty arrays (will be populated by updatePlot)
        let data = [
            {
                x: [],
                y: [],
                type: 'scatter',
                mode: 'lines',
                line: {color: '#008000'},
                name: 'Mass Balance'
            },
            {
                x: [],
                y: [],
                type: 'scatter',
                mode: 'lines',
                line: {color: '#0000FF'},
                name: 'Energy Balance'
            }
        ];

        // Create initial empty plot
        Plotly.newPlot(plot, data, layout);

        // Function to update the plot based on slider values
        function updatePlot() {
            // Get slider values
            const UA = parseFloat(document.getElementById('slider-ua').value);
            const k_r0_slider = parseFloat(document.getElementById('slider-kr0').value);
            const T_f = parseFloat(document.getElementById('slider-tf').value);
            const tau = parseFloat(document.getElementById('slider-tau').value);

            // Calculate actual k_r0 (multiply by 1e12)
            const k_r0 = k_r0_slider * 1e12;

            // Generate temperature range
            const T = [];
            for (let temp = 250; temp <= 400; temp += 1) {
                T.push(temp);
            }

            // Calculate mass balance curve (green)
            const C_B_mass = T.map(temp => {
                const k_f = k_f0 * Math.exp(-E_f / (R * temp));
                const k_r = k_r0 * Math.exp(-E_r / (R * temp));
                return (tau * k_f * C_A0) / (1 + tau * (k_f + k_r));
            });

            // Calculate energy balance line (blue)
            const C_B_energy = T.map(temp => {
                return (rhoCp * (temp - T_f) + UA * tau * (temp - T_f)) / (-ΔH_rxn);
            });

            // Update plot data
            data[0].x = T;
            data[0].y = C_B_mass;
            data[1].x = T;
            data[1].y = C_B_energy;

            // Update annotations positions
            layout.annotations[0].y = C_B_mass[C_B_mass.length - 1];
            layout.annotations[1].y = C_B_energy[0];

            // Update the plot
            Plotly.react(plot, data, layout);
        }

        // Function to format kr0 value in scientific notation
        function formatScientific(value) {
            return value.toFixed(1) + ' × 10¹²';
        }

        // Update value displays and plot when sliders change
        document.getElementById('slider-ua').addEventListener('input', function() {
            document.getElementById('ua-value').textContent = this.value;
            updatePlot();
        });

        document.getElementById('slider-kr0').addEventListener('input', function() {
            document.getElementById('kr0-value').textContent = formatScientific(parseFloat(this.value));
            updatePlot();
        });

        document.getElementById('slider-tf').addEventListener('input', function() {
            document.getElementById('tf-value').textContent = this.value;
            updatePlot();
        });

        document.getElementById('slider-tau').addEventListener('input', function() {
            document.getElementById('tau-value').textContent = this.value;
            updatePlot();
        });

        // Initialize plot with default values
        updatePlot();
    </script>
</body>
</html>
