                        <label for="radio-fixed-centers">fixed centers</label>
                    </div>
                </div>
            </div>
            <div>
                <span>envelope to show</span>
                <div class="radio-group">
                    <div>
                        <input type="radio" name="envelope-type" id="radio-inner" value="inner" checked>
                        <label for="radio-inner">inner</label>
                    </div>
                    <div>
                        <input type="radio" name="envelope-type" id="radio-outer" value="outer">
                        <label for="radio-outer">outer</label>
                    </div>
                    <div>
                        <input type="radio" name="envelope-type" id="radio-both" value="both">
                        <label for="radio-both">both</label>
                    </div>
                </div>
            </div>
            <div>
                <label for="slider-rotation">rotation angle</label>
                <input type="range" id="slider-rotation" min="0" max="6.28318" step="0.0001" value="0">
                <span id="label-rotation">0</span>
            </div>
        </div>

        <!-- Visualization Canvas -->
        <div id="canvas-container"></div>
    </div>

    <!-- CDN Libraries -->
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>

    <script>
        let sketch = function(p) {
            let k = 0.14; // eccentricity ratio
            let phi = 0; // rotation angle
            let refFrame = 'epitrochoid'; // reference frame
            let envelopeType = 'inner'; // envelope to show

            // UI elements
            let sliderEccentricity, sliderRotation;
            let labelEccentricity, labelRotation;

            p.setup = function() {
                let canvas = p.createCanvas(p.select('#canvas-container').width, p.select('#canvas-container').height);
                canvas.parent('canvas-container');

                // Setup UI event listeners
                sliderEccentricity = p.select('#slider-eccentricity');
                sliderRotation = p.select('#slider-rotation');
                labelEccentricity = p.select('#label-eccentricity');
                labelRotation = p.select('#label-rotation');

                sliderEccentricity.input(function() {
                    k = parseFloat(this.value());
                    labelEccentricity.html(k.toFixed(4));
                });

                sliderRotation.input(function() {
                    phi = parseFloat(this.value());
                    labelRotation.html(phi.toFixed(4));
                });

                p.selectAll('input[name="ref-frame"]').forEach(radio => {
                    radio.input(function() {
                        refFrame = this.value();
                    });
                });

                p.selectAll('input[name="envelope-type"]').forEach(radio => {
                    radio.input(function() {
                        envelopeType = this.value();
                    });
                });

                p.angleMode(p.RADIANS);
            };

            p.draw = function() {
                p.background(255);
                p.translate(p.width/2, p.height/2);

                // Calculate scale factor to fit curves in view
                const scale = p.min(p.width, p.height) * 0.4;

                // Draw coordinate axes
                drawAxes(scale);

                // Calculate transformations based on reference frame
                const phi_shaft = 2 * phi;
                const theta = -phi_shaft / 3;
                const Cx = k * p.cos(phi_shaft);
                const Cy = k * p.sin(phi_shaft);

                // Draw curves based on reference frame
                if (refFrame === 'epitrochoid') {
                    // Draw epitrochoid stationary
                    drawEpitrochoid(0, 0, 0, scale, false);

                    // Apply transformation to envelope
                    p.push();
                    p.translate(Cx * scale, Cy * scale);
                    p.rotate(theta);
                    drawEnvelope(0, 0, scale);
                    p.pop();
                } 
                else if (refFrame === 'envelope') {
                    // Draw envelope stationary
                    drawEnvelope(0, 0, scale);

                    // Apply inverse transformation to epitrochoid
                    p.push();
                    p.rotate(-theta);
                    p.translate(-Cx * scale, -Cy * scale);
                    drawEpitrochoid(0, 0, 0, scale, false);
                    p.pop();
                } 
                else { // fixed centers
                    // Draw epitrochoid stationary
                    drawEpitrochoid(0, 0, 0, scale, false);

                    // Draw envelope with its motion
                    p.push();
                    p.translate(Cx * scale, Cy * scale);
                    p.rotate(theta);
                    drawEnvelope(0, 0, scale);
                    p.pop();
                }
            };

            function drawAxes(scale) {
                p.stroke(150);
                p.strokeWeight(1);

                // X and Y axes
                p.line(-p.width/2, 0, p.width/2, 0);
                p.line(0, -p.height/2, 0, p.height/2);

                // Tick marks and labels
                const tickSize = 5;
                const step = 0.5;
                const maxTicks = p.floor(p.max(p.width, p.height) / (scale * step));

                for (let i = -maxTicks; i <= maxTicks; i += step) {
                    if (i !== 0) {
                        // X axis ticks
                        p.line(i * scale, -tickSize, i * scale, tickSize);
                        // Y axis ticks
                        p.line(-tickSize, i * scale, tickSize, i * scale);

                        // Labels every integer
                        if (i % 1 === 0) {
                            p.textSize(12);
                            p.fill(100);
                            p.noStroke();
                            p.textAlign(p.CENTER, p.TOP);
                            p.text(i, i * scale, tickSize + 5);
                            p.textAlign(p.RIGHT, p.CENTER);
                            p.text(i, -tickSize - 5, i * scale);
                        }
                    }
                }
            }

            function drawEpitrochoid(x, y, rotation, scale, applyTransform = true) {
                p.push();
                if (applyTransform) {
                    p.translate(x * scale, y * scale);
                    p.rotate(rotation);
                }

                p.stroke(50, 100, 200);
                p.strokeWeight(2);
                p.noFill();

                p.beginShape();
                const steps = 200;
                for (let i = 0; i <= steps; i++) {
                    const t = p.map(i, 0, steps, 0, p.TWO_PI);
                    const xt = p.cos(t) - k * p.cos(3 * t);
                    const yt = p.sin(t) - k * p.sin(3 * t);
                    p.vertex(xt * scale, yt * scale);
                }
                p.endShape(p.CLOSE);

                p.pop();
            }

            function drawEnvelope(x, y, scale) {
                const R = 1; // rotor generating radius

                if (envelopeType === 'inner' || envelopeType === 'both') {
                    p.stroke(230, 150, 0);
                    p.strokeWeight(2);
                    p.noFill();

                    p.beginShape();
                    const steps = 200;
                    for (let i = 0; i <= steps; i++) {
                        const t = p.map(i, 0, steps, 0, p.TWO_PI);
                        const xt = (2/3)*R*p.cos(t) + k*p.cos(2*t);
                        const yt = (2/3)*R*p.sin(t) - k*p.sin(2*t);
                        p.vertex(xt * scale, yt * scale);
                    }
                    p.endShape(p.CLOSE);
                }

                if (envelopeType === 'outer' || envelopeType === 'both') {
                    p.stroke(200, 100, 0);
                    p.strokeWeight(2);
                    p.noFill();

                    p.beginShape();
                    const steps = 200;
                    for (let i = 0; i <= steps; i++) {
                        const t = p.map(i, 0, steps, 0, p.TWO_PI);
                        const xt = (2/3)*R*p.cos(t) - k*p.cos(2*t);
                        const yt = (2/3)*R*p.sin(t) + k*p.sin(2*t);
                        p.vertex(xt * scale, yt * scale);
                    }
                    p.endShape(p.CLOSE);
                }
            }

            p.windowResized = function() {
                p.resizeCanvas(p.select('#canvas-container').width, p.select('#canvas-container').height);
            };
        };

        new p5(sketch);
    </script>
</body>
</html>
