            cAxis: document.getElementById('c-axis-value-display'),
            phimax: document.getElementById('phimax-display'),
            range: document.getElementById('range-display')
        };

        const plotContainer = document.getElementById('plot-container');

        // Initialize plot
        let plotData = [];
        let plotLayout = {
            xaxis: { title: 'ϕ (volume fraction)', range: [0, parseFloat(sliders.phiAxis.value)] },
            yaxis: { title: 'C (kg/m³)', range: [0, parseFloat(sliders.cAxis.value)] },
            shapes: [],
            showlegend: false,
            margin: { t: 30, r: 30, b: 60, l: 60 }
        };

        // Initialize the plot
        Plotly.newPlot(plotContainer, plotData, plotLayout);

        // Update all displays with current values
        function updateDisplays() {
            displays.phi.textContent = sliders.phi.value;
            displays.gammaSat.textContent = sliders.gammaSat.value;
            displays.r.textContent = sliders.r.value;
            displays.rpe.textContent = sliders.rpe.value;
            displays.mw.textContent = sliders.mw.value;
            displays.phiAxis.textContent = sliders.phiAxis.value;
            displays.cAxis.textContent = sliders.cAxis.value;
        }

        // Calculate the functions
        function calculateFunctions(phi) {
            const gamma_sat = parseFloat(sliders.gammaSat.value);
            const r = parseFloat(sliders.r.value);
            const r_pe = parseFloat(sliders.rpe.value);
            const mw = parseFloat(sliders.mw.value);

            const A = (3 * gamma_sat / r) * C_UNIT_CONVERSION;
            const B = K_DEPLETION * mw / (r_pe ** 3);

            const y1 = A * phi;
            const y2 = y1 / (1 - phi);
            const y3 = A * phi + B / phi;

            return { y1, y2, y3, A, B };
        }

        // Calculate phi_max
        function calculatePhiMax(A, B) {
            return Math.sqrt(B / A);
        }

        // Generate data points for plotting
        function generatePlotData() {
            const phiMax = parseFloat(sliders.phiAxis.value);
            const steps = 100;
            const phiValues = Array.from({ length: steps + 1 }, (_, i) => i * phiMax / steps);

            const { A, B } = calculateFunctions(0); // Just to get A and B

            const phi_max = calculatePhiMax(A, B);

            // Generate data for the curves
            const y1Data = [];
            const y2Data = [];
            const y3Data = [];
            const y2Shaded = [];
            const y3Shaded = [];

            phiValues.forEach(phi => {
                const { y1, y2, y3 } = calculateFunctions(phi);
                y1Data.push({ x: phi, y: y1 });
                y2Data.push({ x: phi, y: y2 });
                y3Data.push({ x: phi, y: y3 });

                if (phi <= phi_max) {
                    y2Shaded.push({ x: phi, y: y2 });
                    y3Shaded.push({ x: phi, y: y3 });
                }
            });

            // Current phi value
            const currentPhi = parseFloat(sliders.phi.value);
            const { y1, y2, y3 } = calculateFunctions(currentPhi);

            // Update the info displays
            displays.phimax.innerHTML = `\\(\\phi_{max} = ${phi_max.toExponential(4)}\\)`;
            displays.range.innerHTML = `Stability range at \\(\\phi = ${currentPhi}\\): \\(${y2.toExponential(4)} \\leq C \\leq ${y3.toExponential(4)}\\)`;

            // Create plot traces
            const traces = [
                // y1 curve
                {
                    x: y1Data.map(p => p.x),
                    y: y1Data.map(p => p.y),
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue' }
                },
                // y2 curve
                {
                    x: y2Data.map(p => p.x),
                    y: y2Data.map(p => p.y),
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'red' }
                },
                // y3 curve
                {
                    x: y3Data.map(p => p.x),
                    y: y3Data.map(p => p.y),
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'green' }
                },
                // Shaded area (y2 to y3 up to phi_max)
                {
                    x: y2Shaded.map(p => p.x).concat(y3Shaded.map(p => p.x).reverse()),
                    y: y2Shaded.map(p => p.y).concat(y3Shaded.map(p => p.y).reverse()),
                    type: 'scatter',
                    mode: 'lines',
                    fill: 'toself',
                    fillcolor: 'rgba(173, 216, 230, 0.5)',
                    line: { width: 0 }
                },
                // Markers at current phi
                {
                    x: [currentPhi, currentPhi, currentPhi],
                    y: [y1, y2, y3],
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: ['blue', 'red', 'green'],
                        size: 8
                    }
                }
            ];

            // Vertical line at current phi
            plotLayout.shapes = [
                {
                    type: 'line',
                    x0: currentPhi,
                    x1: currentPhi,
                    y0: 0,
                    y1: parseFloat(sliders.cAxis.value),
                    line: {
                        color: 'grey',
                        dash: 'dash'
                    }
                }
            ];

            return traces;
        }

        // Update the plot
        function updatePlot() {
            const traces = generatePlotData();
            plotLayout.xaxis.range = [0, parseFloat(sliders.phiAxis.value)];
            plotLayout.yaxis.range = [0, parseFloat(sliders.cAxis.value)];

            Plotly.react(plotContainer, traces, plotLayout);
        }

        // Event listeners for sliders
        function setupEventListeners() {
            // Parameter sliders
            [sliders.gammaSat, sliders.r, sliders.rpe, sliders.mw].forEach(slider => {
                slider.addEventListener('input', () => {
                    updateDisplays();
                    updatePlot();
                });
            });

            // Phi slider
            sliders.phi.addEventListener('input', () => {
                updateDisplays();

                // Get current phi value
                const currentPhi = parseFloat(sliders.phi.value);
                const { y1, y2, y3 } = calculateFunctions(currentPhi);

                // Update the vertical line and markers
                Plotly.relayout(plotContainer, {
                    'shapes[0].x0': currentPhi,
                    'shapes[0].x1': currentPhi
                });

                Plotly.restyle(plotContainer, {
                    x: [[currentPhi, currentPhi, currentPhi]],
                    y: [[y1, y2, y3]]
                }, [4]); // Index of the markers trace

                // Update the range display
                displays.range.innerHTML = `Stability range at \\(\\phi = ${currentPhi}\\): \\(${y2.toExponential(4)} \\leq C \\leq ${y3.toExponential(4)}\\)`;
            });

            // Axis sliders
            sliders.phiAxis.addEventListener('input', () => {
                updateDisplays();
                sliders.phi.max = sliders.phiAxis.value;
                Plotly.relayout(plotContainer, {
                    'xaxis.range[1]': parseFloat(sliders.phiAxis.value)
                });
            });

            sliders.cAxis.addEventListener('input', () => {
                updateDisplays();
                Plotly.relayout(plotContainer, {
                    'yaxis.range[1]': parseFloat(sliders.cAxis.value),
                    'shapes[0].y1': parseFloat(sliders.cAxis.value)
                });
            });
        }

        // Initialize
        updateDisplays();
        setupEventListeners();
        updatePlot();

        // Process MathJax after initial rendering
        window.MathJax = {
            tex: {
                inlineMath: [['\\(', '\\)']],
                displayMath: [['\\[', '\\]']],
                processEscapes: true,
                processEnvironments: true
            },
            options: {
                ignoreHtmlClass: 'tex2jax_ignore',
                processHtmlClass: 'tex2jax_process'
            },
            startup: {
                ready: () => {
                    MathJax.startup.defaultReady();
                    // Re-render MathJax after plot updates
                    const originalUpdate = Plotly.Plots.resize;
                    Plotly.Plots.resize = function(gd) {
                        originalUpdate(gd);
                        MathJax.typesetPromise();
                    };
                }
            }
        };
    </script>
