                <label for="slider-m2">Mass (kg)</label>
                <input type="range" id="slider-m2" min="1" max="10" step="0.01" value="3">
                <span id="label-m2" class="value-display">3.00</span>

                <label for="slider-v2">Speed (m/s)</label>
                <input type="range" id="slider-v2" min="1" max="20" step="0.1" value="5">
                <span id="label-v2" class="value-display">5.0</span>
            </div>

            <div class="control-group">
                <label>Observer velocity (m/s)</label>
                <div id="joystick-container">
                    <div id="joystick-knob"></div>
                </div>
                <div>
                    v₀<sub>x</sub>: <span id="label-v0x" class="value-display">1.10</span>
                    v₀<sub>y</sub>: <span id="label-v0y" class="value-display">1.10</span>
                </div>
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script>
        // Simulation state
        const state = {
            time: -10,
            m1: 5,
            m2: 3,
            v1: 10,
            v2: 5,
            v0x: 1.1,
            v0y: 1.1,

            // Calculated values
            u1_cm: {x: 0, y: 0},
            u2_cm: {x: 0, y: 0},
            v1_cm: {x: 0, y: 0},
            v2_cm: {x: 0, y: 0},

            // Visualization parameters
            canvasWidth: 0,
            canvasHeight: 0,
            scale: 1,
            centerX: 0,
            centerY: 0
        };

        // Initialize p5.js sketch
        let sketch = function(p) {
            let canvas;

            p.setup = function() {
                const container = document.getElementById('canvas-container');
                const width = container.offsetWidth;
                const height = Math.max(container.offsetHeight, 500);
                canvas = p.createCanvas(width, height);
                canvas.parent('canvas-container');

                state.canvasWidth = width;
                state.canvasHeight = height;
                state.centerX = width / 2;
                state.centerY = height / 2;
                state.scale = Math.min(width, height) / 30;

                calculateCMFrame();
                updateLabels();
            };

            p.windowResized = function() {
                const container = document.getElementById('canvas-container');
                const width = container.offsetWidth;
                const height = Math.max(container.offsetHeight, 500);
                p.resizeCanvas(width, height);

                state.canvasWidth = width;
                state.canvasHeight = height;
                state.centerX = width / 2;
                state.centerY = height / 2;
                state.scale = Math.min(width, height) / 30;
            };

            p.draw = function() {
                p.background(255);

                // Draw trajectories
                drawTrajectory(p.color(255, 0, 0, 150), state.u1_cm, state.v1_cm);
                drawTrajectory(p.color(0, 0, 255, 150), state.u2_cm, state.v2_cm);

                // Draw particles
                drawParticle(state.time, state.u1_cm, state.v1_cm, state.m1, p.color(255, 0, 0));
                drawParticle(state.time, state.u2_cm, state.v2_cm, state.m2, p.color(0, 0, 255));

                // Draw observer velocity vector
                drawObserverVector();

                // Draw info text
                drawInfoText();
            };

            function drawTrajectory(color, u_cm, v_cm) {
                p.push();
                p.stroke(color);
                p.strokeWeight(1);
                p.drawingContext.setLineDash([5, 5]);

                // Calculate start and end points in observer frame
                const tStart = -30;
                const tEnd = 30;

                // Pre-collision trajectory (t < 0)
                const x1 = state.centerX + (u_cm.x + state.v0x) * tStart * state.scale;
                const y1 = state.centerY + (u_cm.y + state.v0y) * tStart * state.scale;
                const x2 = state.centerX + (u_cm.x + state.v0x) * 0 * state.scale;
                const y2 = state.centerY + (u_cm.y + state.v0y) * 0 * state.scale;
                p.line(x1, y1, x2, y2);

                // Post-collision trajectory (t > 0)
                const x3 = state.centerX + (v_cm.x + state.v0x) * 0 * state.scale;
                const y3 = state.centerY + (v_cm.y + state.v0y) * 0 * state.scale;
                const x4 = state.centerX + (v_cm.x + state.v0x) * tEnd * state.scale;
                const y4 = state.centerY + (v_cm.y + state.v0y) * tEnd * state.scale;
                p.line(x3, y3, x4, y4);

                p.drawingContext.setLineDash([]);
                p.pop();
            }

            function drawParticle(t, u_cm, v_cm, mass, color) {
                p.push();
                p.fill(color);
                p.noStroke();

                // Calculate position in observer frame
                let x, y;
                if (t < 0) {
                    x = (u_cm.x + state.v0x) * t;
                    y = (u_cm.y + state.v0y) * t;
                } else {
                    x = (v_cm.x + state.v0x) * t;
                    y = (v_cm.y + state.v0y) * t;
                }

                // Scale and center on canvas
                x = state.centerX + x * state.scale;
                y = state.centerY + y * state.scale;

                // Draw particle with radius proportional to sqrt(mass)
                const radius = 5 * Math.sqrt(mass);
                p.circle(x, y, radius * 2);
                p.pop();
            }

            function drawObserverVector() {
                p.push();
                p.stroke(0);
                p.strokeWeight(2);
                p.fill(0);

                // Draw vector showing -v0 (velocity of CM frame relative to observer)
                const startX = state.centerX - 100;
                const startY = state.centerY - 100;
                const endX = startX - state.v0x * state.scale;
                const endY = startY - state.v0y * state.scale;

                // Draw line
                p.line(startX, startY, endX, endY);

                // Draw arrowhead
                const angle = p.atan2(endY - startY, endX - startX);
                p.translate(endX, endY);
                p.rotate(angle);
                p.triangle(0, 0, -10, -5, -10, 5);

                p.pop();
            }

            function drawInfoText() {
                p.push();
                p.fill(0);
                p.noStroke();
                p.textSize(14);

                // Calculate values for display
                const v0 = Math.sqrt(state.v0x*state.v0x + state.v0y*state.v0y);
                const v1 = Math.sqrt(
                    Math.pow(state.u1_cm.x + state.v0x, 2) + 
                    Math.pow(state.u1_cm.y + state.v0y, 2)
                );
                const v2 = Math.sqrt(
                    Math.pow(state.u2_cm.x + state.v0x, 2) + 
                    Math.pow(state.u2_cm.y + state.v0y, 2)
                );
                const energy = 0.5 * state.m1 * v1*v1 + 0.5 * state.m2 * v2*v2;

                // Display text
                const x = 20;
                let y = 30;
                p.text(`v₀ = ${v0.toFixed(2)} m/s`, x, y); y += 20;
                p.text(`v₁ = ${v1.toFixed(2)} m/s`, x, y); y += 20;
                p.text(`v₂ = ${v2.toFixed(2)} m/s`, x, y); y += 20;
                p.text(`KE = ${energy.toFixed(2)} J`, x, y);

                p.pop();
            }
        };

        new p5(sketch);

        // Physics calculations
        function calculateCMFrame() {
            // Calculate initial velocities in CM frame
            const s1_cm = state.v1;
            const s2_cm = (state.m1 / state.m2) * s1_cm;

            state.u1_cm = {x: s1_cm, y: 0};
            state.u2_cm = {x: -s2_cm, y: 0};

            // Calculate post-collision velocities (90 degree scattering)
            const angle = -Math.PI / 4; // -45 degrees
            state.v1_cm = {
                x: state.u1_cm.x * Math.cos(angle) - state.u1_cm.y * Math.sin(angle),
                y: state.u1_cm.x * Math.sin(angle) + state.u1_cm.y * Math.cos(angle)
            };

            state.v2_cm = {
                x: state.u2_cm.x * Math.cos(angle) - state.u2_cm.y * Math.sin(angle),
                y: state.u2_cm.x * Math.sin(angle) + state.u2_cm.y * Math.cos(angle)
            };
        }

        // UI event handlers
        function setupEventListeners() {
            // Time slider
            document.getElementById('slider-time').addEventListener('input', function(e) {
                state.time = parseFloat(e.target.value);
                document.getElementById('label-time').textContent = state.time.toFixed(1);
            });

            // Object 1 controls
            document.getElementById('slider-m1').addEventListener('input', function(e) {
                state.m1 = parseFloat(e.target.value);
                document.getElementById('label-m1').textContent = state.m1.toFixed(2);
                calculateCMFrame();
            });

            document.getElementById('slider-v1').addEventListener('input', function(e) {
                state.v1 = parseFloat(e.target.value);
                document.getElementById('label-v1').textContent = state.v1.toFixed(1);
                calculateCMFrame();
            });

            // Object 2 controls
            document.getElementById('slider-m2').addEventListener('input', function(e) {
                state.m2 = parseFloat(e.target.value);
                document.getElementById('label-m2').textContent = state.m2.toFixed(2);
                calculateCMFrame();
            });

            document.getElementById('slider-v2').addEventListener('input', function(e) {
                state.v2 = parseFloat(e.target.value);
                document.getElementById('label-v2').textContent = state.v2.toFixed(1);
                calculateCMFrame();
            });

            // Joystick control
            const joystickContainer = document.getElementById('joystick-container');
            const joystickKnob = document.getElementById('joystick-knob');
            const containerRect = joystickContainer.getBoundingClientRect();
            const containerSize = containerRect.width;
            const knobSize = 30;
            const maxDistance = (containerSize - knobSize) / 2;

            let isDragging = false;

            function updateJoystickPosition(clientX, clientY) {
                const rect = joystickContainer.getBoundingClientRect();
                const centerX = rect.left + rect.width / 2;
                const centerY = rect.top + rect.height / 2;

                let dx = clientX - centerX;
                let dy = clientY - centerY;

                // Constrain to circular area
                const distance = Math.sqrt(dx*dx + dy*dy);
                if (distance > maxDistance) {
                    dx = dx * maxDistance / distance;
                    dy = dy * maxDistance / distance;
                }

                // Update knob position
                joystickKnob.style.left = `calc(50% + ${dx}px)`;
                joystickKnob.style.top = `calc(50% + ${dy}px)`;

                // Update state variables (scaled to -5 to 5 range)
                state.v0x = (dx / maxDistance) * 5;
                state.v0y = (dy / maxDistance) * 5;

                updateLabels();
            }

            joystickContainer.addEventListener('mousedown', function(e) {
                isDragging = true;
                updateJoystickPosition(e.clientX, e.clientY);
            });

            document.addEventListener('mousemove', function(e) {
                if (isDragging) {
                    updateJoystickPosition(e.clientX, e.clientY);
                }
            });

            document.addEventListener('mouseup', function() {
                isDragging = false;
            });

            // Touch support
            joystickContainer.addEventListener('touchstart', function(e) {
                e.preventDefault();
                isDragging = true;
                updateJoystickPosition(e.touches[0].clientX, e.touches[0].clientY);
            });

            document.addEventListener('touchmove', function(e) {
                if (isDragging) {
                    e.preventDefault();
                    updateJoystickPosition(e.touches[0].clientX, e.touches[0].clientY);
                }
            }, { passive: false });

            document.addEventListener('touchend', function() {
                isDragging = false;
            });
        }

        function updateLabels() {
            document.getElementById('label-v0x').textContent = state.v0x.toFixed(2);
            document.getElementById('label-v0y').textContent = state.v0y.toFixed(2);
        }

        // Initialize
        setupEventListeners();
    </script>
</body>
</html>
