<body>

    <div id="main-container">
        <!-- Control Panel Section -->
        <div class="panel control-panel">
            <div class="control-group">
                <label for="shift-slider">shift: <span id="shift-value-display">12</span></label>
                <input type="range" id="shift-slider" min="0" max="25" step="1" value="12">
