                <input type="text" id="text-input" list="phrase-list" value="abcd">
                <datalist id="phrase-list">
                    <option value="abcd"></option>
                    <option value="test phrase"></option>
                    <option value="to be or not to be"></option>
                    <option value="the quick brown fox jumped over the lazy dog"></option>
                </datalist>
            </div>
        </div>

        <!-- Display Area Section -->
        <div class="panel display-area">
            <!-- Visualization Section -->
            <div>
                <div id="plaintext-alphabet" class="alphabet-row"></div>
                <div id="ciphertext-alphabet" class="alphabet-row"></div>
            </div>

            <!-- Key Section -->
            <div class="key-info">
                <p id="encryption-key-display"></p>
                <p id="decryption-key-display"></p>
            </div>

            <!-- Text Output Section -->
            <div class="text-output">
                <p id="plaintext-output"></p>
                <p id="ciphertext-output"></p>
            </div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // Get references to all DOM elements
            const shiftSlider = document.getElementById('shift-slider');
            const shiftValueDisplay = document.getElementById('shift-value-display');
            const textInput = document.getElementById('text-input');
            const plaintextAlphabetDiv = document.getElementById('plaintext-alphabet');
            const ciphertextAlphabetDiv = document.getElementById('ciphertext-alphabet');
            const encryptionKeyDisplay = document.getElementById('encryption-key-display');
            const decryptionKeyDisplay = document.getElementById('decryption-key-display');
            const plaintextOutput = document.getElementById('plaintext-output');
            const ciphertextOutput = document.getElementById('ciphertext-output');

            const ALPHABET_LOWER = 'abcdefghijklmnopqrstuvwxyz';
            const ALPHABET_UPPER = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';

            /**
             * Encrypts text using the Caesar cipher.
             * @param {string} text - The text to encrypt.
             * @param {number} shift - The shift value.
             * @returns {string} The encrypted ciphertext.
             */
            function encrypt(text, shift) {
                let result = '';
                for (let i = 0; i < text.length; i++) {
                    const char = text[i];
                    const charLower = char.toLowerCase();
                    const index = ALPHABET_LOWER.indexOf(charLower);

                    if (index !== -1) {
                        // It's a letter a-z
                        const shiftedIndex = (index + shift) % 26;
                        result += ALPHABET_UPPER[shiftedIndex];
                    } else {
                        // It's not a letter (space, punctuation, etc.)
                        result += char;
                    }
                }
                return result;
            }

            /**
             * Generates the static plaintext alphabet display.
             */
            function generatePlaintextAlphabet() {
                plaintextAlphabetDiv.innerHTML = '';
                for (const char of ALPHABET_LOWER) {
                    const span = document.createElement('span');
                    span.textContent = char;
                    plaintextAlphabetDiv.appendChild(span);
                }
            }

            /**
             * The main function to update the entire visualization based on control inputs.
             */
            function updateCipher() {
                const shiftValue = parseInt(shiftSlider.value, 10);
                const textValue = textInput.value;

                // --- Alphabet Mapping Update ---
                ciphertextAlphabetDiv.innerHTML = ''; 
                for (let i = 0; i < 26; i++) {
                    const shiftedCharIndex = (ALPHABET_UPPER.charCodeAt(i) - 65 + shiftValue) % 26;
                    const char = ALPHABET_UPPER[shiftedCharIndex];
                    const span = document.createElement('span');
                    span.textContent = char;
                    ciphertextAlphabetDiv.appendChild(span);
                }

                // --- Key Display Update ---
                const decryptionKey = (26 - shiftValue) % 26;
                encryptionKeyDisplay.textContent = `encryption key: ${shiftValue}`;
                decryptionKeyDisplay.textContent = `decryption key: ${decryptionKey}`;
                shiftValueDisplay.textContent = shiftValue; // Update slider value display

                // --- Text Output Update ---
                const ciphertext = encrypt(textValue, shiftValue);
                // Sanitize user input before inserting into innerHTML to prevent XSS
                const sanitizedText = textValue.replace(/</g, "&lt;").replace(/>/g, "&gt;");
                plaintextOutput.innerHTML = `<span class="label-plaintext">plaintext :</span> ${sanitizedText}`;
                ciphertextOutput.innerHTML = `<span class="label-ciphertext">ciphertext:</span> ${ciphertext}`;
            }

            // --- Add Event Listeners ---
            shiftSlider.addEventListener('input', updateCipher);
            textInput.addEventListener('input', updateCipher);

            // --- Initial Page Setup ---
            generatePlaintextAlphabet(); // Generate the static plaintext alphabet once
            updateCipher(); // Run once on load to set the initial state from HTML values
        });
    </script>

</body>
</html>
