            <label for="slider-cation-radius">Relative cation radius: <span id="value-cation-radius" class="value-span">0.100</span></label>
            <input type="range" id="slider-cation-radius" min="0.1" max="1.0" step="0.001" value="0.1">
        </div>
        <div class="control-group">
            <label for="slider-opacity">Opacity of anions</label>
            <input type="range" id="slider-opacity" min="0.0" max="1.0" step="0.01" value="0.8">
        </div>
    </div>

    <div id="main-display-container">
        <div id="canvas-container"></div>
        <p id="ratio-text">The ratio of the cation radius to the anion radius is 1.00.</p>
    </div>

</div>

<script type="importmap">
    {
        "imports": {
            "three": "https://unpkg.com/three@0.164.1/build/three.module.js",
            "three/addons/": "https://unpkg.com/three@0.164.1/examples/jsm/"
        }
    }
</script>

<script type="module">
    import * as THREE from 'three';
    import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

    // --- DOM Elements ---
    const btnThreefold = document.getElementById('btn-threefold');
    const btnFourfold = document.getElementById('btn-fourfold');
    const btnSixfold = document.getElementById('btn-sixfold');
    const btnEightfold = document.getElementById('btn-eightfold');

    const sliderAnionRadius = document.getElementById('slider-anion-radius');
    const valueAnionRadius = document.getElementById('value-anion-radius');
    const sliderCationRadius = document.getElementById('slider-cation-radius');
    const valueCationRadius = document.getElementById('value-cation-radius');
    const sliderOpacity = document.getElementById('slider-opacity');

    const canvasContainer = document.getElementById('canvas-container');
    const ratioText = document.getElementById('ratio-text');

    const coordButtons = [btnThreefold, btnFourfold, btnSixfold, btnEightfold];

    // --- State ---
    let activeCoordination = 3;

    // --- Three.js Setup ---
    let scene, camera, renderer, controls;
    let cation, anions = [], boundingBox;
    let cationMaterial, anionMaterial;

    const DISTANCE = 1.5; // Fixed distance of anion centers from origin

    // --- Pre-calculated Anion Positions ---
    const anionPositions = {
        3: (() => { // Equilateral triangle on XY plane
            return [
                new THREE.Vector3(DISTANCE, 0, 0),
                new THREE.Vector3(DISTANCE * Math.cos(2 * Math.PI / 3), DISTANCE * Math.sin(2 * Math.PI / 3), 0),
                new THREE.Vector3(DISTANCE * Math.cos(4 * Math.PI / 3), DISTANCE * Math.sin(4 * Math.PI / 3), 0)
            ];
        })(),
        4: (() => { // Tetrahedron
            const s = DISTANCE / Math.sqrt(3);
            return [
                new THREE.Vector3(s, s, s),
                new THREE.Vector3(s, -s, -s),
                new THREE.Vector3(-s, s, -s),
                new THREE.Vector3(-s, -s, s)
            ];
        })(),
        6: (() => { // Octahedron
            return [
                new THREE.Vector3(DISTANCE, 0, 0), new THREE.Vector3(-DISTANCE, 0, 0),
                new THREE.Vector3(0, DISTANCE, 0), new THREE.Vector3(0, -DISTANCE, 0),
                new THREE.Vector3(0, 0, DISTANCE), new THREE.Vector3(0, 0, -DISTANCE)
            ];
        })(),
        8: (() => { // Cube
            const s = DISTANCE / Math.sqrt(3);
            return [
                new THREE.Vector3(s, s, s), new THREE.Vector3(s, s, -s),
                new THREE.Vector3(s, -s, s), new THREE.Vector3(s, -s, -s),
                new THREE.Vector3(-s, s, s), new THREE.Vector3(-s, s, -s),
                new THREE.Vector3(-s, -s, s), new THREE.Vector3(-s, -s, -s)
            ];
        })()
    };

    // --- Helper Functions ---

    function createMatcapTexture() {
        const canvas = document.createElement('canvas');
        canvas.width = 256;
        canvas.height = 256;
        const context = canvas.getContext('2d');
        const gradient = context.createRadialGradient(128, 128, 20, 128, 128, 128);
        gradient.addColorStop(0, '#87CEEB'); // Sky Blue
        gradient.addColorStop(0.5, '#4682B4'); // Steel Blue
        gradient.addColorStop(1, '#FF8C00'); // Dark Orange
        context.fillStyle = gradient;
        context.fillRect(0, 0, 256, 256);
        return new THREE.CanvasTexture(canvas);
    }

    function updateRatioText() {
        const cationR = parseFloat(sliderCationRadius.value);
        const anionR = parseFloat(sliderAnionRadius.value);
        const ratio = cationR / anionR;
        ratioText.textContent = `The ratio of the cation radius to the anion radius is ${ratio.toFixed(2)}.`;
    }

    function clearSceneAnions() {
        anions.forEach(anion => {
            scene.remove(anion);
            anion.geometry.dispose();
        });
        anions = [];
        if (boundingBox) {
            scene.remove(boundingBox);
            boundingBox.geometry.dispose();
            boundingBox.material.dispose();
            boundingBox = null;
        }
    }

    function rebuildScene() {
        clearSceneAnions();

        const positions = anionPositions[activeCoordination];
        const anionRadius = parseFloat(sliderAnionRadius.value);
        const anionGeometry = new THREE.SphereGeometry(anionRadius, 32, 16);

        positions.forEach(pos => {
            const anionMesh = new THREE.Mesh(anionGeometry, anionMaterial);
            anionMesh.position.copy(pos);
            scene.add(anionMesh);
            anions.push(anionMesh);
        });

        if (activeCoordination > 3) {
            let boxSize;
            if (activeCoordination === 4 || activeCoordination === 8) {
                boxSize = 2 * (DISTANCE / Math.sqrt(3));
            } else { // coordination 6
                boxSize = 2 * DISTANCE;
            }
            const boxGeom = new THREE.BoxGeometry(boxSize, boxSize, boxSize);
            const edges = new THREE.EdgesGeometry(boxGeom);
            boundingBox = new THREE.LineSegments(edges, new THREE.LineBasicMaterial({ color: 0x888888 }));
            scene.add(boundingBox);
        }

        controls.reset();
        camera.position.set(0, 1.5, 4);
        controls.update();
    }

    function updateActiveButton() {
        coordButtons.forEach(btn => {
            const btnCoord = parseInt(btn.id.match(/\d+|three|four|six|eight/)[0]
                .replace('three', '3')
                .replace('four', '4')
                .replace('six', '6')
                .replace('eight', '8'));
            if (btnCoord === activeCoordination) {
                btn.classList.add('active');
            } else {
                btn.classList.remove('active');
            }
        });
    }

    // --- Initialization ---
    function init() {
        // Scene
        scene = new THREE.Scene();

        // Camera
        const aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
        camera = new THREE.PerspectiveCamera(50, aspect, 0.1, 100);
        camera.position.set(0, 1.5, 4);

        // Renderer
        renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
        renderer.setPixelRatio(window.devicePixelRatio);
        canvasContainer.appendChild(renderer.domElement);

        // Controls
        controls = new OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;
        controls.screenSpacePanning = false;
        controls.minDistance = 1;
        controls.maxDistance = 20;

        // Lighting
        scene.add(new THREE.AmbientLight(0xffffff, 0.6));
        const pointLight = new THREE.PointLight(0xffffff, 0.8);
        pointLight.position.set(5, 5, 5);
        scene.add(pointLight);
        camera.add(new THREE.PointLight(0xffffff, 0.5)); // headlamp
        scene.add(camera);

        // Materials
        cationMaterial = new THREE.MeshMatcapMaterial({ matcap: createMatcapTexture() });
        anionMaterial = new THREE.MeshPhongMaterial({
            color: 0xe0d8e0,
            transparent: true,
            opacity: parseFloat(sliderOpacity.value),
            shininess: 80
        });

        // Cation
        const cationRadius = parseFloat(sliderCationRadius.value);
        const cationGeometry = new THREE.SphereGeometry(cationRadius, 32, 16);
        cation = new THREE.Mesh(cationGeometry, cationMaterial);
        scene.add(cation);

        // Initial setup
        updateActiveButton();
        rebuildScene();
        updateRatioText();

        // --- Event Listeners ---
        coordButtons.forEach(button => {
            button.addEventListener('click', () => {
                const coord = button.id;
                if(coord === 'btn-threefold') activeCoordination = 3;
                if(coord === 'btn-fourfold') activeCoordination = 4;
                if(coord === 'btn-sixfold') activeCoordination = 6;
                if(coord === 'btn-eightfold') activeCoordination = 8;
                updateActiveButton();
                rebuildScene();
            });
        });

        sliderAnionRadius.addEventListener('input', (e) => {
            const newRadius = parseFloat(e.target.value);
            valueAnionRadius.textContent = newRadius.toFixed(3);
            const newGeometry = new THREE.SphereGeometry(newRadius, 32, 16);
            anions.forEach(anion => {
                anion.geometry.dispose();
                anion.geometry = newGeometry;
            });
            updateRatioText();
        });

        sliderCationRadius.addEventListener('input', (e) => {
            const newRadius = parseFloat(e.target.value);
            valueCationRadius.textContent = newRadius.toFixed(3);
            cation.geometry.dispose();
            cation.geometry = new THREE.SphereGeometry(newRadius, 32, 16);
            updateRatioText();
        });

        sliderOpacity.addEventListener('input', (e) => {
            anionMaterial.opacity = parseFloat(e.target.value);
        });

        window.addEventListener('resize', () => {
            camera.aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
        });
    }

    // --- Animation Loop ---
    function animate() {
        requestAnimationFrame(animate);
        controls.update();
        renderer.render(scene, camera);
    }

    // --- Run ---
    init();
    animate();

</script>

</body>
</html>
