</head>
<body>
    <div id="control-panel">
        <div class="slider-container">
            <label for="slider-angle">slope θ</label>
            <input type="range" id="slider-angle" min="10" max="80" value="30" step="1">
        </div>
        <div id="animation-controls">
            <p>release block</p>
            <div class="button-group">
                <button id="btn-play" title="Play">▶</button>
                <button id="btn-pause" title="Pause">||</button>
                <button id="btn-reset" title="Reset">|&#60;</button>
            </div>
        </div>
    </div>
    <div id="visualization-area">
        <p id="info-display"></p>
        <div id="p5-canvas"></div>
    </div>

    <script>
        // DOM Elements
        let sliderAngleEl, btnPlay, btnPause, btnReset, infoDisplayEl;

        // Simulation Constants
        const g = 9.81; // Gravity m/s^2
        const blockMass = 10; // kg (for force calculation, not acceleration)

        // Simulation State
        let angleDegrees;
        let theta; // in radians
        let acceleration;

        // Animation State
        let isPlaying = false;
        let startTime = 0;   // in milliseconds
        let elapsedTime = 0; // in seconds

        // Block State
        let blockPos_s = 0; // distance from top of ramp, in meters
        const blockWidthPx = 40;
        const blockHeightPx = 20;

        // Drawing Constants
        const pixel_per_meter = 40; // Scaling factor for motion
        const forceVectorScale = 6;  // Scaling factor for force vector lengths

        function setup() {
            let canvasContainer = document.getElementById('p5-canvas');
            let canvasWidth = canvasContainer.parentElement.offsetWidth * 0.9;
            let canvasHeight = window.innerHeight * 0.8;
            const canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('p5-canvas');

            // Get DOM elements
            sliderAngleEl = document.getElementById('slider-angle');
            btnPlay = document.getElementById('btn-play');
            btnPause = document.getElementById('btn-pause');
            btnReset = document.getElementById('btn-reset');
            infoDisplayEl = document.getElementById('info-display');

            // Add Event Listeners
            sliderAngleEl.addEventListener('input', handleSliderChange);
            btnPlay.addEventListener('click', playAnimation);
            btnPause.addEventListener('click', pauseAnimation);
            btnReset.addEventListener('click', resetAnimation);

            // Initial setup call
            handleSliderChange();
        }

        function draw() {
            background("#f4f4f9");

            if (isPlaying) {
                elapsedTime = (millis() - startTime) / 1000;
                // Kinematic equation: s = 0.5 * a * t^2
                blockPos_s = 0.5 * acceleration * elapsedTime * elapsedTime;
            }

            drawScene();
        }

        function handleSliderChange() {
            resetAnimation();
            updateInfoDisplay();
        }

        function updateInfoDisplay() {
            angleDegrees = parseInt(sliderAngleEl.value);
            theta = radians(angleDegrees);
            acceleration = g * sin(theta);

            const infoText = `slope = \\( \\theta \\) = ${angleDegrees}°; acceleration = \\( g \\sin(\\theta) \\) = ${acceleration.toFixed(3)} m/s²`;
            infoDisplayEl.innerHTML = infoText;

            // Re-render the math using MathJax
            if (window.MathJax && MathJax.typesetPromise) {
                MathJax.typesetPromise([infoDisplayEl]).catch(err => console.error('MathJax error:', err));
            }
        }

        function playAnimation() {
            if (isPlaying) return;
            isPlaying = true;
            // Adjust startTime to resume correctly from a paused state
            startTime = millis() - (elapsedTime * 1000);
        }

        function pauseAnimation() {
            isPlaying = false;
        }

        function resetAnimation() {
            isPlaying = false;
            elapsedTime = 0;
            blockPos_s = 0;
            startTime = 0;
        }

        function drawScene() {
            // Define ramp geometry based on canvas size
            const rampOriginX = width * 0.1;
            const rampOriginY = height - height * 0.1;
            const rampBaseWidth = width * 0.8;
            const rampHeight = rampBaseWidth * tan(theta);
            const rampHypotenuse = rampBaseWidth / cos(theta);

            // Stop block if it reaches the end of the ramp
            const max_s_pixels = rampHypotenuse;
            if (blockPos_s * pixel_per_meter > max_s_pixels) {
                blockPos_s = max_s_pixels / pixel_per_meter;
                pauseAnimation();
            }

            push(); // Save global drawing state
            translate(rampOriginX, rampOriginY);

            // 1. Draw Inclined Plane (Ramp)
            strokeWeight(2);
            stroke('yellow');
            fill('lightblue');
            triangle(0, 0, rampBaseWidth, 0, rampBaseWidth, -rampHeight);

            // 2. Draw Angle Marker
            noFill();
            stroke(0);
            strokeWeight(1.5);
            arc(0, 0, 80, 80, -theta, 0);
            fill(0);
            noStroke();
            textSize(16);
            textAlign(CENTER, CENTER);
            // Position the label inside the arc
            text('θ', 45 * cos(-theta / 2), 45 * sin(-theta / 2));

            // 3. Calculate block's center position on the canvas (relative to rampOrigin)
            const blockDistFromTop_px = blockPos_s * pixel_per_meter;
            const blockCenterX = rampBaseWidth - blockDistFromTop_px * cos(theta);
            const blockCenterY = -rampHeight + blockDistFromTop_px * sin(theta);

            // 4. Go to block's center to draw block and forces
            push();
            translate(blockCenterX, blockCenterY);

            // Draw Block, aligned with the slope
            push();
            rotate(-theta); // Align coordinate system with the slope
            rectMode(CENTER);
            fill('darkgrey');
            stroke('black');
            strokeWeight(1);
            rect(0, 0, blockWidthPx, blockHeightPx);
            pop();

            // 5. Draw Force Vectors
            const Fg_mag = blockMass * g;
            const Fg_len = Fg_mag * forceVectorScale;

            // Draw F_gravity (points straight down)
            drawArrow(0, 0, 0, Fg_len, 'red', 'F_grav=mg');

            // Calculate component lengths
            const Fg_perp_len = Fg_mag * cos(theta) * forceVectorScale;
            const Fg_para_len = Fg_mag * sin(theta) * forceVectorScale;

            // Draw decomposition and other vectors in a rotated coordinate system
            push();
            rotate(-theta);

            // Draw dashed lines for decomposition
            stroke('black');
            strokeWeight(1.5);
            drawingContext.setLineDash([5, 5]);
            line(0, 0, Fg_para_len, 0); // Parallel component guide
            line(Fg_para_len, 0, Fg_para_len, -Fg_perp_len); // Perpendicular component guide
            drawingContext.setLineDash([]);

            // Draw Net Force vector (F_net = Fg_para)
            drawArrow(0, 0, Fg_para_len, 0, 'purple', 'F_net = mg sin(θ)');

            // Draw Normal Force vector (F_normal = Fg_perp, opposite direction)
            drawArrow(0, 0, 0, -Fg_perp_len, 'deepskyblue', '"normal" force');

            // Label for perpendicular component (mg cos(θ))
            noStroke();
            fill('black');
            textSize(12);
            textAlign(CENTER, TOP);
            push();
            translate(Fg_para_len, -Fg_perp_len / 2);
            rotate(PI / 2); // Rotate text to be parallel with the vector
            text('mg cos(θ)', 0, 5);
            pop();

            pop(); // End rotate(-theta)

            // Draw inner angle 'θ' for force decomposition
            push();
            stroke(0);
            strokeWeight(1);
            noFill();
            arc(0, 0, 40, 40, PI/2 - theta, PI/2);
            fill(0);
            noStroke();
            textSize(12);
            textAlign(CENTER, CENTER);
            text('θ', 25 * cos(PI/2 - theta/2), 25 * sin(PI/2 - theta/2));
            pop();

            pop(); // End translate to block center
            pop(); // End translate to ramp origin
        }

        // Helper function to draw an arrow with a label
        function drawArrow(x1, y1, x2, y2, color, label) {
            push();
            stroke(color);
            fill(color);
            strokeWeight(2);
            line(x1, y1, x2, y2);

            // Draw arrowhead
            let angle = atan2(y2 - y1, x2 - x1);
            translate(x2, y2);
            rotate(angle);
            triangle(0, 0, -8, -4, -8, 4);
            pop();

            // Draw label
            push();
            fill(0);
            noStroke();
            textSize(12);
            textAlign(LEFT, CENTER);
            let midX = (x1 + x2) / 2;
            let midY = (y1 + y2) / 2;
            let textAngle = atan2(y2 - y1, x2 - x1) + PI / 2; // Perpendicular to vector
            translate(midX + 10 * cos(textAngle), midY + 10 * sin(textAngle));
            rotate(atan2(y2 - y1, x2 - x1)); // Align text with vector for readability
            if (cos(atan2(y2-y1, x2-x1)) < 0) { // if pointing left, flip text
                rotate(PI);
                translate(-textWidth(label), 0);
            }
            text(label, 0, 0);
            pop();
        }
    </script>
</body>
</html>
