    // --- Helper Functions ---
    const stringToBinary = (str) => {
        return str.split('').map(char => {
            return char.charCodeAt(0).toString(2).padStart(8, '0');
        }).join('');
    };

    const binaryToHex = (bin) => {
        let hex = '';
        for (let i = 0; i < bin.length; i += 4) {
            let chunk = bin.substr(i, 4);
            hex += parseInt(chunk, 2).toString(16);
        }
        return hex.toUpperCase();
    };

    const permute = (input, table) => {
        let output = '';
        for (let i = 0; i < table.length; i++) {
            output += input[table[i] - 1];
        }
        return output;
    };

    const xor = (a, b) => {
        let result = '';
        for (let i = 0; i < a.length; i++) {
            result += (a[i] === b[i]) ? '0' : '1';
        }
        return result;
    };

    const leftShift = (str, n) => {
        return str.substring(n) + str.substring(0, n);
    };

    // --- Core DES Functions ---
    const generateSubkeys = (keyBinary) => {
        const subkeys = [];
        let permutedKey = permute(keyBinary, PC1);
        let c = permutedKey.substring(0, 28);
        let d = permutedKey.substring(28, 56);

        for (let i = 0; i < 16; i++) {
            c = leftShift(c, ROTATIONS[i]);
            d = leftShift(d, ROTATIONS[i]);
            let combined = c + d;
            subkeys.push(permute(combined, PC2));
        }
        return subkeys;
