            // Feistel function F
            let expandedR = permute(R, E);
            let xored = xor(expandedR, subkeys[i]);
            let sbox_output = '';
            for (let j = 0; j < 8; j++) {
                let chunk = xored.substring(j * 6, (j + 1) * 6);
                let row = parseInt(chunk[0] + chunk[5], 2);
                let col = parseInt(chunk.substring(1, 5), 2);
                sbox_output += S_BOXES[j][row][col].toString(2).padStart(4, '0');
            }
            let p_output = permute(sbox_output, P);

            R = xor(L, p_output);
            L = tempR;
        }

        // Final swap after the last round
        let finalBlock = R + L;
        return permute(finalBlock, FP);
    };

    // --- UI and Interaction Logic ---
    const messageInput = document.getElementById('input-message');
    const keyInput = document.getElementById('input-key');
    const stepsRadios = document.querySelectorAll('input[name="steps"]');
    const outputResults = document.getElementById('output-results');

    function updateDisplay() {
        const messageStr = messageInput.value;
        const keyStr = keyInput.value;
        const numStepsMax = parseInt(document.querySelector('input[name="steps"]:checked').value);

        // 1. Process Key
        let keyBinary = stringToBinary(keyStr);
        keyBinary = keyBinary.padEnd(64, '0').substring(0, 64);

        // 2. Process Message
        let messageBinary = stringToBinary(messageStr);
        const remainder = messageBinary.length % 64;
        if (remainder !== 0) {
            messageBinary = messageBinary.padEnd(messageBinary.length + (64 - remainder), '0');
        }

        const messageBlocks = [];
        for (let i = 0; i < messageBinary.length; i += 64) {
            messageBlocks.push(messageBinary.substring(i, i + 64));
        }

        let outputText = '';

        for (let s = 2; s <= numStepsMax; s += 2) {
            let currentStepCiphertext = '';

            for (const block of messageBlocks) {
                const encryptedBlockBinary = desEncrypt(block, keyBinary, s);
                currentStepCiphertext += binaryToHex(encryptedBlockBinary);
            }

            const stepStr = s.toString().padStart(26, ' ');
            outputText += `${stepStr}${currentStepCiphertext}\n`;
        }

        outputResults.textContent = outputText;
    }

    // Attach event listeners
    messageInput.addEventListener('input', updateDisplay);
    keyInput.addEventListener('input', updateDisplay);
    stepsRadios.forEach(radio => radio.addEventListener('change', updateDisplay));

    // Initial render on page load
    updateDisplay();
});
</script>

