            box-sizing: border-box;
        }

        /* p5.js will create the canvas element */

    </style>
</head>
<body>

    <div id="control-panel">
        <div id="elements-control">
            <label for="slider-elements">elements (set, then click "randomize")</label>
            <input type="range" id="slider-elements" min="2" max="50" value="10" step="1">
            <span id="elements-value">10</span>
        </div>
        <div id="step-control">
            <label for="slider-step">step</label>
            <button id="btn-reset-step">«</button>
            <button id="btn-step-backward">-</button>
            <button id="btn-play-pause">▶</button>
            <button id="btn-step-forward">+</button>
            <input type="range" id="slider-step" min="1" max="1" value="1" step="1">
            <span id="step-value">1</span>
        </div>
        <div id="sort-control">
            <label>sort</label>
            <button id="btn-shellsort" class="sort-btn active">shellsort</button>
            <button id="btn-bubblesort" class="sort-btn">bubblesort</button>
            <button id="btn-insertionsort" class="sort-btn">insertionsort</button>
            <button id="btn-cocktail" class="sort-btn">cocktail</button>
            <button id="btn-bogosort" class="sort-btn">bogosort</button>
            <button id="btn-randomize">randomize</button>
        </div>
    </div>

    <div id="visualization-area">
        <!-- p5.js canvas will be inserted here -->
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {

            // --- STATE MANAGEMENT ---
            const state = {
                currentAlgorithm: 'shellsort',
                numElements: 10,
                sortHistory: [],
                currentStep: 1,
                isPlaying: false,
                animationInterval: null,
                animationSpeed: 100, // ms per step
                maxElements: 50,
            };

            // --- DOM ELEMENT REFERENCES ---
            const dom = {
                sliderElements: document.getElementById('slider-elements'),
                elementsValue: document.getElementById('elements-value'),
                sliderStep: document.getElementById('slider-step'),
                stepValue: document.getElementById('step-value'),
                btnPlayPause: document.getElementById('btn-play-pause'),
                btnStepBackward: document.getElementById('btn-step-backward'),
                btnStepForward: document.getElementById('btn-step-forward'),
                btnResetStep: document.getElementById('btn-reset-step'),
                sortButtons: document.querySelectorAll('.sort-btn'),
                btnRandomize: document.getElementById('btn-randomize'),
                visualizationArea: document.getElementById('visualization-area'),
            };

            // --- SORTING ALGORITHMS ---
            // Each returns a history of array states

            const algorithms = {
                shellsort: (arr) => {
                    let a = [...arr];
                    const history = [[...a]];
                    const n = a.length;
                    for (let gap = Math.floor(n / 2); gap > 0; gap = Math.floor(gap / 2)) {
                        for (let i = gap; i < n; i++) {
                            let temp = a[i];
                            let j;
                            for (j = i; j >= gap && a[j - gap] > temp; j -= gap) {
                                a[j] = a[j - gap];
                                history.push([...a]);
                            }
                            a[j] = temp;
                            history.push([...a]);
                        }
                    }
                    return history;
                },
                bubblesort: (arr) => {
                    let a = [...arr];
                    const history = [[...a]];
                    const n = a.length;
                    for (let i = 0; i < n - 1; i++) {
                        for (let j = 0; j < n - i - 1; j++) {
                            if (a[j] > a[j + 1]) {
                                [a[j], a[j + 1]] = [a[j + 1], a[j]];
                                history.push([...a]);
                            }
                        }
                    }
                    return history;
                },
                insertionsort: (arr) => {
                    let a = [...arr];
                    const history = [[...a]];
                    const n = a.length;
                    for (let i = 1; i < n; i++) {
                        let key = a[i];
                        let j = i - 1;
                        while (j >= 0 && a[j] > key) {
                            a[j + 1] = a[j];
                            j = j - 1;
                            history.push([...a]);
                        }
                        a[j + 1] = key;
                        history.push([...a]);
                    }
                    return history;
                },
                cocktail: (arr) => {
                    let a = [...arr];
                    const history = [[...a]];
                    let swapped = true;
                    let start = 0;
                    let end = a.length;
                    while (swapped) {
                        swapped = false;
                        for (let i = start; i < end - 1; ++i) {
                            if (a[i] > a[i + 1]) {
                                [a[i], a[i + 1]] = [a[i + 1], a[i]];
                                history.push([...a]);
                                swapped = true;
                            }
                        }
                        if (!swapped) break;
                        swapped = false;
                        end--;
                        for (let i = end - 1; i >= start; --i) {
                            if (a[i] > a[i + 1]) {
                                [a[i], a[i + 1]] = [a[i + 1], a[i]];
                                history.push([...a]);
                                swapped = true;
                            }
                        }
                        start++;
                    }
                    return history;
                },
                bogosort: (arr) => {
                    let a = [...arr];
                    const history = [[...a]];
                    const isSorted = (arr) => {
                        for (let i = 0; i < arr.length - 1; i++) {
                            if (arr[i] > arr[i + 1]) return false;
                        }
                        return true;
                    };
                    while (!isSorted(a)) {
                        // Fisher-Yates shuffle
                        for (let i = a.length - 1; i > 0; i--) {
                            const j = Math.floor(Math.random() * (i + 1));
                            [a[i], a[j]] = [a[j], a[i]];
                        }
                        history.push([...a]);
                        // Safety break for extreme cases
                        if (history.length > 20000) break;
                    }
                    return history;
                }
            };

            // --- P5.JS VISUALIZATION SKETCH ---
            let p5sketch;

            const sketch = (p) => {
                const margin = { top: 20, right: 20, bottom: 40, left: 50 };
                let canvasWidth, canvasHeight, chartWidth, chartHeight;

                p.setup = () => {
                    const containerSize = dom.visualizationArea.getBoundingClientRect();
                    canvasWidth = containerSize.width;
                    canvasHeight = containerSize.height;
                    chartWidth = canvasWidth - margin.left - margin.right;
                    chartHeight = canvasHeight - margin.top - margin.bottom;

                    let canvas = p.createCanvas(canvasWidth, canvasHeight);
                    canvas.id('bar-chart-canvas');
                    canvas.parent(dom.visualizationArea);
                    p.colorMode(p.HSB, 360, 100, 100);
                    p.strokeWeight(1);
                    p.noLoop(); // We will manually trigger redraws
                    handleRandomize(); // Initial draw
                };

                p.windowResized = () => {
                    const containerSize = dom.visualizationArea.getBoundingClientRect();
                    canvasWidth = containerSize.width;
                    canvasHeight = containerSize.height;
                    chartWidth = canvasWidth - margin.left - margin.right;
                    chartHeight = canvasHeight - margin.top - margin.bottom;
                    p.resizeCanvas(canvasWidth, canvasHeight);
                    p.redraw();
                };

                p.draw = () => {
                    p.background(255);

                    drawYAxis();
                    drawBars();
                };

                function drawYAxis() {
                    p.push();
                    p.translate(margin.left, margin.top);
                    p.stroke(0);
                    p.strokeWeight(1);
                    p.line(0, 0, 0, chartHeight); // Y-axis line

                    p.textAlign(p.RIGHT, p.CENTER);
                    p.noStroke();
                    p.fill(100);

                    // Dynamic ticks based on number of elements
                    const tickCount = Math.min(state.numElements, 10);
                    for (let i = 0; i <= tickCount; i++) {
                        const value = Math.round(p.map(i, 0, tickCount, 0, state.numElements));
                        const y = p.map(value, 0, state.numElements, chartHeight, 0);
                        p.text(value, -8, y);
                        p.stroke(200);
                        p.line(-5, y, 0, y);
                        p.noStroke();
                    }
                    p.pop();
                }

                function drawBars() {
                    if (!state.sortHistory.length || !state.sortHistory[state.currentStep - 1]) return;

                    const currentArray = state.sortHistory[state.currentStep - 1];
                    const n = currentArray.length;
                    const barWidth = chartWidth / n;

                    p.push();
                    p.translate(margin.left, margin.top);

                    for (let i = 0; i < n; i++) {
                        const value = currentArray[i];
                        const barHeight = p.map(value, 1, n, 0, chartHeight);
                        const x = i * barWidth;
                        const y = chartHeight - barHeight;

                        // Map value to a hue (e.g., blue to red)
                        const hue = p.map(value, 1, n, 240, 0); 
                        p.fill(hue, 90, 90);
                        p.stroke(0);
                        p.rect(x, y, barWidth, barHeight);
                    }
                    p.pop();
                }
            };

            // --- EVENT HANDLERS & LOGIC ---

            function updateVisualization() {
                if (p5sketch) {
                    p5sketch.redraw();
                }
            }

            function updateStepUI() {
                dom.sliderStep.value = state.currentStep;
                dom.stepValue.textContent = state.currentStep;
                updateVisualization();
            }

            function handleRandomize() {
                stopAnimation();
                state.numElements = parseInt(dom.sliderElements.value, 10);

                // Create a shuffled array of unique numbers from 1 to N
                const arr = Array.from({ length: state.numElements }, (_, i) => i + 1);
                for (let i = arr.length - 1; i > 0; i--) {
                    const j = Math.floor(Math.random() * (i + 1));
                    [arr[i], arr[j]] = [arr[j], arr[i]];
                }

                state.sortHistory = algorithms[state.currentAlgorithm](arr);

                dom.sliderStep.max = state.sortHistory.length;
                state.currentStep = 1;
                updateStepUI();
            }

            function handleAlgorithmChange(e) {
                const newAlgorithm = e.target.id.replace('btn-', '');
                state.currentAlgorithm = newAlgorithm;

                dom.sortButtons.forEach(btn => btn.classList.remove('active'));
                e.target.classList.add('active');

                // BogoSort specific logic
                if (newAlgorithm === 'bogosort') {
                    dom.sliderElements.disabled = true;
                    dom.sliderElements.value = 7;
                    dom.elementsValue.textContent = 7;
                } else {
                    dom.sliderElements.disabled = false;
                }

                handleRandomize();
            }

            function playAnimation() {
                state.isPlaying = true;
                dom.btnPlayPause.textContent = '❚❚';
                state.animationInterval = setInterval(() => {
                    if (state.currentStep < state.sortHistory.length) {
                        handleStepForward();
                    } else {
                        stopAnimation();
                    }
                }, state.animationSpeed);
            }

            function stopAnimation() {
                state.isPlaying = false;
                dom.btnPlayPause.textContent = '▶';
                if (state.animationInterval) {
                    clearInterval(state.animationInterval);
                    state.animationInterval = null;
                }
            }

            function handlePlayPause() {
                if (state.isPlaying) {
                    stopAnimation();
                } else {
                    playAnimation();
                }
            }

            function handleStepForward() {
                if (state.currentStep < state.sortHistory.length) {
                    state.currentStep++;
                    updateStepUI();
                }
            }

            function handleStepBackward() {
                if (state.currentStep > 1) {
                    state.currentStep--;
                    updateStepUI();
                }
            }

            function handleResetStep() {
                state.currentStep = 1;
                updateStepUI();
            }


            // --- BIND EVENT LISTENERS ---
            dom.sliderElements.addEventListener('input', (e) => {
                dom.elementsValue.textContent = e.target.value;
            });

            dom.sliderStep.addEventListener('input', (e) => {
                state.currentStep = parseInt(e.target.value, 10);
                updateStepUI();
            });

            dom.btnRandomize.addEventListener('click', handleRandomize);

            dom.sortButtons.forEach(btn => {
                btn.addEventListener('click', handleAlgorithmChange);
            });

            dom.btnPlayPause.addEventListener('click', handlePlayPause);
            dom.btnStepForward.addEventListener('click', handleStepForward);
            dom.btnStepBackward.addEventListener('click', handleStepBackward);
            dom.btnResetStep.addEventListener('click', handleResetStep);

            // --- INITIALIZATION ---
            p5sketch = new p5(sketch);
        });
    </script>
</body>
</html>
