<script>
document.addEventListener('DOMContentLoaded', () => {

    // --- 1. Component State and DOM Element References ---

    const DEFAULTS = {
        x1: 500,
        v1: -10,
        x2: 100,
        v2: 5,
    };

    const SLIDER_CONFIG = {
        'slider-x1': { min: -1000, max: 1000, step: 1, default: DEFAULTS.x1 },
        'slider-v1': { min: -15,   max: 15,   step: 0.01, default: DEFAULTS.v1 },
        'slider-x2': { min: -1000, max: 1000, step: 1, default: DEFAULTS.x2 },
        'slider-v2': { min: -15,   max: 15,   step: 0.01, default: DEFAULTS.v2 },
    };

    // DOM Elements
    const sliders = {
        x1: document.getElementById('slider-x1'),
        v1: document.getElementById('slider-v1'),
        x2: document.getElementById('slider-x2'),
        v2: document.getElementById('slider-v2'),
    };

    const valueDisplays = {
        x1: document.getElementById('value-x1'),
        v1: document.getElementById('value-v1'),
        x2: document.getElementById('value-x2'),
        v2: document.getElementById('value-v2'),
    };

    const resetButton = document.getElementById('btn-reset');
    const resultText = document.getElementById('result-text');
    const plotContainer = document.getElementById('plot-container');

    // --- 2. Main Update and Visualization Logic ---

    function updateVisualization() {
        // 2.1. Read current values from sliders
        const x1 = parseFloat(sliders.x1.value);
        const v1 = parseFloat(sliders.v1.value);
        const x2 = parseFloat(sliders.x2.value);
        const v2 = parseFloat(sliders.v2.value);

        // 2.2. Update numeric value displays
        valueDisplays.x1.textContent = x1.toFixed(0);
        valueDisplays.v1.textContent = v1.toFixed(2);
        valueDisplays.x2.textContent = x2.toFixed(0);
        valueDisplays.v2.textContent = v2.toFixed(2);

        // 2.3. Calculate intersection
        let T = null;
        let X = null;
        let intersectionExists = false;

        if (v1 !== v2) {
            T = (x1 - x2) / (v2 - v1);
            if (T >= 0 && T <= 1000) {
                X = x1 + v1 * T;
                intersectionExists = true;
            }
        }

        // 2.4. Update the result text
        if (intersectionExists) {
            resultText.textContent = `T = ${T.toFixed(2)} s | X = ${X.toFixed(2)} m`;
        } else {
            resultText.textContent = "The runners' paths never intersect";
        }

        // 2.5. Redraw the plot
        drawPlot(x1, v1, x2, v2, T, X, intersectionExists);
    }

    function drawPlot(x1, v1, x2, v2, T, X, intersectionExists) {
        const t_max = 1000;

        // Trace for Runner 1
        const runner1Trace = {
            x: [0, t_max],
            y: [x1, x1 + v1 * t_max],
            type: 'scatter',
            mode: 'lines',
            name: 'Runner 1',
            line: {
                color: 'rgb(70, 130, 180)',
                width: 3
            }
