        // Trace for Runner 2
        const runner2Trace = {
            x: [0, t_max],
            y: [x2, x2 + v2 * t_max],
            type: 'scatter',
            mode: 'lines',
            name: 'Runner 2',
            line: {
                color: 'rgb(255, 165, 0)',
                width: 3
            }
        };

        // Trace for Intersection Point
        const intersectionTrace = {
            x: intersectionExists ? [T] : [],
            y: intersectionExists ? [X] : [],
            type: 'scatter',
            mode: 'markers',
            name: 'Intersection',
            marker: {
                color: 'rgb(60, 179, 113)', // MediumSeaGreen
                size: 10,
                symbol: 'circle',
                line: {
                    color: 'rgb(46, 139, 87)', // SeaGreen
                    width: 2
                }
            }
        };

        const data = [runner1Trace, runner2Trace, intersectionTrace];

        // Layout Configuration
        const layout = {
            xaxis: {
                title: 't',
                range: [0, 1000],
                gridcolor: '#e9e9e9'
            },
            yaxis: {
                title: 'distance',
                autorange: true,
                gridcolor: '#e9e9e9',
                zerolinecolor: '#969696',
                zerolinewidth: 2
            },
            showlegend: false,
            margin: { t: 20, r: 20, b: 40, l: 60 },
            paper_bgcolor: 'rgba(0,0,0,0)',
            plot_bgcolor: 'rgba(0,0,0,0)',
        };

        Plotly.react(plotContainer, data, layout, {responsive: true});
    }

    // --- 3. Event Handlers and Initialization ---

    function handleReset() {
        sliders.x1.value = DEFAULTS.x1;
        sliders.v1.value = DEFAULTS.v1;
        sliders.x2.value = DEFAULTS.x2;
        sliders.v2.value = DEFAULTS.v2;
        updateVisualization();
    }

    function init() {
        // Configure and set initial values for sliders
        for (const sliderId in SLIDER_CONFIG) {
            const slider = document.getElementById(sliderId);
            const config = SLIDER_CONFIG[sliderId];
            slider.min = config.min;
            slider.max = config.max;
            slider.step = config.step;
            slider.value = config.default;
        }

        // Add event listeners
        for (const key in sliders) {
            sliders[key].addEventListener('input', updateVisualization);
        }
        resetButton.addEventListener('click', handleReset);

        // Initial render
        updateVisualization();
    }

    // --- 4. Run Initialization ---
    init();

});
</script>

</body>
