            <span id="slider-value">0</span>
            <button id="btn-reset">↻ Reset</button>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        // Global variables for p5.js sketch
        let slider;
        let sliderValueDisplay;
        let resetButton;

        // Array to store interactable labels for tooltips
        let labels = [];

        // Tooltip content data store
        const tooltips = {
            "Clouds": "Water vapor condensed in the atmosphere.",
            "Rocks": "A primary reservoir of phosphorus in the form of phosphate.",
            "Animals": "Obtain phosphorus by eating plants or other animals.",
            "Plants": "Absorb phosphate from the soil to build organic molecules.",
            "Water": "Phosphorus is carried into water bodies by runoff and leaching.",
            "Erosion": "Weathering of rocks by wind and water releases phosphate.",
            "Runoff": "Phosphate from fertilizers and rocks is washed into rivers and oceans.",
            "Soil": "Phosphorus in the soil is available for plants.",
            "Leaching": "Phosphates in the soil are dissolved in water and carried away.",
            "Plant uptake": "Plants absorb inorganic phosphate from the soil through their roots.",
            "Animals eat plants": "Transfers phosphorus from plants to animals along the food chain.",
            "Decomposition": "Bacteria break down dead organic matter, returning phosphate to the soil and water.",
            "Geologic uplift": "Over millions of years, ocean sediments containing phosphate are lifted up to form new land."
        };

        // p5.js setup function, runs once at the beginning
        function setup() {
            let canvas = createCanvas(800, 500);
            canvas.parent('canvas-container');

            // Link to HTML controls using p5's select function
            slider = select('#slider-phase');
            sliderValueDisplay = select('#slider-value');
            resetButton = select('#btn-reset');

            // Attach event listeners to controls
            slider.input(() => sliderValueDisplay.html(slider.value()));
            resetButton.mousePressed(() => {
                slider.value(0);
                sliderValueDisplay.html(0);
            });

            // Set default text alignment and size for the sketch
            textAlign(CENTER, CENTER);
            textSize(14);
        }

        // p5.js draw function, runs in a loop
        function draw() {
            // 1. Clear labels array for redraw to avoid duplicates
            labels = [];

            // 2. Read current value from the slider
            const phase = parseInt(slider.value());

            // 3. Draw static background
            drawBackground();

            // 4. Draw static scene elements and add their labels for hover detection
            drawSun();
            drawCloudsAndRain();
            drawRocks();
            drawWater();
            drawPlants();
            drawAnimal();

            // 5. Draw dynamic process elements based on the current phase (cumulative)
            if (phase >= 1) drawErosion();
            if (phase >= 2) drawRunoff();
            if (phase >= 3) drawSoil();
            if (phase >= 4) drawLeaching();
            if (phase >= 5) drawPlantUptake();
            if (phase >= 6) drawAnimalConsumption();
            if (phase >= 7) drawDecomposition();
            if (phase >= 8) drawGeologicUplift();

            // 6. Check for mouse hover over labels and draw tooltip if necessary
            drawTooltip(phase);
        }

        // Helper function to add a label to the `labels` array for hover detection
        function addLabel(name, x, y) {
            push();
            textSize(14);
            const w = textWidth(name);
            const h = 20;
            labels.push({ name, x, y, w, h, tooltip: tooltips[name] });
            pop();
        }

        // --- DRAWING FUNCTIONS ---

        function drawBackground() {
            background('#CAE9FF'); // Sky
            noStroke();
            fill('#9B7653'); // Ground
            rect(0, 350, width, height - 350);
        }

        function drawSun() {
            push();
            // Sun rays
            strokeWeight(2);
            stroke(255, 221, 0, 150);
            for (let i = 0; i < 12; i++) {
                let angle = i * TWO_PI / 12;
                let x1 = 720 + cos(angle) * 50;
                let y1 = 80 + sin(angle) * 50;
                let x2 = 720 + cos(angle) * 70;
                let y2 = 80 + sin(angle) * 70;
                line(x1, y1, x2, y2);
            }
            // Sun body
            noStroke();
            fill('#FFDD00');
            circle(720, 80, 80);
            pop();
        }

        function drawCloudsAndRain() {
            push();
            // Rain
            stroke('#0000FF');
            for (let i = 0; i < 5; i++) {
                line(140 + i * 15, 120, 130 + i * 15, 140);
            }

            // Clouds
            noStroke();
            fill('#E0E0E0');
            ellipse(150, 90, 120, 50);
            ellipse(100, 100, 90, 40);
            ellipse(200, 100, 90, 40);

            // Cloud Label
            fill('#505050');
            text('Clouds', 150, 95);
            addLabel('Clouds', 150, 95);
            pop();
        }

        function drawRocks() {
            push();
            fill('#5A5A5A');
            noStroke();
            ellipse(200, 380, 100, 60);
            ellipse(170, 390, 80, 50);
            ellipse(230, 390, 80, 40);

            // Rock Label
            fill(0);
            text('Rocks', 200, 430);
            addLabel('Rocks', 200, 430);
            pop();
        }

        function drawWater() {
            push();
            noStroke();
            fill(0, 0, 255, 150); // Transparent blue
            ellipse(150, 460, 300, 80);
            ellipse(80, 470, 200, 60);

            // Water Label
            fill('#00008B');
            text('Water', 150, 460);
            addLabel('Water', 150, 460);
            pop();
        }

        function drawPlants() {
            push();
            fill('#90EE90');
            stroke('#006400');
            strokeWeight(1.5);
            triangle(650, 400, 660, 370, 670, 400);
            triangle(630, 400, 640, 380, 650, 400);
            triangle(670, 400, 680, 380, 690, 400);

            // Plant Label
            noStroke();
            fill('#006400');
            text('Plants', 720, 390);
            addLabel('Plants', 720, 390);
            pop();
        }

        function drawAnimal() {
            push();
            // Body
            fill(255);
            stroke(0);
            strokeWeight(2);
            rect(450, 370, 80, 40, 10);

            // Head
            circle(530, 375, 30);

            // Legs
            noStroke();
            fill(0);
            rect(460, 410, 10, 20);
            rect(510, 410, 10, 20);

            // Spots
            fill(0, 150);
            ellipse(470, 385, 15, 10);
            ellipse(500, 390, 18, 12);

            // Eye
            fill('#0000FF');
            circle(535, 372, 5);

            // Animal Label
            noStroke();
            fill(0);
            text('Animals', 490, 440);
            addLabel('Animals', 490, 440);
            pop();
        }

        // --- DYNAMIC PROCESS DRAWING FUNCTIONS ---

        function drawErosion() {
            drawArrow(180, 150, 200, 360);
            fill(0);
            noStroke();
            text('Erosion', 160, 250);
            addLabel('Erosion', 160, 250);
        }

        function drawRunoff() {
            drawArrow(240, 400, 200, 440);
            fill(0);
            noStroke();
            text('Runoff', 250, 425);
            addLabel('Runoff', 250, 425);
        }

        function drawSoil() {
            drawArrow(260, 450, 350, 400);
            fill(0);
            noStroke();
            text('Soil', 330, 435);
            addLabel('Soil', 330, 435);
        }

        function drawLeaching() {
            drawArrow(300, 380, 230, 445);
            fill(0);
            noStroke();
            text('Leaching', 310, 410);
            addLabel('Leaching', 310, 410);
        }

        function drawPlantUptake() {
            drawArrow(580, 410, 650, 395);
            fill(0);
            noStroke();
            text('Plant uptake', 590, 385);
            addLabel('Plant uptake', 590, 385);
        }

        function drawAnimalConsumption() {
            drawArrow(660, 370, 540, 375);
            fill(0);
            noStroke();
            text('Animals eat plants', 600, 350);
            addLabel('Animals eat plants', 600, 350);
        }

        function drawDecomposition() {
            drawArrow(490, 415, 490, 470);
            fill(0);
            noStroke();
            text('Decomposition', 560, 450);
            addLabel('Decomposition', 560, 450);
        }

        function drawGeologicUplift() {
            // Draw a curved bezier arrow
            let x1 = 100, y1 = 490;
            let x2 = 180, y2 = 380;
            let cx1 = 20, cy1 = 440;
            let cx2 = 100, cy2 = 360;

            push();
            noFill();
            stroke(0);
            strokeWeight(2);
            bezier(x1, y1, cx1, cy1, cx2, cy2, x2, y2);

            // Draw arrowhead for bezier
            let angle = atan2(y2 - cy2, x2 - cx2);
            translate(x2, y2);
            rotate(angle);
            fill(0);
            triangle(0, 0, -12, 6, -12, -6);
            pop();

            fill(0);
            noStroke();
            text('Geologic uplift', 80, 390);
            addLabel('Geologic uplift', 80, 390);
        }

        // --- UTILITY FUNCTIONS ---

        function drawArrow(x1, y1, x2, y2) {
            push();
            stroke(0);
            strokeWeight(2);
            fill(0);
            line(x1, y1, x2, y2);
            let angle = atan2(y1 - y2, x1 - x2);
            translate(x2, y2);
            rotate(angle - HALF_PI);
            triangle(-6, 0, 6, 0, 0, -12);
            pop();
        }

        function drawTooltip(currentPhase) {
            let foundHover = false;
            // Iterate backwards to prioritize top-most labels
            for (let i = labels.length - 1; i >= 0; i--) {
                const label = labels[i];
                // Check if label should be visible at the current phase
                const phaseRequirement = {
                    "Erosion": 1, "Runoff": 2, "Soil": 3, "Leaching": 4, 
                    "Plant uptake": 5, "Animals eat plants": 6, "Decomposition": 7, "Geologic uplift": 8
                };

                const requiredPhase = phaseRequirement[label.name] || 0;
                if (currentPhase < requiredPhase) continue;

                const halfW = label.w / 2;
                const halfH = label.h / 2;
                if (mouseX > label.x - halfW && mouseX < label.x + halfW &&
                    mouseY > label.y - halfH && mouseY < label.y + halfH) {

                    foundHover = true;

                    push();
                    textSize(12);
                    let tooltipW = textWidth(label.tooltip) + 20;
                    let tooltipH = 40; // A fixed height is simpler for multi-line
                    let tooltipX = mouseX + 15;
                    let tooltipY = mouseY + 15;

                    // Prevent tooltip from going off-screen
                    if (tooltipX + tooltipW > width) {
                        tooltipX = mouseX - 15 - tooltipW;
                    }
                    if (tooltipY + tooltipH > height) {
                        tooltipY = mouseY - 15 - tooltipH;
                    }

                    // Draw tooltip background
                    fill(0, 0, 0, 200);
                    noStroke();
                    rect(tooltipX, tooltipY, tooltipW, tooltipH, 5);

                    // Draw tooltip text
                    fill(255);
                    textAlign(LEFT, TOP);
                    text(label.tooltip, tooltipX + 10, tooltipY + 10, tooltipW - 20, tooltipH - 20);
                    pop();

                    break; // Stop after finding the first hovered label
                }
            }
             // Change cursor style on hover
             cursor(foundHover ? 'pointer' : 'default');
        }
    </script>
