            { x: 300, y: 180 }, { x: 380, y: 250 }, { x: 300, y: 320 }, { x: 220, y: 250 },
            // Extra vertices
            { x: 520, y: 250 }, { x: 80, y: 250 }
        ],
        edges: [
            { u: 0, v: 1 }, { u: 1, v: 2 }, { u: 2, v: 3 }, { u: 3, v: 4 }, { u: 4, v: 5 }, { u: 5, v: 0 },
            // Blossom edges
            { u: 6, v: 7 }, { u: 7, v: 8 }, { u: 8, v: 9 }, { u: 9, v: 6 },
            // Connecting edges
            { u: 0, v: 6 }, { u: 3, v: 8 },
            // Path edges
            { u: 1, v: 10 }, { u: 4, v: 11 }, { u: 9, v: 5 }
        ],
        steps: [
           { // 1
            description: "A 12-vertex graph with a 4-edge maximal matching.",
            matching: [0, 2, 7, 14],
            isFinal: false, blossom: null, tree: null, path: null, exposed: [10, 11]
          },
          { // 2
            description: "Begin search from an exposed vertex 'E'.",
            matching: [0, 2, 7, 14],
            isFinal: false, blossom: null, tree: null, path: null, exposed: [10, 11]
          },
          { // 3
            description: "Grow tree: add unmatched edge from exposed vertex.",
            matching: [0, 2, 7, 14],
            isFinal: false, blossom: null, tree: { edges: [12], vertices: [10, 1] }, path: null, exposed: [10, 11]
          },
          { // 4
            description: "Grow tree: add matched edge.",
            matching: [0, 2, 7, 14],
            isFinal: false, blossom: null, tree: { edges: [12, 0], vertices: [10, 1, 0] }, path: null, exposed: [10, 11]
          },
          { // 5
            description: "Grow tree: add unmatched edge, reaching the 5-cycle.",
            matching: [0, 2, 7, 14],
            isFinal: false, blossom: null, tree: { edges: [12, 0, 10], vertices: [10, 1, 0, 6] }, path: null, exposed: [10, 11]
          },
          { // 6
            description: "Blossom found: An odd cycle (a 5-cycle including the stem) is detected in the tree.",
            matching: [0, 2, 7, 14],
            isFinal: false, blossom: { vertices: [6, 9, 8, 7], topVertex: 6 }, tree: { edges: [12, 0, 10], vertices: [10, 1, 0, 6] }, path: null, exposed: [10, 11]
          },
          { // 7
            description: "Shrink the blossom into a super-vertex.",
            matching: [0, 2, 7, 14],
            isFinal: false, blossom: { vertices: [6, 9, 8, 7], topVertex: 6 }, tree: null, path: null, exposed: [10, 11]
          },
          { // 8
            description: "Continue search from the super-vertex, finding a path to the other exposed vertex.",
            matching: [0, 2, 7, 14],
            isFinal: false, blossom: { vertices: [6, 9, 8, 7], topVertex: 6 }, tree: { edges: [12, 0, 10, 11, 3, 13], vertices: [10, 1, 0, 6, 8, 3, 4, 11] }, path: null, exposed: [10, 11]
          },
          { // 9
            description: "An augmenting path from one exposed vertex to the other has been found.",
            matching: [0, 2, 7, 14],
            isFinal: false, blossom: { vertices: [6, 9, 8, 7], topVertex: 6 }, tree: null, path: { edges: [12, 0, 10, 9, 8, 3, 13], vertices: [10, 1, 0, 6, 9, 8, 3, 4, 11] }, exposed: [10, 11]
          },
          { // 10
            description: "Lifting the path from the shrunken graph back to the original graph.",
            matching: [0, 2, 7, 14],
            isFinal: false, blossom: { vertices: [6, 9, 8, 7], topVertex: 6 }, tree: null, path: { edges: [12, 0, 10, 9, 8, 3, 13], vertices: [10, 1, 0, 6, 9, 8, 3, 4, 11] }, exposed: [10, 11]
          },
          { // 11
            description: "Matching augmented. A perfect matching of 6 edges is found.",
            matching: [12, 10, 9, 2, 13, 5],
            isFinal: true, blossom: null, tree: null, path: null, exposed: null
          }
        ]
      }
    };

    // --- STATE MANAGEMENT ---
    let currentExample = '1';
    let currentStep = 1;
    let p5Instance;

    // --- P5.JS SKETCH ---
    const sketch = (p) => {
        p.setup = () => {
            const canvasContainer = document.getElementById('canvas-container');
            const canvas = p.createCanvas(600, 450);
            canvas.parent(canvasContainer);
            p.noLoop();
        };

        p.draw = () => {
            p.background(255);
            const data = demoData[currentExample];
            const stepData = data.steps[currentStep - 1];

            // --- DRAW EDGES ---
            data.edges.forEach((edge, index) => {
                const v1 = data.vertices[edge.u];
                const v2 = data.vertices[edge.v];
                const isMatched = stepData.matching && stepData.matching.includes(index);
                const isTree = stepData.tree && stepData.tree.edges.includes(index);
                const isPath = stepData.path && stepData.path.edges.includes(index);

                if (isPath) {
                    p.strokeWeight(7);
                    p.stroke(255, 165, 0); // Orange for augmenting path
                } else if (isMatched) {
                    p.strokeWeight(6);
                    p.stroke(stepData.isFinal ? '#4d4dff' : '#ff4d4d'); // Blue if final, else Red
                } else if (isTree) {
                    p.strokeWeight(3);
                    p.stroke(0, 200, 0); // Green for alternating tree
                } else {
                    p.strokeWeight(2);
                    p.stroke('#cccccc'); // Light gray for unmatched
                }
                p.line(v1.x, v1.y, v2.x, v2.y);
            });

            // --- DRAW BLOSSOM ---
            if (stepData.blossom) {
                const blossomVertices = stepData.blossom.vertices.map(i => data.vertices[i]);
                p.fill(0, 0, 255, 75); // rgba(0, 0, 255, 0.3)
                p.stroke(0);
                p.strokeWeight(1);
                p.beginShape();
                blossomVertices.forEach(v => p.vertex(v.x, v.y));
                p.endShape(p.CLOSE);

                const topV = data.vertices[stepData.blossom.topVertex];
                p.fill('#ffff00'); // Yellow
                p.stroke(0);
                p.strokeWeight(1.5);
                p.circle(topV.x, topV.y, 16);
            }

            // --- DRAW VERTICES ---
            data.vertices.forEach((v, index) => {
                const isExposedStart = currentStep === 2 && stepData.exposed && index === stepData.exposed[0];

                p.stroke(0);
                p.strokeWeight(1);
                p.fill(0); // Black for vertices
                p.circle(v.x, v.y, 8);

                if (isExposedStart) {
                    p.noFill();
                    p.stroke(255, 0, 0);
                    p.strokeWeight(2);
                    p.text('E', v.x + 8, v.y + 5);
                }
            });
        };
    };

    // --- INTERACTION LOGIC ---
    document.addEventListener('DOMContentLoaded', () => {
        const descriptionText = document.getElementById('description-text');
        const exampleBtns = {
            '1': document.getElementById('btn-example-1'),
            '2': document.getElementById('btn-example-2')
        };
        const stepBtns = Array.from({ length: 11 }, (_, i) => document.getElementById(`btn-step-${i + 1}`));

        function updateUI() {
            // Update active buttons
            Object.values(exampleBtns).forEach(btn => btn.classList.remove('active'));
            exampleBtns[currentExample].classList.add('active');

            stepBtns.forEach((btn, i) => {
                btn.classList.toggle('active', i + 1 === currentStep);
            });

            // Update description
            descriptionText.textContent = demoData[currentExample].steps[currentStep - 1].description;

            // Redraw canvas
            if (p5Instance) {
                p5Instance.redraw();
            }
        }

        // Example button listeners
        Object.entries(exampleBtns).forEach(([id, btn]) => {
            btn.addEventListener('click', () => {
                if (currentExample !== id) {
                    currentExample = id;
                    currentStep = 1;
                    updateUI();
                }
            });
        });

        // Step button listeners
        stepBtns.forEach((btn, i) => {
            btn.addEventListener('click', () => {
                const stepNum = i + 1;
                if (currentStep !== stepNum) {
                    currentStep = stepNum;
                    updateUI();
                }
            });
        });

        // Initial setup
        p5Instance = new p5(sketch);
        updateUI();
    });

    </script>
</body>
</html>
