    <script type="importmap">
    {
        "imports": {
            "three": "https://unpkg.com/three@0.158.0/build/three.module.js",
            "three/addons/": "https://unpkg.com/three@0.158.0/examples/jsm/",
            "three-bvh-csg": "https://unpkg.com/three-bvh-csg@0.0.16/dist/index.module.js"
        }
    }
    </script>
    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';
        import { CSG } from 'three-bvh-csg';

        // --- DOM Elements ---
        const controls = {
            cylinderRadius: document.getElementById('slider-cylinder-radius'),
            opacity: document.getElementById('slider-opacity'),
            drillRadius: document.getElementById('slider-drill-radius'),
            inclination: document.getElementById('slider-inclination'),
            rotation: document.getElementById('slider-rotation'),
            offset: document.getElementById('slider-offset'),
            boundaryOnly: document.getElementById('checkbox-boundary-only'),
        };

        const labels = {
            cylinderRadius: document.getElementById('label-cylinder-radius'),
            opacity: document.getElementById('label-opacity'),
            drillRadius: document.getElementById('label-drill-radius'),
            inclination: document.getElementById('label-inclination'),
            rotation: document.getElementById('label-rotation'),
            offset: document.getElementById('label-offset'),
        };

        const visContainer = document.getElementById('visualization-container');

        // --- State Configuration ---
        const config = {
            'slider-cylinder-radius': { default: 2.1, min: 0.5, max: 3.0, step: 0.05 },
            'slider-opacity': { default: 0.95, min: 0.0, max: 1.0, step: 0.01 },
            'slider-drill-radius': { default: 1.35, min: 0.1, max: 2.5, step: 0.05 },
            'slider-inclination': { default: 0.76, min: 0, max: 1.57, step: 0.01 },
            'slider-rotation': { default: 0.9, min: -3.14, max: 3.14, step: 0.01 },
            'slider-offset': { default: -0.92, min: -2.0, max: 2.0, step: 0.01 },
            'checkbox-boundary-only': { default: false },
        };

        // --- 3D Scene Setup ---
        let scene, camera, renderer, orbitControls;
        let csg, mainGroup;

        // --- Materials ---
        const mainMaterial = new THREE.MeshStandardMaterial({
            color: '#D2B48C',
            roughness: 0.5,
            metalness: 0.1,
            transparent: true,
            side: THREE.DoubleSide
        });

        const ghostMaterial = new THREE.MeshStandardMaterial({
            color: '#EAEAEA',
            roughness: 0.8,
            metalness: 0.1,
            transparent: true,
            opacity: 0.15
        });

        const frontCurveMaterial = new THREE.MeshBasicMaterial({ color: '#FFA500' });
        const backCurveMaterial = new THREE.MeshBasicMaterial({ color: '#4169E1' });


        function init() {
            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color('#e0e0e0');
            csg = new CSG();
            mainGroup = new THREE.Group();
            scene.add(mainGroup);

            // Camera
            camera = new THREE.PerspectiveCamera(75, visContainer.clientWidth / visContainer.clientHeight, 0.1, 1000);
            camera.position.set(5, 5, 5);
            camera.lookAt(0, 0, 0);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(visContainer.clientWidth, visContainer.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);
            visContainer.appendChild(renderer.domElement);

            // Controls
            orbitControls = new OrbitControls(camera, renderer.domElement);
            orbitControls.enableDamping = true;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 1.0);
            directionalLight.position.set(5, 10, 7.5);
            scene.add(directionalLight);

            // Setup UI
            setupControls();

            // Event Listeners
            window.addEventListener('resize', onWindowResize);
            Object.values(controls).forEach(control => {
                control.addEventListener('input', updateScene);
            });

            // Initial render
            updateScene();
            animate();
        }

        function setupControls() {
            for (const id in config) {
                const el = document.getElementById(id);
                if (el.type === 'range') {
                    el.min = config[id].min;
                    el.max = config[id].max;
                    el.step = config[id].step;
                    el.value = config[id].default;
                } else if (el.type === 'checkbox') {
                    el.checked = config[id].default;
                }
            }
            updateLabels();
        }

        function updateLabels() {
            for (const key in labels) {
                labels[key].textContent = parseFloat(controls[key].value).toFixed(2);
            }
        }

        function updateScene() {
            updateLabels();

            // Clear previous objects
            while (mainGroup.children.length > 0) {
                mainGroup.remove(mainGroup.children[0]);
            }

            // Get current values
            const cylinderRadius = parseFloat(controls.cylinderRadius.value);
            const opacity = parseFloat(controls.opacity.value);
            const drillRadius = parseFloat(controls.drillRadius.value);
            const inclination = parseFloat(controls.inclination.value);
            const rotation = parseFloat(controls.rotation.value);
            const offset = parseFloat(controls.offset.value);
            const boundaryOnly = controls.boundaryOnly.checked;

            // 1. Main Cylinder
            const cylinderGeom = new THREE.CylinderGeometry(cylinderRadius, cylinderRadius, 8, 64);
            const mainCylinderMesh = new THREE.Mesh(cylinderGeom, mainMaterial);
            mainCylinderMesh.updateMatrixWorld();

            // 2. Drill Cylinder
            const drillGeom = new THREE.CylinderGeometry(drillRadius, drillRadius, 10, 64);
            const drillCylinderMesh = new THREE.Mesh(drillGeom);

            // Apply transformations to drill
            drillCylinderMesh.rotation.set(0, 0, Math.PI / 2); // Make horizontal (along X-axis)
            drillCylinderMesh.rotation.x += inclination; // Apply inclination

            // To apply rotation around world's Y-axis, we wrap it
            const rotationGroup = new THREE.Group();
            rotationGroup.add(drillCylinderMesh);
            rotationGroup.rotation.y = rotation;
            rotationGroup.position.y = offset;

            // We need the final world matrix for CSG and curve calculation
            rotationGroup.updateMatrixWorld(true);
            drillCylinderMesh.updateMatrixWorld(true);


            // 3. Boundary Curve Calculation
            const frontPoints = [];
            const backPoints = [];
            const drillMatrix = drillCylinderMesh.matrixWorld;
            const steps = 128;

            // Coefficients for quadratic equation Ah^2 + Bh + C = 0
            const m = drillMatrix.elements;
            const xh = m[4], zh = m[6];
            const A = xh*xh + zh*zh;

            for (let i = 0; i <= steps; i++) {
                const theta = (i / steps) * 2 * Math.PI;
                const cos_t = Math.cos(theta);
                const sin_t = Math.sin(theta);

                const x0_ = m[0]*cos_t + m[8]*sin_t;
                const z0_ = m[2]*cos_t + m[10]*sin_t;

                const B = 2 * ( (m[12]+drillRadius*x0_)*xh + (m[14]+drillRadius*z0_)*zh );
                const C = (m[12]+drillRadius*x0_)*(m[12]+drillRadius*x0_) + (m[14]+drillRadius*z0_)*(m[14]+drillRadius*z0_) - cylinderRadius*cylinderRadius;

                const discriminant = B*B - 4*A*C;

                if (discriminant >= 0) {
                    const sqrt_d = Math.sqrt(discriminant);
                    const h1 = (-B + sqrt_d) / (2 * A);
                    const h2 = (-B - sqrt_d) / (2 * A);

                    const p1 = new THREE.Vector3(drillRadius * cos_t, h1, drillRadius * sin_t).applyMatrix4(drillMatrix);
                    const p2 = new THREE.Vector3(drillRadius * cos_t, h2, drillRadius * sin_t).applyMatrix4(drillMatrix);

                    frontPoints.push(p1);
                    backPoints.push(p2);
                }
            }

            if(frontPoints.length > 1) {
                const frontCurve = new THREE.CatmullRomCurve3(frontPoints, true);
                const backCurve = new THREE.CatmullRomCurve3(backPoints, true);

                const tubeGeomFront = new THREE.TubeGeometry(frontCurve, 128, 0.05, 8, false);
                const tubeMeshFront = new THREE.Mesh(tubeGeomFront, frontCurveMaterial);
                mainGroup.add(tubeMeshFront);

                const tubeGeomBack = new THREE.TubeGeometry(backCurve, 128, 0.05, 8, false);
                const tubeMeshBack = new THREE.Mesh(tubeGeomBack, backCurveMaterial);
                mainGroup.add(tubeMeshBack);
            }

            // 4. CSG Operation
            const result = csg.subtract(mainCylinderMesh, drillCylinderMesh);
            result.material = mainMaterial.clone();
            result.material.opacity = opacity;

            // 5. Final Rendering Logic
            if (boundaryOnly) {
                result.visible = false;
                const ghostCylinder = new THREE.Mesh(cylinderGeom, ghostMaterial);
                mainGroup.add(ghostCylinder);
            } else {
                result.visible = true;
            }
            mainGroup.add(result);
        }

        function onWindowResize() {
            camera.aspect = visContainer.clientWidth / visContainer.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(visContainer.clientWidth, visContainer.clientHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            orbitControls.update();
            renderer.render(scene, camera);
        }

        init();

    </script>
</body>
</html>
