                <input type="range" id="slider-saturation" min="0" max="1" step="0.001" value="0.5">
                <span id="value-saturation">0.500</span>
            </div>
            <div class="slider-row">
                <label for="slider-brightness">brightness</label>
                <input type="range" id="slider-brightness" min="0" max="1" step="0.001" value="1">
                <span id="value-brightness">1.000</span>
            </div>
            <button id="btn-reset">+</button>
        </div>
        <div id="visualization-container">
            <div class="bar-wrapper">
                <div id="canvas-hue-parent"></div>
                <div id="arrow-hue" class="arrow">↑</div>
            </div>
            <div class="bar-wrapper">
                <div id="canvas-saturation-parent"></div>
                <div id="arrow-saturation" class="arrow">↑</div>
            </div>
            <div class="bar-wrapper">
                <div id="canvas-brightness-parent"></div>
                <div id="arrow-brightness" class="arrow">↑</div>
            </div>
            <div class="bar-wrapper">
                <div id="canvas-result-parent"></div>
            </div>
        </div>
    </main>

<script>
    // --- DOM Elements ---
    const sliderHue = document.getElementById('slider-hue');
    const sliderSaturation = document.getElementById('slider-saturation');
    const sliderBrightness = document.getElementById('slider-brightness');

    const valueHue = document.getElementById('value-hue');
    const valueSaturation = document.getElementById('value-saturation');
    const valueBrightness = document.getElementById('value-brightness');

    const arrowHue = document.getElementById('arrow-hue');
    const arrowSaturation = document.getElementById('arrow-saturation');
    const arrowBrightness = document.getElementById('arrow-brightness');

    const btnReset = document.getElementById('btn-reset');

    const parentHue = document.getElementById('canvas-hue-parent');
    const parentSaturation = document.getElementById('canvas-saturation-parent');
    const parentBrightness = document.getElementById('canvas-brightness-parent');
    const parentResult = document.getElementById('canvas-result-parent');

    // --- Initial State ---
    const initialState = {
        hue: 0,
        saturation: 0.5,
        brightness: 1,
    };

    // --- p5.js Sketch ---
    const sketch = (p) => {
        let canvasHue, canvasSaturation, canvasBrightness, canvasResult;
        let barWidth, barHeight;

        p.setup = () => {
            barWidth = parentHue.offsetWidth;
            barHeight = parentHue.offsetHeight;

            // Create canvases and parent them to the respective divs
            canvasHue = p.createCanvas(barWidth, barHeight).parent(parentHue);
            canvasSaturation = p.createCanvas(barWidth, barHeight).parent(parentSaturation);
            canvasBrightness = p.createCanvas(barWidth, barHeight).parent(parentBrightness);
            canvasResult = p.createCanvas(barWidth, barHeight).parent(parentResult);

            p.colorMode(p.HSB, 1, 1, 1, 1);
            p.noLoop(); // We will manually redraw when sliders change

            // Draw the static Hue bar once
            drawHueBar();

            // Set initial state from plan
            resetState();

            // Add event listeners
            sliderHue.addEventListener('input', updateVisuals);
            sliderSaturation.addEventListener('input', updateVisuals);
            sliderBrightness.addEventListener('input', updateVisuals);
            btnReset.addEventListener('click', resetState);
            window.addEventListener('resize', handleResize);
        };

        const handleResize = () => {
            barWidth = parentHue.offsetWidth;
            p.resizeCanvas(barWidth, barHeight, canvasHue);
            p.resizeCanvas(barWidth, barHeight, canvasSaturation);
            p.resizeCanvas(barWidth, barHeight, canvasBrightness);
            p.resizeCanvas(barWidth, barHeight, canvasResult);
            drawHueBar(); // Redraw static bar
            updateVisuals(); // Redraw everything else
        };

        const updateVisuals = () => {
            const h = parseFloat(sliderHue.value);
            const s = parseFloat(sliderSaturation.value);
            const b = parseFloat(sliderBrightness.value);

            // Update value displays
            valueHue.textContent = h.toFixed(3);
            valueSaturation.textContent = s.toFixed(3);
            valueBrightness.textContent = b.toFixed(3);

            // Redraw dynamic canvases
            drawSaturationBar(h, b);
            drawBrightnessBar(h, s);
            drawResultBar(h, s, b);

            // Update arrow positions
            updateArrows(h, s, b);
        };

        const resetState = () => {
            sliderHue.value = initialState.hue;
            sliderSaturation.value = initialState.saturation;
            sliderBrightness.value = initialState.brightness;
            updateVisuals();
        };

        const updateArrows = (h, s, b) => {
            arrowHue.style.left = `${h * 100}%`;
            arrowSaturation.style.left = `${s * 100}%`;
            arrowBrightness.style.left = `${b * 100}%`;
        };

        const drawHueBar = () => {
            canvasHue.drawingContext.save(); // Use separate context for each canvas
            p.push();
            p.noStroke();
            for (let i = 0; i < barWidth; i++) {
                const hue = i / barWidth;
                p.fill(hue, 1, 1);
                p.rect(i, 0, 1, barHeight);
            }
            p.pop();
            canvasHue.drawingContext.restore();
        };

        const drawSaturationBar = (h, b) => {
            canvasSaturation.drawingContext.save();
            p.push();
            p.noStroke();
            for (let i = 0; i < barWidth; i++) {
                const saturation = i / barWidth;
                p.fill(h, saturation, b);
                p.rect(i, 0, 1, barHeight);
            }
            p.pop();
            canvasSaturation.drawingContext.restore();
        };

        const drawBrightnessBar = (h, s) => {
            canvasBrightness.drawingContext.save();
            p.push();
            p.noStroke();
            for (let i = 0; i < barWidth; i++) {
                const brightness = i / barWidth;
                p.fill(h, s, brightness);
                p.rect(i, 0, 1, barHeight);
            }
            p.pop();
            canvasBrightness.drawingContext.restore();
        };

        const drawResultBar = (h, s, b) => {
            canvasResult.drawingContext.save();
            p.push();
            p.noStroke();
            p.fill(h, s, b);
            p.rect(0, 0, barWidth, barHeight);
            p.pop();
            canvasResult.drawingContext.restore();
        };
    };

    // Initialize the p5 sketch
    new p5(sketch);
</script>

</body>
</html>
